#define S2(d,b) smoothstep(8.0*antialiasing(2.0),b,d)
#define PI     3.14159265
#define TWO_PI 6.28318530



float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}    

float intersectSDF(float distA, float distB)
	{ return max(distA, distB);}
float unionSDF(float distA, float distB)
	{ return min(distA, distB);}
float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}



vec3 DrawFigBorde(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.005,0.0));
  return colOut;
}

vec3 DrawFigBordeCol(vec3 pColObj, vec3 colOut, float distObj , vec3 colBorde )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,colBorde ,S2(abs( distObj)-0.01,0.0));
  return colOut;
}


float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b;  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);  }

float sdCircle( vec2 p, float r )
{ return length(p) - r;}

vec2 rotate(vec2 v, float angle)
{
	return vec2(v.x * cos(angle) + v.y * sin(angle),
		    v.y * cos(angle) + v.x * sin(angle));
}

vec3 PisoOnda(vec2 uv,vec3 colIn, vec3 colOut )
{   float y=uv.y + sin(uv.x*5.)*0.1;
    vec3 col= DrawFigBorde(colIn, colOut, y);
    return col;
}

float PisoOnda(vec2 uv )
{   float y=uv.y + sin(uv.x*5.)*0.1;
    return y;
}

vec3 PisoPlano(vec2 uv,vec3 colIn, vec3 colOut )
{   float y= uv.y+ 1.0;
    vec3 col= DrawFigBorde(colIn-(0.5+uv.y/8.0), colOut, y);
    return col;
}

vec3 PisoPlanoIzq(vec2 uv,vec3 colIn, vec3 colOut )
{   float y= uv.y+uv.x+1.0;
    vec3 col= DrawFigBorde(colIn, colOut, y);
    return col;
}

vec3 PisoPlanoDer(vec2 uv,vec3 colIn, vec3 colOut )
{   float y= uv.y-uv.x+1.0;
    vec3 col= DrawFigBorde(colIn, colOut, y);
    return col;
}

float PisoPlanoIzqFl(vec2 uv )
{   //float y= uv.y+uv.x+1.0;
     float onda= uv.y+uv.x+sin(uv.x*10.)*0.2; 
     float y=onda;
    return y;
}

float PisoPlanoDerFl(vec2 uv)
{   //float y= uv.y-uv.x+1.0;
    float onda= uv.y-uv.x+sin(uv.x*10.0)*0.2;
    float y=onda;
    return y;
}

vec3 PisoPlanoLinea(vec2 uv,vec3 colIn, vec3 colOut )
{   float y1= uv.y+ 0.25;
    float y2= uv.y- 0.25;
    float dif= differenceSDF(y2,y1);
    vec3 col= DrawFigBorde(colIn, colOut, dif);
    return col;
}

vec3 HacerCascada(vec2 uv, vec3 colOut)
{   float t=iTime*10.0;
    uv.y= opRep1D(uv.y, 1.0 );
    uv.y -= sin(uv.x*5.+t)*0.2;
    float p1=uv.y-0.1;
    float p2=uv.y+0.2;
    float dif= differenceSDF(p1, p2);
    vec3 col= DrawFigBorde(vec3(0.2,0.2,0.8), colOut, dif);
    return col;
}

float HacerCascadaFlo(vec2 uv)
{   float t=0.0;
    uv.y= opRep1D(uv.y, 1.0 );
    uv.y -= sin(uv.x*5.)*0.2+t;
    float p1=uv.y-0.1;
    float p2=uv.y+0.2;
    float dif= differenceSDF(p1, p2);
    return dif;
}

vec3 HacerCascadaTriangulo(vec2 uv, vec3 colOut)
{   float t=iTime*10.0;
    vec2 uv0=uv;
    float dif= HacerCascadaFlo(uv+vec2(0.0+0.125*sin(t*20.0),t));

    float d1= PisoPlanoIzqFl(vec2(uv0.x, -uv0.y+5.0));
    float d2= PisoPlanoDerFl(vec2(uv0.x, -uv0.y+5.0));
    float d3= uv.y-10.0;
    float uni= unionSDF(d1, d2);  //inclinados
    float inter= differenceSDF(d3,uni);  //
    dif= differenceSDF(dif,uni);
    vec3 col= DrawFigBorde(vec3(1.0), colOut, inter);
    col= DrawFigBorde(vec3(0.2,0.0,1.0), col, dif);
    return col;
}

vec3 CascadaBloque(vec2 uv, vec3 colOut)
{   float d1= sdBox(uv,vec2(3.0,5.0) ); //grande
    float d2= sdBox(uv,vec2(2.5,4.5) ); //chico
    float dif1= differenceSDF(d1, d2);
    float t=iTime;
    float d3=HacerCascadaFlo(uv+vec2(0.0+0.125*sin(t*20.0),t));
    float dif2= intersectSDF(d3,d2);    
    vec3 col= DrawFigBorde(vec3(1.0), colOut, d2);
    col= DrawFigBorde(vec3(0.0), col, dif1);    
    col= DrawFigBorde(vec3(0.2,0.2,0.8), col, dif2);
    return col;
}

vec3 nubes(vec2 uv, vec3 colOut)
{   vec3 col;
    float d1,d2,d3;
     d1= sdCircle(uv, 1.0 );
     d2= sdCircle(uv-vec2(1.5,0.0), 1.5 );
     d3= sdCircle(uv-vec2(3.0,-0.25), 1.2 );
     float duni= unionSDF(d1, d2);
     duni= unionSDF(duni,d3);
     col= DrawFigBorde(vec3(1.0), colOut, duni);
     return col;
}

vec3 Hacermonte(vec2 uv,vec3 colIn, vec3 colOut)
{   float x=uv.x;
    float xx=x+2.0*cos(x*0.5);
    float fx2=3.0*sin(xx*0.5)/xx;
    float monte=uv.y+fx2;
    vec3 col= DrawFigBorde(colIn, colOut, monte); //cafe
    return col;
}

vec3 BloqueSimple(vec2 uv, vec3 colOut)
{   float d1= sdBox(uv-vec2(0.0,2.5),vec2(2.5,0.5) ); //grande sup
    float d2= sdBox(uv,vec2(2.0,3.0) ); //grande inf
    float d3= PisoOnda(uv-vec2(0.0,2.5));
    d1= differenceSDF(d1,d3);
    vec3 col;
    col= DrawFigBorde(vec3(0.6,1.2,0.3)*0.8-(uv.x/7.0), colOut, d2); 
    col= DrawFigBorde(vec3(0.8,0.2,0.3)*0.8-(uv.x/8.0), col, d1); 
    return col;
}


vec3 Hacermontev2(vec2 uv,vec3 colIn, vec3 colOut)
{   float x=uv.x;
    float xx=x+cos(x*0.25);
    float fx2=3.0*sin(xx+cos(xx*1.5));
    float monte=uv.y+fx2;
    vec3 col= DrawFigBorde(colIn-(-0.12+uv.y/8.0), colOut, monte); //cafe
    return col;
}


float starPattern(vec2 p)
{
	return 0.1 / (abs(cos(p.x)) + 0.01) * 0.1 / (abs(sin(p.y)) + 0.01) * abs(sin(p.x)) * abs(cos(p.y));
}

vec3 stars(vec2 p)
{
	p -= 0.5;

	float a = starPattern(rotate(p * 26.0, 0.1)) * max(0.0, 1.3 + cos(p.x + iTime)) +
		starPattern(rotate(p * 50.0, 0.2)) * max(0.0, 1.2 + cos(p.x * 0.4 + iTime * 2.0));

	return vec3(a * 0.1 * (1.0 + (2.0 + sin(iTime * 30.0)) * 0.2));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;
    uv-=vec2(0.0,-0.5);
    float t=iTime;
    //float t0=mod(t,15.0);

    vec3 col=vec3(0.0,0.1,0.8)+stars(uv*0.5);



    uv.x+=t;
    uv.x= opRep1D(uv.x, 5.0 );
    float esc=8.0;
    uv*=esc;

    float trep=t*0.25;
    vec2 uv0=uv;
    float mu=2.5;
    uv.y+=mu*sin(t*0.25);

    col= Hacermonte(uv-vec2(0.0,10.0),vec3(0.7,0.35,0.2), col);
    col= Hacermonte(uv-vec2(10.0,8.0),vec3(0.7,0.35,0.2)*0.8, col);
    col= Hacermontev2(uv-vec2(5.0,4.0),vec3(0.7,0.35,0.2)*0.6, col);

    col= HacerCascadaTriangulo(uv-vec2(0.0,3.0),col);
    uv=uv0;
    uv.y-=4.0+1.0*sin(t*0.125);
    uv.x= opRep1D(uv.x+t, 20.0 );
    col= nubes(uv-vec2(4.0,7.0),col);
    col= nubes(uv-vec2(-8.0,5.0),col);
    uv=uv0;
    uv.y+=mu*sin(t*0.25);

    col= PisoOnda(uv+vec2(0.0,-0.2),vec3(0.0),col); //pasto
    col= PisoOnda(uv,vec3(0.2,0.8,0.2),col); //pasto
    col= PisoPlano(uv,vec3(0.1,0.46,0.2), col );
    col= PisoPlanoLinea(uv-vec2(0.0,-0.8),vec3(0.5,0.4,0.3), col);

    col= BloqueSimple(uv-vec2(10.0,2.0),col);

    col=pow(col,  vec3(0.554545));
    fragColor = vec4(col,1.0);
}

#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec3 getRo() {
    vec2 m = iMouse.xy/iResolution.xy;

    float r = 3.;
    float t = -100. * tanh(0.5 * iTime);
    vec3 ro = r * vec3(cos(t), 0, sin(t));
    ro.y = 1.6;
   // ro.yz *= Rot(-m.y*3.14+1.);
    //ro.xz *= Rot(-m.x*6.2831);
    return ro;
}

float GetDist(vec3 p) {
    float d = p.y;

    p.xz *= Rot(2. * p.y);

    vec3 q = vec3(0, 0.3 * thc(2., p.y * 10. + 4. * iTime), 0);
    float d2 = length(p-q); //sdBox(p-q, vec3(0.6)) - 0.4;
    d2 -= 0.5 - clamp(p.y, 0., 0.5);
    d = -smin(-d, d2 - 0.5);
    d = min(d, d2);

    return 0.5 * d;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 3.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    float IOR = 1.05;
    vec3 p = ro + rd * d;
    if(d<MAX_DIST) {

        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);
        col = 0.5 * n + 0.5;
        float fres = pow(1. + dot(rd, n), 1.);
        //if (length(p.xz) < 2.5)
        col = mix(col, vec3(dif), fres);

    }


    vec3 e = vec3(0.5);
    float h = 2. * clamp(p.y, -1., 0.75);
    vec3 col2 = pal(h, e, e, e, 0.25 * vec3(0,1,2)/3.);

    float lg = log(length(p.xz));
    for (int i = 0; i < 3; i++)
        col2 = pal(4. * length(col) + h + floor((iTime - 4. * lg))/2., e, e, e, 0.5 * col2);


    col = clamp(col2, 0., 1.);
    col *= clamp(exp(0.2-0.1 * length(p.xz)), 0., 1.);
    //col = mix(col, col2, exp(-10. * length(p.xz)));
    //col *= col2;
    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

