    q -= (k + 1.1) * dwr + vec3 (0., 0.65, 0.1);
    d = PrCapsDf (q, 0.05, 0.25);
    DMINQ (idFr);
  }
  for (float k = 0.; k < 3.; k ++) {
    q = p;
    q.x = abs (q.x);
    q -= (k + 1.3) * dwf + vec3 (0., 0.22, 2.8);
    d = max (PrRoundCylDf (q.yzx - vec3 (0.25, -0.1, 0.), 0.25, 0.05, 0.), 0.3 - q.y);
    DMINQ (idFr);
    d = min (SmoothMax (abs (PrCapsDf (q, 0.3 * (1. - 0.5 * q.z * q.z), 0.9)) - 0.02,
       abs (q.z + 0.1) - 0.55, 0.02), PrCylDf (q, 0.28, 0.25));
    s = q.z + 0.2;
    d = max (min (d, PrCapsDf (q + vec3 (0., 0., 0.2), 0.25 * (1. - 2. * s * s), 0.7)),
       - PrCylDf (vec3 (q.xy, q.z + 0.8), 0.07, 0.2));
    DMINQ (idEng);
  }
  ww = vec2 (1.3, 0.85);
  q = p;
  q.x = abs (q.x);
  q.yz -= vec2 (0.8, -5.8);
  q.xy = Rot2Cs (q.xy, csWt.xy);
  q.xz = Rot2Cs (q.xz, csWt.zw);
  d = min (ww.x - abs (abs (p.x) - ww.x), abs (PrBox2Df (vec2 (abs (p.x) - 1.7,
     q.z - 0.07 * q.x + 0.6), vec2 (0.7, 0.25))));
  t = ww.y * (1. - 0.25 * q.x / ww.x);
  s = (q.z - 0.1) / ww.y;
  d = SmoothMax (PrCaps2Df (q.yz, 0.12 * (t - s * s), t), - d, 0.03);
  DMINQ (idWngT);
  ww = vec2 (1.5, 0.65);
  q = p;
  q.x = abs (q.x);
  q.xy = Rot2Cs (q.xy, sin (drpWt + vec2 (0.5 * pi, 0.)));
  q -= vec3 (2.7, 0.8, -6.9);
  t = ww.y * (1. - 0.25 * abs (q.y) / ww.x);
  s = (q.z + 0.1 * abs (q.y) / ww.x - 0.2) / ww.y;
  d = SmoothMax (PrCaps2Df (vec2 (q.x, q.z + 0.1 * abs (q.y) / ww.x), 0.1 * (t - s * s), t),
     abs (q.y - 0.35 * ww.x) - 0.65 * ww.x, 0.06);
  d = SmoothMax (d, - abs (PrBox2Df (vec2 (q.y - 0.8, q.z + 0.3), vec2 (0.5, 0.2))), 0.03);
  DMINQ (idFin);
  q.z -= -0.3;
  d = PrCapsDf (q, 0.05, 0.2);
  DMINQ (idFr);
  if (wlDown) {
    q = p;
    q.yz -= vec2 (-1., 0.4);
    t = 0.45;
    s = abs (q.z) - 3.5 * t;
    q.z = mod (q.z + 0.5 * t, t) - 0.5 * t;
    q.x = abs (q.x) - 0.5;
    d = max (PrRoundCylDf (vec3 (q.yz, abs (q.x) - 0.09), 0.16, 0.03, 0.02), s);
    DMINQ (idWhl);
    d = max (min (PrCylDf (q.yzx, 0.05, 0.09), PrCylDf (vec3 (q.xz, q.y - 0.2), 0.03, 0.2)), s);
    DMINQ (idLeg);
    q = p;
    q.x = abs (q.x);
    q -= vec3 (0.2, -1.06, 4.8);
    d = PrRoundCylDf (vec3 (q.yz, abs (q.x) - 0.07), 0.1, 0.03, 0.01);
    DMINQ (idWhl);
    d = min (PrCylDf (q.yzx, 0.05, 0.07), PrCylDf (vec3 (q.xz, q.y - 0.2), 0.03, 0.2));
    DMINQ (idLeg);
  }
  return 0.6 * flSzFac * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec4 ObjCol ()
{
  vec4 col4, colY4, colB4;
  vec2 s;
  float t;
  col4 = vec4 (1., 1., 0.95, 0.2);
  colY4 = vec4 (0.8, 0.8, 0., 0.2);
  colB4 = vec4 (0.2, 0.2, 1., 0.2);
  if (idObj == idFus) {
    col4 = mix (colB4, col4, smoothstep (0.07, 0.08, abs (qHit.y + 0.6)));
    col4 = mix (colY4, col4, smoothstep (0.07, 0.08, abs (qHit.y + 0.35)));
    if (PrCylDf (qHit - vec3 (0., 0., 0.4), 0.98, 1.7) < 0. ||
       abs (qHit.x) < 0.4 && qHit.y < 0. && PrCylDf (qHit - vec3 (0., 0., 4.7), 0.93, 0.3) < 0.)
       col4 *= vec4 (vec3 (0.2), 0.);
    if (! wlDown && qHit.y < 0.) col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.012, 
       min (abs (PrBox2Df (vec2 (abs (abs (qHit.x) - 0.45) - 0.1, qHit.z - 0.4), vec2 (0.1, 1.6))),
       abs (PrBox2Df (vec2 (abs (abs (qHit.x) - 0.2) - 0.05, qHit.z - 4.8), vec2 (0.05, 0.2)))));
    col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.01, abs (PrRoundBox2Df (qHit.yz - vec2 (-0.4, 3.5),
       vec2 (0.3, 0.15), 0.05)));
  } else if (idObj == idCkp) {
    if (qHit.z > 0.4 && abs (qHit.y - 0.45) < 0.07 && abs (abs (qHit.x) - 0.2) > 0.03 &&
       abs (abs (qHit.z - 1.35) - 0.2) > 0.03) col4 = vec4 (0., 0., 0., -1.);
  } else if (idObj == idEng) {
    t = length (qHit.xy);
    if (PrCapsDf (qHit, 0.3 * (1. - 0.5 * qHit.z * qHit.z), 0.85) < 0.) {
      if (qHit.z > 0. && t < 0.14) col4 = vec4 (1., 0., 0., 0.2);
      else col4 *= vec4 (vec3 (0.3), 0.);
    } else {
      if (abs (qHit.z + 0.1) > 0.5) col4 = colY4;
      else col4 = mix (colB4, col4, smoothstep (0.02, 0.03, abs (qHit.z - 0.3)));
    }
  } else if (idObj == idWngM) {
    t = qHit.z + 0.1 * abs (qHit.x);
    col4 = mix (colB4, col4, smoothstep (0.04, 0.05, abs (t - 1.2)));
    col4 = mix (colY4, col4, smoothstep (0.04, 0.05, abs (t - 1.)));
    if (qHit.y < 0.) {
      if (abs (abs (qHit.x) - 3.9) < 2.1) col4 *= 0.7 + 0.3 *
         smoothstep (0.008, 0.012, abs (qHit.z - 0.1 * abs (qHit.x) + 1.));
    } else {
      s = Rot2D (qHit.xz - vec2 (3.5, -0.3), -0.1 * pi);
      s.x = abs (abs (s.x) - 0.8) - 0.3;
      col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.012, abs (PrBox2Df (s, vec2 (0.3, 0.15))));
    }
  } else if (idObj == idWngT) {
    t = qHit.z + 0.15 * abs (qHit.x);
    col4 = mix (colB4, col4, smoothstep (0.02, 0.03, abs (t - 0.7)));
    col4 = mix (colY4, col4, smoothstep (0.02, 0.03, abs (t - 0.6)));
  } else if (idObj == idFin) {
    t = qHit.z + 0.15 * abs (qHit.y);
    col4 = mix (colB4, col4, smoothstep (0.02, 0.03, abs (t - 0.4)));
    col4 = mix (colY4, col4, smoothstep (0.02, 0.03, abs (t - 0.3)));
  } else if (idObj == idWhl) {
    col4 = mix (vec4 (0.7, 0.7, 0.75, 0.1), vec4 (0.3, 0.3, 0.3, 0.),
       step (0.07, length (qHit.yz)));
  } else if (idObj == idLeg) {
     col4 = vec4 (0.8, 0.8, 0.85, 0.1);
  }
  return col4;
}

float GrndDf (vec3 p)
{
  vec3 q;
  float d, hb, hn, hf, a, r, vs;
  q = p / grndScl;
  r = length (q.xz);
  d = p.y;
  if (r > 0.) {
    a = atan (q.z, - q.x) / (2. * pi) + 0.5;
    hb = sqrt (r) / (2. * pi);
    vs = 40.;
    hf = (r > 1.4) ? 2.5 * hb * mix (Fbm2e (vs * vec2 (hb, a + 1.)),
       Fbm2e (vs * vec2 (hb, a)), a) : 0.;
    hn = (r > 1.8) ? 0. : 0.03 * Fbm2e (8. * q.xz) * (1. - smoothstep (1.4, 1.8, r));
    d = q.y - hn - mix (hb, hf, smoothstep (1.4, 2.2, r)) * smoothstep (1.4, 2.6, r);
    d = max (r - 20., d);
  }
  return grndScl * d;
}

float GrndDfN (vec3 p)
{
  return GrndDf (p) - 0.8 * Fbm2 (0.5 * p.xz);
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + s * rd;
    h = GrndDf (p);
    if (h < 0.) break;
    sLo = s;
    s += max (0.5, h);
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = VAR_ZERO; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      if (GrndDf (p) > 0.) sLo = s;
      else sHi = s;
    }
    dHit = 0.5 * (sLo + sHi);
  }
  return dHit;
}

vec3 GrndNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.01, -0.01);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = GrndDfN (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd;
  rd.y = abs (rd.y);
  q = 0.01 * (ro.xz + 0.5 * tCur + ((200. - ro.y) / rd.y) * rd.xz);
  ff = Fbm2 (q);
  f = smoothstep (0.2, 0.8, ff);
  fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
  clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
     smoothstep (0., 0.05, abs (fd)));
  sd = max (dot (rd, sunDir), 0.);
  skCol = vec3 (0.4, 0.5, 0.8) + step (0.1, sd) * vec3 (1., 1., 0.9) * min (0.3 * pow (sd, 64.) +
     0.5 * pow (sd, 2048.), 1.);
  fd = smoothstep (0.01, 0.1, rd.y);
  col = mix (mix (vec3 (0.8, 0.8, 0.7), skCol, 0.3 + 0.7 * fd), clCol, 0.1 + 0.9 * f * fd);
  return col;
}

vec3 TrkPos (float t)
{
  vec3 r;
  vec2 cs;
  t *= 0.05 / (2. * pi);
  t = mod (t, 1.);
  cs = sin (2. * pi * t + vec2 (0.5 * pi, 0.));
  r.xz = vec2 (1., cs.y);
  r.xz = 40. * cs.x * r.xz / dot (r.xz, r.xz) + vec2 (1., - 5.);
  r.y = 10. + 2. * sin (2. * pi * t);
  return r;
}

void FlyerPM (float t)
{
  vec3 vel, acc, va, flVd, fpF, fpB;
  vec2 cs;
  float oRl, dt;
  if (vuMode == 0) {
    dt = 1.;
    flPos = TrkPos (t);
    fpF = TrkPos (t + dt);
    fpB = TrkPos (t - dt);
    vel = (fpF - fpB) / (2. * dt);
    acc = (fpF - 2. * flPos + fpB) / (dt * dt);
    va = cross (acc, vel) / length (vel);
    flVd = normalize (vel);
    oRl = 2. * length (va) * sign (va.y);
    oRl = smoothstep (0.01, 0.03, abs (oRl)) * oRl;
    cs = sin (oRl + vec2 (0.5 * pi, 0.));
    flMat = mat3 (cs.x, - cs.y, 0., cs.y, cs.x, 0., 0., 0., 1.) *
       mat3 (flVd.z, 0., flVd.x, 0., 1., 0., - flVd.x, 0., flVd.z);
  } else {
    flPos = vec3 (0.);
    flMat = mat3 (1., 0., 0., 0., 1., 0., 0., 0., 1.);
  }
}

void FlyerParms ()
{
  angWm = 0.1 * pi;
  drpWm = 0.02 * pi;
  csWm = vec4 (sin (drpWm + vec2 (0.5 * pi, 0.)), sin (angWm + vec2 (0.5 * pi, 0.)));
  angWt = 0.12 * pi;
  drpWt = -0.03 * pi;
  csWt = vec4 (sin (drpWt + vec2 (0.5 * pi, 0.)), sin (angWt + vec2 (0.5 * pi, 0.)));
  dwr = vec3 (1.4, 0., 0.);
  dwr.xy = Rot2Cs (dwr.xy, sin (- drpWm + vec2 (0.5 * pi, 0.)));
  dwr.xz = Rot2Cs (dwr.xz, sin (-0.7 * angWm + vec2 (0.5 * pi, 0.)));
  dwf = vec3 (1.5, 0., 0.);
  dwf.xy = Rot2Cs (dwf.xy, sin (-0.9 * drpWm + vec2 (0.5 * pi, 0.)));
  dwf.xz = Rot2Cs (dwf.xz, sin (-1.3 * angWm + vec2 (0.5 * pi, 0.)));
  wlDown = (mod (0.05 * tCur, 2.) > 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, dstGrnd, nDotL, sh;
  FlyerParms ();
  dstGrnd = (vuMode == 0) ? GrndRay (ro, rd) : dstFar;
  dstObj = ObjRay (ro, rd);
  if (min (dstGrnd, dstObj) < dstFar) {
    if (dstObj < dstGrnd) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      col4 = ObjCol ();
      if (col4.a >= 0.) {
        nDotL = max (dot (vn, sunDir), 0.);
        if (col4.a > 0.) nDotL = pow (nDotL, 1.5);
        sh = ObjSShadow (ro, sunDir);
        col = col4.rgb * (0.2 + 0.2 * max (- dot (vn, sunDir), 0.) + 0.8 * sh * nDotL) +
           col4.a * step (0.95, sh) * pow (max (dot (sunDir, reflect (rd, vn)), 0.), 32.);
      } else col = SkyCol (ro, reflect (rd, vn));
    } else if (dstGrnd < dstFar) {
      ro += dstGrnd * rd;
      vn =  GrndNf (ro);
      col = mix (vec3 (0.2, 0.4, 0.1), vec3 (0., 0.5, 0.), clamp (0.7 * Noisefv2 (ro.xz) - 0.3,
         0., 1.));
      col = mix (vec3 (0.6, 0.6, 0.7) * (0.7 + 0.3 * Noisefv2 (4. * vec2 (length (ro.xz), ro.y))),
         col, smoothstep (0.5, 0.8, vn.y));
      col *= 0.1 + 0.9 * max (0., max (dot (vn, sunDir), 0.));
    }
  } else {
    col = (vuMode == 0) ? SkyCol (ro, rd) : vec3 (0.6, 0.6, 0.75) * (0.2 + 0.2 * (rd.y + 1.) *
       (rd.y + 1.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr, b;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  vuMode = 0;
  if (vuMode == 0) {
    flSzFac = 0.2;
    flyVel = 1.5;
    FlyerPM (tCur * flyVel);
    ro = vec3 (1., 10., 1.);
    vd = flPos - ro;
    b = length (vd);
    zmFac = 6. + 0.2 * b;
    az = 0.5 * pi + atan (- vd.z, vd.x);
    el = asin (vd.y / b);
    if (mPtr.z > 0.) {
      az += 0.4 * pi * mPtr.x;
      el += 0.2 * pi * mPtr.y;
    }
    vuMat = StdVuMat (el, az);
    grndScl = 100.;
    dstFar = 10. * grndScl;
    sunDir = normalize (vec3 (0., 2., -1.));
    sunDir.xz = Rot2D (sunDir.xz, 0.3 * pi * sin (0.1 * pi * tCur));
  } else {
    flSzFac = 0.3;
    FlyerPM (0.);
    if (mPtr.z > 0.) {
      az = 2. * pi * mPtr.x;
      el = pi * mPtr.y;
    } else {
      az = 0.6 * pi + 2. * pi * mod (0.02 * tCur, 1.);
      el = 0.15 * pi * sin (0.1 * pi * tCur);
    }
    vuMat = StdVuMat (el, az);
    ro = vuMat * vec3 (0., 0., -8.);
    zmFac = 4.;
    dstFar = 120.;
    sunDir = vuMat * normalize (vec3 (1., 1., -1.));
  }
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (p - vec2 (0., clamp (p.y, - h, h))) - r;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2e (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 6; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
