///

// This is an old idea of mine, and I've never implemented it very well

// Idea:
// You have 1 particle which follows a fixed route/sequence
// e.g. up x3, right x5, down x6, left x4
// Once it's gone up/right/down/left, it will start again
// If it hits inactive white cells, it will travel through them, pause the sequence
// and start the sequence again once it's left those cells

// Some sequences have very short "cycles" - they'll twirl in a very obvious pattern
// into the distance
// Other sequences have very long cycles, and you can only see a pattern after 100s
// or 1000s of frames.

// I'm not sure if there are sequences which don't repeat

///

#define KEYBOARD iChannel1
#define KEY_RESET 82


vec3 Cell( in ivec2 p )
{
    // do wrapping
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;

    // fetch texel
   // return (texelFetch(iChannel0, p, 0 ).x > 0.5 ) ? 1 : 0;
   return texelFetch(iChannel0, p, 0 ).rgb;
}

bool key_down(int key) {
    return int(texelFetch(KEYBOARD, ivec2(key, 0), 0).x) == 1;
}

// Change me
#define mx 9.
#define val vec4(2,2,2,6)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 px = ivec2( fragCoord );

    if (iFrame == 0 || key_down(KEY_RESET)) {
        vec2 f = 0.01 * fragCoord + 0.01 * iTime; 
        float h = h21(f);
        float h2 = h21(f.yx + 1.);

        fragColor.r = 0.;//floor(4. * h2); // 0 1 2 3
        fragColor.g = 0.;
        vec2 i = 0.5 * iResolution.xy;
        vec2 f2 = fragCoord;
        if (f2.x > i.x && f2.x <= i.x + 1. && f2.y > i.y && f2.y <= i.y + 1.) { 
       // if (h > 0.999) {      
            fragColor.b = mx;
        }
        return;
    }

    // center cell
    vec3 e = Cell(px); 

    // neighbour cells
    vec3 t = Cell(px + ivec2(0,-1));
    vec3 b = Cell(px + ivec2(0,1));
    vec3 l = Cell(px + ivec2(-1,0));
    vec3 r = Cell(px + ivec2(1,0));   

    vec3 k = t + b + l + r;
    //float k = max( max(l.x, r.x), max(t.x, b.x) );

    // red: direction
    // green: on/off
    // blue: is cell active / how many gens before it turns

    // if cell is active, become inactive
    if (e.b >= 1.) {
        e = vec3(e.r, 1, 0);
        //return;
    }

    // (please dont judge me, im too lazy to untab everything)
    if (true) {//k.b <= 2.) {
        if (e.g == 1. || k.g > 1.) {
            if (t.b >= 1. && t.r == 0.)
                e = t;
            else if (r.b >= 1. && r.r == 1.) 
                e = r;
            else if (b.b >= 1. && b.r == 2.) 
                e = b;
            else if (l.b >= 1. && l.r == 3.)
                e = l;    
        } else {
            if (t.b >= 1. && t.r == 0.) {
                if (t.b == val.x)
                    e = vec3(1, 1, mx); 
                else
                    e = vec3(0, 1, t.b - 1.);       
            }
            else if (r.b >= 1. && r.r == 1.) {
                if (r.b == val.y)
                    e = vec3(2, 1, mx);
                else
                    e = vec3(1, 1, r.b - 1.);
            }
            else if (b.b >= 1. && b.r == 2.) {
                if (b.b == val.z)
                    e = vec3(3, 1, mx);
                else
                    e = vec3(2, 1, b.b - 1.);
            }
            else if (l.b >= 1. && l.r == 3.) {
                if (l.b == val.w)
                    e = vec3(0, 1, mx);
                else
                    e = vec3(3, 1, l.b - 1.);
            }    
        }
    }    
    //e = clamp(e, 0., 1.);
	fragColor = vec4(e, 0.0 );
}

