#define cosquare(x) ((fract(((x) + 0.5) * 0.5) > 0.5) ? -1.0 : 1.0)
#define saw(x) ((fract((x) * 0.5) * 2.0) - 1.0)
#define cosaw(x) ((fract(((x) + 0.5) * 0.5) * 2.0) - 1.0)
#define cotriangle(x) (-(abs(fract((x) * 0.5) - 0.5) * 4.0) + 1.0)
