float noise(vec3 pos)
{
    vec3 posI = floor(pos);
    vec3 posF = pos - posI;

    posI.x = float(int(posI.x) & 255);
    posI.y = float(int(posI.y) & 255);
    posI.z = float(int(posI.z) & 255);

    vec3 uvw = fade(posF);

    int aaa = random(vec3(     posI.x ,     posI.y ,     posI.z  ));
    int aba = random(vec3(     posI.x , inc(posI.y),     posI.z  ));
    int aab = random(vec3(     posI.x ,     posI.y , inc(posI.z) ));
    int abb = random(vec3(     posI.x , inc(posI.y), inc(posI.z) ));
    int baa = random(vec3( inc(posI.x),     posI.y ,     posI.z  ));
    int bba = random(vec3( inc(posI.x), inc(posI.y),     posI.z  ));
    int bab = random(vec3( inc(posI.x),     posI.y , inc(posI.z) ));
    int bbb = random(vec3( inc(posI.x), inc(posI.y), inc(posI.z) ));

    float x1, x2, y1, y2;

    x1 = lerp(
        grad(aaa, posF), 
        grad(baa, vec3(posF.x - 1., posF.y     , posF.z     )),
        uvw.x
    );
    x2 = lerp(
        grad(aba, vec3(posF.x     , posF.y - 1., posF.z     )), 
        grad(bba, vec3(posF.x - 1., posF.y - 1., posF.z     )),
        uvw.x
    );
    y1 = lerp(x1,x2, uvw.y);

    x1 = lerp(
        grad(aab, vec3(posF.x     , posF.y     , posF.z - 1.)), 
        grad(bab, vec3(posF.x - 1., posF.y     , posF.z - 1.)),
        uvw.x
    );
    x2 = lerp(
        grad(abb, vec3(posF.x     , posF.y - 1., posF.z - 1.)), 
        grad(bbb, vec3(posF.x - 1., posF.y - 1., posF.z - 1.)),
        uvw.x
    );
    y2 = lerp(x1,x2, uvw.y);

    return (lerp (y1, y2, uvw.z) + 1.) / 2.;
}

float perlin(vec3 pos, int octaves, float persistence)
{
    float total = 0.;
    float frequency = 1.;
    float amplitude = 1.;
    float maxValue = 0.;

    for(int i=0;i<octaves;i++) {
        total += noise(pos * frequency) * amplitude;

        maxValue += amplitude;

        amplitude *= persistence;
        frequency *= 2.;
    }

    return total/maxValue;
}

