vec3 box(float t) {
    const vec3[] points = vec3[](
        vec3(-1.0, -1.0, -1.0),
        vec3(-1.0, -1.0, 1.0),
        vec3(-1.0, -1.0, 1.0),
        vec3(-1.0, 1.0, 1.0),
        vec3(-1.0, 1.0, 1.0),
        vec3(-1.0, 1.0, -1.0),
        vec3(-1.0, 1.0, -1.0),
        vec3(-1.0, -1.0, -1.0),

        vec3(1.0, -1.0, -1.0),
        vec3(1.0, -1.0, 1.0),
        vec3(1.0, -1.0, 1.0),
        vec3(1.0, 1.0, 1.0),
        vec3(1.0, 1.0, 1.0),
        vec3(1.0, 1.0, -1.0),
        vec3(1.0, 1.0, -1.0),
        vec3(1.0, -1.0, -1.0),

        vec3(-1.0, -1.0, -1.0),
        vec3(1.0, -1.0, -1.0),
        vec3(1.0, -1.0, 1.0),
        vec3(-1.0, -1.0, 1.0),
        vec3(-1.0, 1.0, 1.0),
        vec3(1.0, 1.0, 1.0),
        vec3(1.0, 1.0, -1.0),
        vec3(-1.0, 1.0, -1.0)
    );

    int i = (int(t) * 2) % (points.length());
    return mix(points[i], points[i + 1], fract(t));
}

vec3 hihats(float t) {
    vec3 p = vec3(0.0);

    p.xy = ((randomnoise12(t) * 2.0) - 1.0) * 0.5 * fract(-t + 0.25);

    p.xy *= step(fract(t * 2.0), 0.125) * step(fract(t * 0.5), 0.85);

    return p;
}

vec3 kick(float t) {
    float tf = (t * 300.0) - (20.0 * (fract(-t * 0.5) * easeOut(0.0, 0.1, fract(t * 3.0))));
    vec3 p = vec3(sine(tf), cosine(tf), 0.0);
    p *= 3.0 * fract(-t * 3.0);
    p *= smoothstep(0.0625, 0.0, t);
    p *= step(0.0, t);
    return p;
}

vec3 kicks(float t) {
    vec3 p;

    float t2 = fract(t * 0.125) * 8.0;

    p += kick(t2);
    p += kick(t2 - 0.5);
    p += kick(t2 - 1.75);
    p += kick(t2 - 2.5);

    p += kick(t2 - 4.0);
    p += kick(t2 - 4.5);
    p += kick(t2 - 5.75);
    p += kick(t2 - 6.5);
    p += kick(t2 - 7.5);

    return p;
}

vec3 snare(float t) {
    float tf = (t * 400.0) - (20.0 * (fract(-t * 0.5) * easeOut(-0.015, 0.04, fract(t * 3.0))));
    vec3 p = vec3(sine(tf), cosine(tf), 0.0);
    p.xy += ((randomnoise12(t) * 2.0) - 1.0) * 2.0 * fract(t * 3.0);
    p *= 3.0 * fract(-t * 4.0);
    p *= smoothstep(0.0625 * 4.0, 0.0, t);
    p *= step(0.0, t);
    return p;
}

vec3 snare2(float t) {
    float tf = (t * 1200.0) - (20.0 * (fract(-t * 0.5) * easeOut(-0.015, 0.04, fract(t * 3.0))));
    vec3 p = vec3(sine(tf), cosine(tf), 0.0);
    p.xy += ((randomnoise12(t) * 2.0) - 1.0) * 2.0 * fract(t * 3.0);
    p *= 3.0 * fract(-t * 4.0);
    p *= smoothstep(0.0625 * 3.0 * 0.25, 0.0, t);
    p *= step(0.0, t);
    return p;
}

vec3 snares(float t) {
    vec3 p;

    float t2 = fract((t * 0.25) + 0.25) * 4.0;

    p += snare(t2);
    p += snare(t2 - 2.0);

    return p;
}

vec3 drums(float t) {
    vec3 p;

    p += kicks(t);
    p += snares(t);

    return p;
}

float freqArpeggiator1(float t) {
    float i = (fract(floor(t * 8.0) * 0.25) * 16.0);
    i += (fract(floor(t * 1.0) * 0.25) * 12.0);
    i += 2.0;

    return pitch(i);
}

float freqArpeggiator2(float t) {
    float s = (triangle(floor(t * 8.0) * 0.25) * 0.5) + 0.5;
    s += ((triangle(floor(t) * 0.5) * 0.5) + 0.5) * 2.0;
    s += 1.0;

    return s * 300.0;
}

float freqArpeggiator3(float t) {
    return ((fract(floor(t * 8.0) * 0.25) * 2.0) + (fract(floor(t) * 0.25) * 2.0) + 1.0) * 440.0;
}

vec3 spiralSpeedup(float t) {
    vec3 p;

    t -= easeOut(0.0, 8.0, t) * 4.0;

    float freq = freqArpeggiator1(t);
    float tf = t * freq;

    p = vec3(sine(tf), cosine(tf), 0.0);
    p.xy *= 2.0;

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p *= fract((tf * 0.125) + (t * 0.25));

    p *= (fract(-t) * 0.5) + 0.5;

    float pan = sine(t * 0.5);
    p.xy *= vec2((pan * 0.25) + 0.75, (-pan * 0.25) + 0.75);
    p.xy *= vec2((pan * 0.25) + 0.75, (-pan * 0.25) + 0.75);

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec3 spiralWithHihat(float t) {
    vec3 p;

    float freq = freqArpeggiator1(t);
    float tf = t * freq;

    p = vec3(sine(tf), cosine(tf), 0.0);
    p.xy *= 2.0;

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p *= fract((tf * 0.125) + (t * 0.25));

    p *= (fract(-t) * 0.5) + 0.5;

    float pan = sine(t * 0.5);
    p.xy *= vec2((pan * 0.25) + 0.75, (-pan * 0.25) + 0.75);
    p.xy *= vec2((pan * 0.25) + 0.75, (-pan * 0.25) + 0.75);

    p *= easeOut(8.0, 7.75, t);

    p += hihats(t);

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec3 spiralWithHihatAndDrums(float t) {
    vec3 p;

    float freq = freqArpeggiator1(t);
    float tf = t * freq;

    p = vec3(sine(tf), cosine(tf), 0.0);
    p.xy *= 2.0;

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p *= mix(fract((tf * 0.125) + (t * 0.25)), 1.0, (1.0 - fract(-t)) * easeOut(0.0, 8.0, t));

    p *= (fract(-t) * 0.5) + 0.5;

    vec2 p2 = p.xy;
    float pan = sine(t * 0.5);
    p2 *= vec2((pan * 0.25) + 0.75, (-pan * 0.25) + 0.75);
    p2 *= vec2((pan * 0.25) + 0.75, (-pan * 0.25) + 0.75);

    p.xy = mix(p.xy, p2, linearstep(16.0, 8.0, t));

    p += kicks(t);
    p += hihats(t);

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec3 transitionToRegularSpring(float t) {
    vec3 p;

    float t2 = t + 16.0;

    float freq = freqArpeggiator1(t2);
    float tf = t2 * freq;

    p = vec3(sin(tf * 3.1415), 0.0, cos(tf * 3.1415));

    tf += freq * 0.0625 * 0.0625 * 0.125;

    p.y = mix(
        (easeIn(fract(tf * 0.25)) * 2.0) - 1.0,
        (easeOut(fract(tf * 0.25)) * 2.0) - 1.0,
        (sine(t * 8.0) * 0.5) + 0.5
    );

    p.yz *= rot(((easeIn(0.5, 0.0, t) * -0.5) * PI));

    p += drums(t);
    p += hihats(t);

    p -= vec3(0.0, 0.0, -2.0);
    p.xy = mix(p.xy * 0.5 * ((fract(-t) * 0.5) + 0.5), p.xy / p.z, easeOut(0.0, 0.5, t));

    return p;
}

vec3 regularSpring(float t) {
    vec3 p;

    float t2 = t + 16.0;

    float freq = freqArpeggiator1(t2);
    float tf = t2 * freq;

    // A little different than normal but it sounds better this way imo (slight PI imperfection to desync spring cycle)
    p = vec3(sin(tf * 3.1415), 0.0, cos(tf * 3.1415));

    p.y = mix(
        (easeIn(fract(tf * 0.25)) * 2.0) - 1.0,
        (easeOut(fract(tf * 0.25)) * 2.0) - 1.0,
        (sine(t * 8.0) * 0.5) + 0.5
    );

    p += drums(t);
    p += hihats(t);

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec3 regularSpringRotating(float t) {
    vec3 p;

    float t2 = t + 16.0;

    float freq = freqArpeggiator1(t2);
    float tf = t2 * freq;

    p = vec3(sin(tf * 3.1415), 0.0, cos(tf * 3.1415));

    p.y = mix(
        (easeIn(fract(tf * 0.25)) * 2.0) - 1.0,
        (easeOut(fract(tf * 0.25)) * 2.0) - 1.0,
        (sine(t * 8.0) * 0.5) + 0.5
    );

    p.yz *= rot(t * 0.5 * PI);
    p.xz *= rot(t * 0.5 * PI);
    p.xy *= rot(t * 0.5 * PI);

    p += drums(t);
    p += hihats(t);

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec3 sphericalSpring(float t) {
    vec3 p;

    float freq = freqArpeggiator1(t);
    float tf = t * freq;

    t = fract(t * 0.125) * 8.0;
    p = vec3(sine(tf), 0.0, cosine(tf));

    p.y = (((fract((tf * 0.125) + (t * 0.5))) * 2.0) - 1.0);

    p.xz *= abs(sine((t * freq * 0.125) + (t * 0.5)));

    p.xy *= rot(t * 0.5 * PI);

    p *= 1.25;

    p += drums(t);
    p += hihats(t);

    p *= 0.75 + 0.25 * fract(-t * 8.0);

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec3 sphericalSpringRotating(float t) {
    vec3 p;

    float freq = freqArpeggiator1(t);
    float tf = t * freq;

    t = fract(t * 0.125) * 8.0;
    p = vec3(sine(tf), 0.0, cosine(tf));

    p.y = (((fract((tf * 0.125) + (t * 0.5))) * 2.0) - 1.0);

    p.xz *= abs(sine((t * freq * 0.125) + (t * 0.5)));

    p.xy *= rot(t * 0.5 * PI);
    p.yz *= rot(t * 0.5 * PI);

    p *= 1.25;

    p += drums(t);
    p += hihats(t);

    p *= 0.75 + 0.25 * fract(-t * 8.0);

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec3 springTunnel(float t) {
    vec3 p;

    float freq = freqArpeggiator1(t);
    float tf = t * freq;

    tf += t;
    p = vec3(sine(tf), cosine(tf), 0.0);
    p.xy *= 3.7;

    p *= pow(fract((tf * 0.125) + (t - pow(fract(-t * 2.0), 2.0)) * 0.5), 2.0);

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p.xy = clamp(p.xy, vec2(-1.5), vec2(1.5));

    // Clamping fun
    /*vec2 clbl = vec2(-((sine(t * 0.5) * 0.25) + 1.0), -((sine(t * 0.5) * 0.25) + 1.0));
    vec2 clbr = vec2(((sine(t * 0.5 + 0.5) * 0.25) + 1.0), -((sine(t * 0.5 + 0.5) * 0.25) + 1.0));
    vec2 cltl = vec2(-((sine(t * 0.5 + 1.5) * 0.25) + 1.0), ((sine(t * 0.5 + 1.5) * 0.25) + 1.0));
    vec2 cltr = vec2(((sine(t * 0.5 + 1.0) * 0.25) + 1.0), ((sine(t * 0.5 + 1.0) * 0.25) + 1.0));

    float cll = mix(clbl.x, cltl.x, linearstep(clbl.y, cltl.y, p.y));
    float clb = mix(clbl.y, clbr.y, linearstep(clbl.x, clbr.x, p.x));
    float clr = mix(clbr.x, cltr.x, linearstep(clbr.y, cltr.y, p.y));
    float clt = mix(cltl.y, cltr.y, linearstep(cltl.x, cltr.x, p.x));

    p.xy = clamp(p.xy, vec2(cll, clb), vec2(clr, clt));*/

    p.xy = clamp(p.xy, vec2(-1.25), vec2(1.25));

    p += drums(t);
    p += hihats(t);

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec3 springTunnel2(float t) {
    vec3 p;

    float freq = freqArpeggiator1(t);
    float tf = t * freq;

    tf += t;
    p = vec3(sine(tf), cosine(tf), saw(tf * 0.25 - t * 0.25) + 1.0);

    p += drums(t);
    p += hihats(t);

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p -= vec3(0.0, 0.0, -0.5);
    p.xy /= p.z * 2.0;

    return p;
}

vec3 springTunnelEnd(float t) {
    vec3 p;

    float freq = freqArpeggiator1(t);
    float tf = t * freq;

    tf += t;
    p = vec3(sine(tf), cosine(tf), 0.0);
    p.xy *= 3.7;

    p *= mix(pow(fract((tf * 0.125) + (t + pow(fract(-t * 2.0), 2.0)) * 0.5), 2.0), 0.7, (1.0 - fract(-t * 0.5)));

    p.xy = clamp(p.xy, vec2(-1.25), vec2(1.25));

    p += drums(t);
    p += hihats(t);

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec3 springTunnelEndFadeToCircle(float t) {
    vec3 p;

    float freq = freqArpeggiator1(t);
    float tf = t * freq;

    float kd = easeOut(12.0, 16.0, t);

    tf += t;
    p = vec3(sine(tf), cosine(tf), 0.0);
    p.xy *= mix(3.7, 1.25, kd);

    p *= mix(mix(pow(fract((tf * 0.125) + (t + pow(fract(-t * 2.0), 2.0)) * 0.5), 2.0), 0.7, (1.0 - fract(-t * 0.5))), 1.0, kd);

    p.xy = clamp(p.xy, vec2(-1.25), vec2(1.25));

    p += drums(t);
    p += hihats(t);

    p *= 1.0 - (easeOut(7.75, 8.0, fract(t * 0.125) * 8.0) * fract(floor(t * 16.0) * 0.5));

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec3 spinningDonut(float t) {
    vec3 p;

    float freq = freqArpeggiator1(t);
    float tf = t * freq;

    p = vec3(0.0, 0.0, 0.0);

    p.x -= 0.5 * fract(-t * 4.0);
    p.xz *= rote(tf * (((fract(floor(t * 2.0) * 0.25) * 8.0) + 3.0)));

    p.x -= 1.0;
    p.xy *= rote(tf);

    p.yz *= rote(t * 0.25);
    p.xz *= rote(t * 1.0);
    p.xy *= rote(t * 0.5);

    p += drums(t);
    p += hihats(t);

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

// Unused for now
vec3 fractalDonut(float t) {
    vec3 p;

    float freq = freqArpeggiator1(t) * 0.25;
    float tf = t * freq;

    p = vec3(0.0, 0.0, 0.0);

    float st = 8.0;

    p.x -= 0.0625 * 0.25;
    p.xy *= rote(tf * 512.0);

    p.x -= 0.0625;
    p.xy *= rote(tf * 64.0);

    p.x -= 0.25;
    p.xz *= rote(tf * 8.0);

    p.x -= 1.0;
    p.xy *= rote(tf);

    p += vec3(0.0, 0.0, 0.0);

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;
    p.xy *= step(0.0, p.z);

    return p;
}

// Unrelated, might remove later but it sounds cool
vec3 spikyBeam(float t) {
    vec3 p;

    float freq = freqArpeggiator2(t);
    float tf = t * freq;

    float k = sine(tf) + sine(tf * 0.5) + sine(tf * 0.25);
    p = vec3(k, k, 0.0);

    p.xy *= rote(t);

    p *= (fract(-t * 8.0) * 0.5) + 0.5;

    p += drums(t);
    p += hihats(t);

    p -= vec3(0.0, 0.0, -2.0);
    p.xy /= p.z;

    return p;
}

vec2 point(float t) {
    vec3 p = vec3(0.0);

    t -= 2.0;
    t = max(t, -2.0);

    float t2 = t;

    p += steprange(0.0, 8.0, t2) * spiralSpeedup(t2);
    t2 -= 8.0;

    p += steprange(0.0, 8.0, t2) * spiralWithHihat(t2);
    t2 -= 8.0;

    p += steprange(0.0, 8.0, t2) * spiralWithHihatAndDrums(t2);
    t2 -= 8.0;

    p += steprange(0.0, 0.5, t2) * transitionToRegularSpring(t2);
    p += steprange(0.5, 8.0, t2) * regularSpring(t2);
    t2 -= 8.0;

    p += steprange(0.0, 8.0, t2) * regularSpringRotating(t2);
    t2 -= 8.0;

    p += steprange(0.0, 8.0, t2) * sphericalSpring(t2);
    t2 -= 8.0;

    p += steprange(0.0, 8.0, t2) * sphericalSpringRotating(t2);
    t2 -= 8.0;

    p += steprange(0.0, 16.0, t2) * springTunnel(t2);
    t2 -= 16.0;

    p += steprange(0.0, 12.0, t2) * springTunnelEnd(t2);
    p += steprange(12.0, 16.0, t2) * springTunnelEndFadeToCircle(t2);
    t2 -= 16.0;

    p += steprange(0.0, 16.0, t2) * spinningDonut(t2);
    p *= smoothstep(16.0, 15.75, t2);
    t2 -= 16.0;

    //p = springTunnel2(t);

    //p = spikyBeam(t);

    //p = round(p * 32.0) / 32.0;

    return clamp(p.xy, vec2(-1.0), vec2(1.0));
}

