// [0; 8]
#define NEIGHBOR_THRESHOLD 3

#define R vec3(0.0, 1.0, 1.0)
#define P vec3(1.0, 0.0, 1.0)
#define S vec3(1.0, 1.0, 0.0)


vec3 getColorFromNoise(float white)
{
    return (white < 0.333) ? R : ((white < 0.666) ? P : S);
}

vec3 getWinner(ivec2 coord)
{
    vec3 me;
    int r = 0;
    int p = 0;
    int s = 0;

    for (int j = -1; j <= 1; j++)
    {
        for (int i = -1; i <= 1; i++)
        {
            vec3 rps = texelFetch(iChannel0, coord + ivec2(i, j), 0).rgb;

            if (i == j && j == 0)
                me = rps;
            else if (rps == R)
                r++;
            else if (rps == P)
                p++;
            else if (rps == S)
                s++;
        }
    }

    if (me == R)
        return (p >= NEIGHBOR_THRESHOLD) ? P : R;
    else if (me == P)
        return (s >= NEIGHBOR_THRESHOLD) ? S : P;
    else if (me == S)
        return (r >= NEIGHBOR_THRESHOLD) ? R : S;

    return vec3(0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 coord = ivec2(fragCoord);
    vec3 col;

    if (iFrame < 100) {
        vec2 uv = fragCoord / iResolution.xy;
        float white = texture(iChannel1, uv).r;

        col = getColorFromNoise(white);
    }
    else {
        col = getWinner(coord);
    }

    fragColor = vec4(col, 1.0);
}

