    for(int i = 32; i<40;i++) {
        float md1 = sampleFreq(float(tk)*.02)*.45; 
        if(md1<.2) md1 = 0.;
        vec2 p = vec2(.3125,.2)-vec2(float(tk)*.13,0);
        button(C,uv+p, md1, 0);
        tk++;
    }  

    // mute buttons
    tk = 0;
    for(int i = 48; i<56;i++) {
        float md1 = sampleFreq(float(tk)*.1)*.45; 
        if(md1<.1) md1 = 0.;
        vec2 p = vec2(.3125,.25)-vec2(float(tk)*.13,0);
        button(C,uv+p, md1, 0);
        tk++;
    }  

    // record buttons
    tk = 0;
    for(int i = 64; i<72;i++) {
        float md1 = sampleFreq(float(i-35)*.1)*.45; 
        if(md1<.1) md1 = 0.;
        vec2 p = vec2(.3125,.3)-vec2(float(tk)*.13,0);
        button(C,uv+p, md1, 0);
        tk++;
    }  

    // track buttons
    for(int i = 41; i<46;i++) {
        float md1 = sampleFreq(float(i-41)*.1)*.45; 
        if(md1<.25)md1 = 0.;
        // track button midi jumps all over? why Korg?
        // fixing my brute force
        float fk = 0.;
        if(i==41){
        fk=10.;
        }else if(i==42){
        fk=9.;
        }else if(i==43){
        fk=7.;
        }else if(i==44){
        fk=8.;
        }else{
        fk=11.;
        }

        vec2 p = vec2(1.,.4)-vec2(float(fk)*.05,0);
        button(C,uv+p, md1, 0);
        tk++;
    }

    // cycle button
    tk = 0;
    float md1 = sampleFreq(float(.23434)*.1)*.45; 
    if(md1<.2) md1 = 0.;
    button(C,uv+vec2(.65,.30), md1, 2);

    // marker buttons
        for(int i = 60; i<63;i++) {
        float md1 = sampleFreq(float(i-35)*.1)*.45; 
        if(md1<.1) md1 = 0.;
        vec2 p = vec2(.55,.30)-vec2(float(tk)*.05,0);
        button(C,uv+p, md1, 2);
        tk++;
    }  

    // track buttons
        for(int i = 58; i<60;i++) {
        float md1 = sampleFreq(float(i-57)*.078)*.45; 
        if(md1<.25) md1 = 0.;
        vec2 p = vec2(.8,.25)-vec2(float(tk)*.05,0);
        button(C,uv+p, md1, 2);
        tk++;
    }  

    if(hs<.65) C = clamp(C+(hs*.005),C,vec3(1));
    C = pow(C, vec3(.4545));
    O = vec4(C,1.);
}

// https://en.wikipedia.org/wiki/Poisson_distribution
//#define hash2(p)  fract(sin(dot(p, vec2(12.9898, 78.233))) * 4375.85453) // float hash
