  #define hash2(p)  ( U.y/R.y < .5 ?  fract(sin(dot(p, vec2(12.9898, 78.233))) * 4375.85453) : hash(vec3(p,0)).x ) // bottom/top 

//#define hash1(f)  hash2(vec2(f,1))                         // float hash
//#define hash1(f)  hash(vec3(f)).x                          // integer hash
  #define hash1(f)  ( U.y/R.y < .5 ? hash2(vec2(f,1)) : hash(vec3(f)).x ) // bottom/top

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    float lambda = iMouse.z>0. ? exp2(5.*iMouse.y/R.y) : 3., // average ( and variance )
            seed = hash2(U),
          x = 0., p = exp(-lambda), s = p,S;
    while( ( seed = hash1(seed) ) > s )
        s += p *= lambda/ ++x;

    s = 4.*U.x/R.x; 
    x =   s<1. ? x/10.                             // Poisson distrib with 3 different normalizations
        : s<2. ? .5*x/lambda
        : s<3. ? .5+.5*(x-lambda)/sqrt(lambda)
        :        hash1(seed);                      // Uniform
    O = vec4( sqrt(x) );                           // to sRGB
}

