#define st smoothstep

float noise(vec2 p) {
  return fract(sin(p.x * 83.876 + p.y * 76.123) * 3853.875);
}

float perlin(vec2 uv, float iters) {
  float c = 1.0;
  for (float i = 0.0; i < iters; i++) {
    float p = pow(2.0, i + 1.0);
    vec2 luv = uv * vec2(p) + iTime * 0.2;
    vec2 gv = st(0.0, 1.0, fract(luv));
    vec2 id = floor(luv);
    float lb = noise(id + vec2(0.0, 0.0));
    float rb = noise(id + vec2(1.0, 0.0));
    float lt = noise(id + vec2(0.0, 1.0));
    float rt = noise(id + vec2(1.0, 1.0));
    float b = mix(lb, rb, gv.x);
    float t = mix(lt, rt, gv.x);
    c += 1.0 / p * mix(b, t, gv.y);
  }
  return c / 2.0;
}

float dst_sun(vec2 uv) {
  return st(0.42, 0.35, sqrt(length(uv - vec2(33.8, 34.7))));
}

float dst_cldsun(vec2 uv) {
  return 0.8 * st(0.6, 0.2, sqrt(length(uv - vec2(33.8, 34.7))));
}

float dst_galo(vec2 uv) {
  return st(1.0, 0.1, pow(length(uv - vec2(33.8, 34.7)), 0.4));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (gl_FragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
  uv *= 5.0;
  uv += 33.0;

  vec3 sky = vec3(0.3, 0.5, 0.8);
  vec3 cloud = 0.85 * vec3(1.0, 1.0, 1.0);
  vec3 galo = vec3(1.0, 0.9, 0.8);
  vec3 sun = vec3(1.0, 1.0, 0.98);
  vec3 cld_sun = 1.3 * vec3(1.0, 1.0, 0.75);

  float j = sin(2.0) * 0.1 + 0.7;
  float dst_cloud = st(j, j + 0.1, perlin(uv, 8.0));
  float dst_sun = dst_sun(uv);

  vec3 color = mix(sky, galo, dst_galo(uv));
  color = mix(color, sun, dst_sun);
  color = mix(color, cloud, dst_cloud);
  color = mix(color, mix(color, cld_sun, dst_cldsun(uv)), dst_cloud);
  fragColor = vec4(color, 1.0);
}

