float line(vec2 f, float t, float o) {
    float a = 0.25 + 0.25 * cos(-7.5 * t + o);
    float y = a * dim.y * sin(t + o) + (1.-a) * f.x * cos(t + o);
    //y = 0.45 * dim.y * cos(10000. * pow((f.y - f.x)/dim.x, 3.)+ t + o);
    y *= 0.5 + 0.5 * cos(7.5 * f.x/dim.x - 3.75 * t + o + 0. * pi/30. * h21(f));
    return step(f.y, y);
}

// a bit sloppy
vec3 lineCol(float x) {
    float o = 2. * pi / 3.;
    return 0.5 + 0.5 * thc(4., vec3(x-o,x,x+o));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Center fragCoord
    fragCoord -= 0.5 * dim;

    vec3 col;

    float n = 15.;
    for (float i = 0.; i < n; i++) {
        // Lower framerate + offset time for each line
        float time = 0.25 * floor(15. * iTime) / 15.;
        float o = 2. * pi * i / n;

        // Center line (draw everything under the line)
        float c = line(fragCoord, time, o); 

        // Shift line up/down/left/right
        float t = line(fragCoord - vec2(0,1), time, o);
        float b = line(fragCoord + vec2(0,1), time, o);
        float l = line(fragCoord - vec2(1,0), time, o);
        float r = line(fragCoord + vec2(1,0), time, o);

        // Only draw cells which have 0 and 1 cell neighbours
        float mx = max(max(t,b), max(l,r));
        float mn = min(min(t,b), min(l,r));

        // Only draw new line if a line hasn't been drawn already on this pixel
        if (col == vec3(0))
            col = c * (mx - mn) * lineCol(2. * pi * i / n + 0.75 * pi);
    }

    // Restrict to bounds
    col *= step(fragCoord.x, 0.5 * dim.x) * step(fragCoord.y, 0.5 * dim.y);

    fragColor = vec4(col, 1.);
}

