precision highp float;

#define PI 3.14159265359
#define minDist 0.00001
#define maxMarch 800
#define maxDist 50.

struct camera {
    vec3 origin;
    vec3 lookVec;
    vec3 rightVec;
    vec3 upVec;
};

struct rayMarchHit {
    bool hasHit;
    int marchCount;
    vec3 pos;
    vec3 normal;
    float distTraveled;
    float closestDistance;
};

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

float distToSphere(vec3 c, float r, vec3 p) {
    return length(c - p) - r;
}

float distToSphereMod(vec3 c, float r, vec3 p) {
    return length(c - p) - r;
}

float distToGround(vec3 p, float h) {
    //float r = p.y - h
    //+ noise(p.xz * 19. + iTime) / 80.
    //+ noise(p.xz * 5. + iTime) / 40.
    //+ noise(p.xz + iTime) / 4.;

    //float m = length(p.xz);
    //return r + m*m/10. / (1. + m * 0.05);

    float r = distToSphere(vec3(0, -20, 0), 20., p) 
    + noise(p.xy * 2. + iTime) / 20.
    + noise(p.yz * 2. + iTime) / 20.
    + noise(p.xz * 2. + iTime) / 20.;
    return r;
}

float sdf(vec3 p) {
    const int objectCount = 2;
    float dists[objectCount];
    float smallestDistance = 1e10;

    dists[0] = distToSphereMod(
        vec3(0, 0.5, 0),
        1., 
        p
    );

    dists[1] = distToGround(p, -0.4);

    for (int i = 0; i < objectCount; i++) {
        smallestDistance = min(smallestDistance, dists[i]);
    }

    return smallestDistance;
}

vec3 getNormalAt(vec3 p) {
    float d = sdf(p);
    vec2 e = vec2(0.01, 0);
    return normalize(d - vec3(
        sdf(p-e.xyy),
        sdf(p-e.yxy),
        sdf(p-e.yyx)
    ));
}

rayMarchHit rayMarch(vec3 origin, vec3 direction) {
    rayMarchHit hit = rayMarchHit(
        false,      // hasHit
        0,          // marchCount
        origin,     // pos
        vec3(0),  // normal
        0.,         // distTraveled
        0.          // closestDistance 
    );

    for (int i = 0; i < maxMarch; i++) {
        float distToScene = sdf(hit.pos);

        if (distToScene < hit.closestDistance) {
            hit.closestDistance = distToScene;
        }

        hit.marchCount += 1;
        hit.distTraveled += distToScene;
        hit.pos = origin + direction * hit.distTraveled;
        hit.normal = getNormalAt(hit.pos);

        if (distToScene <= minDist) {
            hit.hasHit = true;
            break;
        }

        if (hit.distTraveled >= maxDist) { break; }
    }

    return hit;
}

vec3 getSunCol(vec3 normal, vec3 lightDir) {
    vec3 sunColor = vec3(0);
    sunColor += max(smoothstep(0.99, 1., dot(normal, lightDir)) , 0.);  
    sunColor += max(smoothstep(0.9, 1., dot(normal, lightDir)) , 0.) / 3.;

    return sunColor;
}

vec3 getSkyCol(vec3 normal, vec3 lightDir) {
    // base color
    vec3 skyCol = mix(
        vec3(0.2, 0.2, 0.9),
        vec3(0.4, 0.7, 1),
        dot(normal, lightDir)
    );

    // clouds
    skyCol = mix(
        skyCol,
        mix(skyCol, vec3(1.), noise(normal.xz * 5.) * noise(normal.xz * 1.) * .2),
        smoothstep(-1., 1., dot(normal, vec3(0, 1, 0)))
    );

    return skyCol + getSunCol(normal, lightDir);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv *= vec2(1, -1);

    vec2 muv = iMouse.xy / iResolution.xy;

    camera cam = camera(
        vec3(-cos(muv.x * 10.) * 7., muv.y * 20. - 10., -sin(muv.x * 10.) * 7.),
        vec3(0),
        vec3(0),
        vec3(0)
    );

    cam.lookVec = normalize(vec3(0, 0.5, 0)-cam.origin);
    cam.rightVec = cross(cam.lookVec, vec3(0, 1, 0));
    cam.upVec = cross(cam.lookVec, cam.rightVec);

    vec3 lightDir = normalize(vec3(
        0.5, 2, 1
    ));

    vec3 rayDir = normalize(
        cam.lookVec + 
        cam.rightVec*uv.x + 
        cam.upVec*uv.y
    );

    vec3 col = getSkyCol(rayDir, lightDir);

    rayMarchHit marchHit = rayMarch(cam.origin, rayDir);

    if (marchHit.hasHit) {
        vec3 reflected = rayDir - (2. * dot(rayDir, marchHit.normal) * marchHit.normal);

        col = vec3(0.5,0.5,0.5);

        // fresnel
        col = mix(col, getSkyCol(reflected, lightDir), smoothstep(0.5, 1., 1.-dot(marchHit.normal, reflected)));

        // specular
        col += getSunCol(reflected, lightDir);

        // diffuse
        col *= max(dot(marchHit.normal, lightDir), 0.);

        // shadow
        rayMarchHit shadow = rayMarch(marchHit.pos - rayDir/100., lightDir);

        if (shadow.hasHit) {
            col *= 0.;
        }

        // fog
        col = mix(col, getSkyCol(rayDir, lightDir), smoothstep(0.6, 1., marchHit.distTraveled / maxDist));
    }

    fragColor = vec4(col, 1);
}

