vec3 tile_cross(in vec2 pos) {
    float col = min(abs(pos.x), abs(pos.y));
 	return mix(fg, bg, smoothstep(lw, lw+aa, col));    
}

vec3 tile_qcircle(in vec2 pos) {
    float r1 = length(pos-vec2(1.));
    float r2 = length(pos+vec2(1.));
    float d = min(r1, r2);
    d = abs(d-1.0);
 	return mix(fg, bg, smoothstep(lw, lw+aa, d));    
}

vec3 tile_qcircle_1full(in vec2 pos) {
    float r1 = length(pos-vec2(1.));
    float r2 = length(pos+vec2(1.));
    float d = min(r1, r2);
    d = abs(d-1.0);
    d = min(d, step(1.0,r2));
    d = smoothstep(lw, lw+aa, d);
 	return mix(fg, bg, d);    
}

vec3 tile(float id, vec2 p) {
    int tile_type = int(id * 3.0);
    vec3 col;
    if (tile_type > 1) {
        col = tile_qcircle_1full(p);
    } else if (tile_type > 0) {
        col = tile_qcircle(p);
    } else {
        col = tile_cross(p);
    }
    p = abs(p);
//    col = min(col, smoothstep(1.0,0.99, max(p.x, p.y)));
    return col;
}
const float n_tiles = 2.0;

