}

const float Earth = 63.71;
const float AtRad = 64.01;
const float directScatterCoef = .055;
const float inDirectScatterCoef = .3;
const float sunCoef = .1;
const vec3 rgbScatter = vec3(1,2,4);

vec2 sphIntersect( in vec3 ro, in vec3 rd, in vec3 ce, float ra )
{
    vec3 oc = ro - ce;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - ra*ra;
    float h = b*b - c;
    if( h<0.0 ) return vec2(-1.0); // no intersection
    h = sqrt( h );
    return vec2( -b-h, -b+h );
}

vec3 getSky(vec3 rd, vec3 pos, vec3 sun){
//Distances, proportaional to the real world
    float atDist = sphIntersect(pos, rd, vec3(0), AtRad).y;
    float snDist = sphIntersect(pos, sun,vec3(0), AtRad).y-1.1;

    float rds = dot(sun, rd);
    float ssc = (abs(rds)*0.5+0.5);
    vec3 tra = normalize(sun-rd);
    vec2 jab = vec2(
    max(tra.x, max(tra.y, tra.z)),
    min(tra.x, max(tra.y, tra.z))
    );
    float fla = pow(abs(sin(atan(jab.x,jab.y)*40.)), 100.)*.001;

    vec3 light = vec3(smoothstep(0.999, .9999, rds))*2.;
         light += atDist * (1.-abs(rd.y)) * ssc*0.2;
         light += (1.-exp(-rgbScatter*atDist*inDirectScatterCoef*(atDist*5.*.5+.5)));
         light *= exp(
             -rgbScatter*
             (atDist*directScatterCoef+snDist*sunCoef)
         );

     return light;
}

mat2 rot(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(c,-s,s,c);
}

vec3 aces_tonemap(vec3 color){	
	mat3 m1 = mat3(
        0.59719, 0.07600, 0.02840,
        0.35458, 0.90834, 0.13383,
        0.04823, 0.01566, 0.83777
	);
	mat3 m2 = mat3(
        1.60475, -0.10208, -0.00327,
        -0.53108,  1.10813, -0.07276,
        -0.07367, -0.00605,  1.07602
	);
	vec3 v = m1 * color;    
	vec3 a = v * (v + 0.0245786) - 0.000090537;
	vec3 b = v * (0.983729 * v + 0.4329510) + 0.238081;
	return pow(clamp(m2 * (a / b), 0.0, 1.0), vec3(1.0 / 2.2));	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uc = (uv-0.5)*vec2(1,iResolution.y/iResolution.x);
    vec3 rd = normalize(
        vec3(uc, -.7)
    );
    rd.yz*=rot(.35);

    float m = iMouse.y / iResolution.y*5.+1.;
    float f = iMouse.x / iResolution.x*10.+4.;

    vec3 sun = vec3(0,0,1);
    sun.yz *= rot(-m+.75);
    sun.xz *= rot(-f+.75);
    //sun.xz *=rot(1.5);


    vec3 col;
    vec3 ro = vec3(0,63.71002,0);
    float EarthD = sphIntersect(ro, rd, vec3(0), Earth).x;
    if (EarthD > -0.1){
        vec3 p = ro+rd*EarthD;
        vec3 nor = normalize(p);
        vec3 ref = reflect(rd, nor);
        float f = dot(rd,ref)*.25+.75;
        col = getSky(ref, p, sun)*f*vec3(0.502,0.549,0.918);
    } else
        col = getSky(rd, ro, sun);

    col = pow(col, vec3(2.4));
    fragColor = vec4(aces_tonemap(col*2.), 1.);
}

