#define MAX_STEPS 50
#define PI 3.141592653589793
#define NORMAL_MAP false // true for overly expensive normal maps

const int nfold = 7;
const float hexH = 1.;
const int hexL = 6;
vec2 center = vec2(0.);
float initR = 0.;
const float angle1 = 1. / float(nfold) * PI * 2.;
const float angle2 = PI * 0.5 - angle1 / 2.;
const float sidelength = hexH / (2. * cos(0.5 * angle1) + 1.);
const float hexW = 2. * sidelength * sin(0.5 * angle1);
const float offX = hexW / 2.;
const float offY = sidelength + ((hexH - sidelength) / 2.);

float hexSDF(in vec2 position) {
    position /= vec2(sqrt(3.0), 1.5); // magic numbers
    position.y -= 0.5;
    position.x -= fract(floor(position.y) * 0.5);
    position = abs(fract(position) - 0.5);
    return abs(1. - max(position.x * tan(angle2) * 1.15 + position.y, position.x / hexW * 1.15)) * sqrt(3.) * 0.5; // magic numbers
}

float drawHex(in vec2 position, in float size) {
    // position *= vec2(hexH,hexW)/hexH;
    float sdf = hexSDF(position);
    if (position.x < -0.5) sdf = 0.;
    if (position.x > 0.5) sdf = 0.;
    if (position.y < -0.5) sdf = 0.;
    if (position.y > 0.5) sdf = 0.;
    return smoothstep(size-0.002,size+0.002,sdf);
}

float lineSDF( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 ba = b-a;
    vec2 pa = p-a;
    float h =clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length(pa-h*ba);
}

float drawLine(in vec2 p, in vec2 a, in vec2 b, in float lt) {
    return 1.-smoothstep(0.0,lt,lineSDF(p, a, b));
}


float moveDot( in vec2 start, in vec2 end, in float dt, in float size, in vec2 uv) {
    vec2 dotpos = mix(start,end,mod(dt,1.));
    float dotstep = length(uv+dotpos);
    return 1.0 - smoothstep(size,size+0.0001,dotstep);
}

vec3 drawHexrosa(in vec2 uv, in float thic) {
    // incomplete version of hexrosa, tuned to work with n7
    vec3 color = vec3(0.);

    float dt = iTime * 0.5;

    float lines = 0.;
        // dot positions of hexagon edge. magic numbers, the lot of them
        float xedge = 0.0317 * 2.;
        float y12 = 0.139 * 1.46;
        float y1 = 0.139;
        float y2 = 0.139 * 0.55;
        float y3 = 0.;

        // points, clock wise (12 at top)
        vec2 p12 = vec2(0.,y12);
        vec2 p1 = vec2(xedge/2.,y1);
        vec2 p2 = vec2(xedge,y2);
        vec2 p3 = vec2(xedge,y3);
        vec2 p4 = vec2(xedge,-y2);
        vec2 p5 = vec2(xedge/2.,-y1);
        vec2 p6 = vec2(0.,-y12);                    
        vec2 p7 = vec2(-xedge/2.,-y1);
        vec2 p8 = vec2(-xedge,-y2);                    
        vec2 p9 = vec2(-xedge,y3);
        vec2 p10 = vec2(-xedge,y2);
        vec2 p11 = vec2(-xedge/2.,y1);

        float lt = thic*0.002;

    //the hex rosa is made of 2 sets of nfold# of what I call 'branches'. a branch is a single triangular array of 'seeds' drawn as 'hexes' (base hexagons shapes)
    //this loop doubles the process for each set of branches
    for(int outset=0;outset<2;outset++){    
        vec2 ouv = uv;
        ouv *= Rot(float(outset) * PI / float(nfold));
        //this loop divides the circle by nfold# and draws that many branches, evenly spaced
        for (int branch=0;branch<nfold;branch++){
            vec2 buv = ouv;
            buv *= Rot(angle1 * float(branch));
            buv.y -= .08 / float(nfold); // magic numbers
            buv.y += sidelength * float(outset) * 0.41; // magic numbers
            //this loop sets and positions the row being drawn in the current branch
            for (int row=0;row<hexL;row++) { // the hexagonal loop could probably be optimized
                //this loop sets and positions the individual seed being drawn in the current row
                for (int col = row * -1; col <= row; col += 2) {
                    vec2 huv = buv;
                    vec2 hexcenter = vec2(float(col)*offX,float(row)*offY) * 0.41;
                    huv += hexcenter; // magic numbers
                    huv.y += offY/PI;

                    float mainhex = drawHex(huv, 0.749); // magic numbers
                    color += mainhex * 0.8;

                    lines += drawLine(huv,p12,p2,lt);
                    lines += drawLine(huv,p12,p10,lt);
                    lines += drawLine(huv,p2,p8,lt);
                }            
            }            
        }
    }

    color = vec3(lines);

    return clamp(color, vec3(0.),vec3(1.));
}
vec3 drawHexrosa(in vec2 uv) {
    return drawHexrosa(uv, 5.);
}

struct March 
{
    float dist;
    int mater;
    bool hit;
    vec3 p;
};

// array of structs not working, neither is declaring mat1
// struct Mater
// {
//     vec3 col;
// };

// Mater mat1;
// mat1.col = vec3(1.,0.,0.);

vec3 mat1 = vec3(1.,0.,0.);

float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}
float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float sdCube( vec3 p, float b ) {
    return sdBox(p,vec3(b));
}
float sdCutSphere( vec3 p, float r, float h )
{
  // sampling independent computations (only depend on shape)
  float w = sqrt(r*r-h*h);

  // sampling dependant computations
  vec2 q = vec2( length(p.xz), p.y );
  float s = max( (h-r)*q.x*q.x+w*w*(h+r-2.0*q.y), h*q.x-w*q.y );
  return (s<0.0) ? length(q)-r :
         (q.x<w) ? h - q.y     :
                   length(q-vec2(w,h));
}

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

float Bounce3(float x, float base) {

    x = -log(1.-x)/log(base);
    x -= (1.-pow(base, -.5)) / (1.-1./base);

    float i = floor(x);

    x = fract(x);

    x = (1.-pow(base, -x)) / (1.-1./base);
    float bounce = -4.*x*(x-1.);

    bounce /= pow(base*.8, i+1.);
    return .4*bounce;   
}

March map(in vec3 p, in int mode) {
    float d = 1000.;
    int mater = -1;

    if (mode==0) {

        float floatyT = sin(PI * 1.5 + iTime * 0.5); // timing of floaters
        float floatheight = 1.25 + 0.5 * floatyT; // float up
        if (mod(iTime*0.5,PI*2.)/(PI*2.) > 0.5) { // hover at top
            floatheight = 1.75;
        }
        if (mod(iTime*0.5,PI*2.)/(PI*2.) > 0.75) { // fall
            // floatheight = Bounce3(fract(iTime*0.5), 0.5);
            floatheight = .75 + 2.5*Bounce3(fract(iTime*0.5*(2./PI)), 0.7);
        }
        vec3 floatyp = p - vec3(0.,floatheight,0.);
        float ballsize = sin(PI*1.5+iTime*0.25)*0.5;
        ballsize = clamp(ballsize+0.15, 0.05, 0.2);
        float ball = length(floatyp)-ballsize;
        if (ball < d) mater = 1;
        // d = min(d,ball);

        floatyp.xz *= Rot(iTime + sin(PI*1.15+iTime*0.5)*1.8);
        if (mod(iTime * 0.5, PI*2.) < PI) 
            floatyp.xy *= Rot(iTime * 0.25);
        // floatyp.yz *= Rot(iTime * 0.08);
        float cubesize = sin(PI*.5+iTime*0.25)*0.5;
        cubesize = clamp(cubesize+0.15, 0.05, 0.15);
        float cube = sdCube(floatyp,cubesize) - 0.02;
        if (cube < d) mater = 1;
        // d = min(d,cube);
        d = min(d,smin(cube,ball,0.1));
    }

    float brickfloor = p.y - .6;
    if (brickfloor < d) mater = 2;
    d = min(d, brickfloor);

    vec3 rock1p = p;
    rock1p.x = mod(rock1p.x - 2., 2.) + 2.;
    rock1p.z = mod(rock1p.z - 1.5, 2.) + 1.5;
    rock1p.y *= 1.1;
    float rocksize = noise(p.xz*3.)*0.4;
    rocksize *= smoothstep(3.,4.,length(p.xz));
    float rock1 = length(rock1p-vec3(2.5,0.55,2.))-rocksize;
    rock1 -= noise(p.xz*5.) * 0.2;
    rock1 -= noise(p.xz*8.) * 0.1;
    if (rock1 < d) mater = 4;
    d = min(d, rock1);

    vec3 mush1p = p;
    mush1p.xz *= Rot(0.5);
    mush1p.xz += 3. + sin(p.x) * 0.2;
    mush1p.y -= .6;
    float mush1s = .2;
    // mush1p.x = mod(mush1p.x - 1., 2.) + 1.;
    mush1p.z = mod(mush1p.z, 3.) - 1.5;
    mush1p.x = mod(mush1p.x, 3.) - 1.5;
    mush1p.x += sin(p.z)*0.2;
    mush1p.z += sin(p.x)*0.2;
    float mush_stem = sdVerticalCapsule(vec3(mush1p),0.8*mush1s,0.08*mush1s);
    float mush = mush_stem;
    float mush_cap = length(mush1p-vec3(0.,1.*mush1s,0.))-.1*mush1s;
    mush_cap = sdCutSphere(mush1p-vec3(0.,1.*mush1s,0.), .25*mush1s, 0.);
    mush = min(mush, mush_cap);
    if (mush < d) mater = 5;
    d = min(d, mush);

    float clsize = 0.09;

    if (length(p.xz) > 2.) {
    for (int j=0;j<3;j++) {

        vec3 cloverp = p - vec3(1.,.6,0.);

        cloverp *= 0.5 + float(j)/8.;
        cloverp.x += float(j);

        cloverp.xz *= Rot(0.8 + 0.3*sin(length(cloverp.xz)));
        cloverp.x = mod(cloverp.x, .8) - 0.15;
        // cloverp.y = mod(cloverp.z, .3) - 0.15;
        cloverp.z = mod(cloverp.z, .8) - 0.15;
        cloverp.xz *= Rot(noise(p.xz) * 5.);

        cloverp.x += sin(p.x + iTime*0.5) * cloverp.y * .3;
        cloverp.z += sin(p.x + iTime*0.22) * cloverp.y * .2;
        //
        // cloverp.x += noise(p.xz) * 0.05;


        // cloverp.x += cloverp.y;

        float cloverstem = sdVerticalCapsule(vec3(cloverp),clsize*0.6,clsize*0.05);
        float clover = min(cloverstem, 2000.);
        cloverp.xz *= Rot(0.2);
        float cloverleaf1 = sdCappedCylinder(cloverp - clsize*vec3(.2,.7,0.),clsize*0.18,clsize*0.02) - 0.01*clsize;
        cloverp.yz *= Rot(-0.1);
        float cloverleaf2 = sdCappedCylinder(cloverp - clsize*vec3(-.08,.69,-.12),clsize*0.18,clsize*0.018) - 0.01*clsize;
        cloverp.xy *= Rot(0.1);
        float cloverleaf3 = sdCappedCylinder(cloverp - clsize*vec3(-.08,.69,.12),clsize*0.18,clsize*0.015) - 0.01*clsize;
        clover = min(clover, cloverleaf1);
        clover = min(clover, cloverleaf2);
        clover = min(clover, cloverleaf3);
        if (clover < d) mater = 3;
        d = min(d, clover);


    }
    }

    March result;
    result.dist = d;
    result.mater = mater;
    result.hit = d < 0.0001;
    result.p = p;

    return result;
}
March map(in vec3 p) {
    return map(p,0);
}

March raymarch(in vec3 ro, in vec3 rd, in int steps, in int mode) {
    March march; // initialize result struct
    vec3 p;
    float d = 0.;
    for (int i=0;i<steps;i++){
        p = ro + rd * d;
        march = map(p,mode);
        d += march.dist;
        if (march.hit) break;
        if (march.dist > 100.) break;
    }
    return march;
}

March raymarch(in vec3 ro, in vec3 rd) {
    return raymarch(ro,rd,MAX_STEPS, 0);
}

vec3 GetNormal(vec3 p) {
	float d = map(p).dist;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        map(p-e.xyy).dist,
        map(p-e.yxy).dist,
        map(p-e.yyx).dist);

    return normalize(n);
}

float GetLight(vec3 p, vec3 n) {
    float floatyT = sin(PI*1.5 + iTime * 0.5); // timing of floaters
    vec3 lightPos = vec3(0., 2. + 0.5*floatyT, 0.);
    vec3 l = normalize(lightPos-p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = raymarch(p+n*0.0001*2., l, 60, 1).dist;
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}
float GetLight(vec3 p) {
    vec3 n = GetNormal(p);
    return GetLight(p,n);
}

vec2 hexrosaNormal(in vec2 uv) { // expensive, ~33% GPU
    if (!NORMAL_MAP) return vec2(0.);
    float s = 0.001;

    float p = drawHexrosa(uv,7.).r;
    float h1 = drawHexrosa(uv + vec2(s,0),7.).r;
    float v1 = drawHexrosa(uv + vec2(0,s),7.).r;

   	return (p - vec2(h1, v1)) * 10.;
}

vec3 getCol(in March march) {
    vec3 col = vec3(0.);
    vec3 norm = GetNormal(march.p);
    if (march.mater == 1) {
        col = vec3(0.95,0.94,0.99);
    }
    if (march.mater == 2) {
        vec3 hexrosa = drawHexrosa(march.p.xz * .4);
        col = 1. - hexrosa;
        vec3 bricktexture = texture(iChannel0,march.p.xz * 0.1 * iResolution.x/800.).rgb;
        col *= bricktexture * vec3(.9,0.8,0.6);
        vec2 hexnorm = hexrosaNormal(march.p.xz * .4);
        norm.rg += hexnorm;


        // grass zone
        vec2 grasslim = march.p.xz * 0.5 - 0.5;
        grasslim += texture(iChannel0,march.p.xz).rb;
        grasslim += texture(iChannel0,march.p.xz * 0.05).rb - 0.5;
        float grasszone = length(grasslim) - 1.1;
        grasszone = clamp(grasszone,0.,1.);

        vec3 grass = texture(iChannel0,march.p.xz * Rot(0.5) * .5).rgb * vec3(0.1,0.5,0.1);
        grass -= texture(iChannel0,march.p.xz * Rot(-0.5) * .1).rgb * vec3(0.05,0.3,0.05);
        col = mix(col,grass,grasszone);

        // col = vec3(grasszone);

        float mossbricks = grasszone * hexrosa.r * (1. - grasszone);
        mossbricks += mossbricks;
        mossbricks *= mossbricks;
        col.g += mossbricks;

        float floatyT = min(1.25,sin(PI*1.75 + iTime * 0.5) + 0.75); // timing of floaters

        float glowbricks = hexrosa.r * (1. - grasszone);
        col.r += clamp(glowbricks * (2.*floatyT - length(march.p.xz)),0.,1.);

        float glowbricksfine = drawHexrosa(march.p.xz * .4,3.).r * (1. - grasszone);
        col.g += clamp(glowbricksfine * glowbricksfine * (1.5*floatyT - length(march.p.xz)),0.,1.);


        float glowbrickscenter = hexrosa.r * (1. - grasszone);
        col.b += clamp(glowbricksfine * glowbricks * (1.*floatyT - length(march.p.xz)),0.,1.);

    }
    if (march.mater == 3) { // clovers
        col = vec3(0.01,0.25,0.05);
    }
    if (march.mater == 4) { // rocks
        vec3 bricktexture = texture(iChannel0,march.p.xz * 0.1).rgb;
        col = vec3(1.);
        col *= bricktexture * vec3(0.6,0.57,0.49);
        col.g += smoothstep(0.2,.45,1.-march.p.y) * 0.11;
        col.g += smoothstep(0.35,.45,1.-march.p.y) * 0.6;
        col -= smoothstep(0.35,.45,1.-march.p.y) * 0.5;
        col *= (1. - smoothstep(0.3,.4,1.-march.p.y) * 0.5);
        col *= 0.6+march.p.y*march.p.y;
    }
    if (march.mater == 5) { // mushies
        col = vec3(0.86,0.7,0.5);
        col = mix(col,col*norm.g,0.6);
        col += 1.-march.p.y*1.6;
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec3 col = vec3(0.);
    vec2 uv = (2.*fragCoord - iResolution.xy ) / iResolution.y;

    // raymarch
    vec3 ro = vec3(0.,5.,-3.);
    vec3 rd = normalize(vec3(uv.x,uv.y,2.));

    float d = 0.;
    ro.xz *= Rot(iTime*0.02);
    // rd.yz *= Rot(0.5);
    rd.yz *= Rot(1.);

    rd.xz *= Rot(iTime*0.02);

    March march = raymarch(ro,rd);

    if (march.hit) {
        col = mat1;
    }

    // for debugging
    // if (march.mater == 5) {fragColor = vec4(1.,0.,0.,1.); return;}

    vec3 norm = GetNormal(march.p);

    col = getCol(march);
    if (march.mater == 1) {
        march.dist += 0.001;
        // single bounce reflection
        ro = ro + rd * march.dist;
        rd = reflect(rd, norm);
        March bounce = raymarch(ro,rd);
        vec3 bouncecol = getCol(bounce);
        // if (bounce.hit) col = getCol(bounce);
        vec3 forest = texture(iChannel1,rd).rgb;

        float floatyT = sin(iTime * 0.5); // timing of floaters
        bouncecol.r += floatyT * 0.7;
        forest += floatyT * 0.5;

        col = mix(bouncecol,forest,smoothstep(0.4,0.5,norm.g));
    }

    float shad = GetLight(march.p, norm);
    shad = mix(shad,1.,0.8); // dim the lights
    col *= shad;

    // Output to screen
    fragColor = vec4(col,1.0);
}

