          M = iMouse.xy/R;
    if ( int(u) ==int(R)/2 ) { O = vec4(1,0,0,1); return; } // red separator

    float Z = 8., H = 1.5, a,                       // Z: object distance
          W = iMouse.z > 1e4 ? .38/ M.x : 1.;       // 1/W: camera opening
    W = 1./max(0.,texelFetch(iChannel1,ivec2(0),0).y );
    vec3  P, D = vec3( U , W );                     // ray direction: left: flat perspective
    if (D.x>0.) {                                   //                right: cylindrical perspective (panorama)
          a = U.x/W;
          D.xz = W* vec2(sin(a),cos(a));
          if (abs(a) > 3.14/2.) O.r+=.5;            // view > 180° : mid-red alert
          if (abs(a) > 3.14)    O.r+=.5;            // view > 360° : red alert
       }
    D = normalize(D);
    if (iMouse.z > 0.)
      D.yz *= rot( 1.57*(2.*M.y-1.) ),
      D.xz *= rot( 1.57*(2.*M.x-1.) );

    P = Z* D/abs(D.z);                              // draw flat ribbon 
    if ( abs(P.y-1.) < .5 && P.z>0. ) { 
        O += T(P.xy-.5); return; }                  // with mapped texture

    P = Z* D/length(D.xz);                          // draw cylindrical ribbon
    if ( abs(P.y+1.) < .5 ) {                       // with mapped texture
        O += T(vec2(Z*atan(P.x,P.z),P.y)-.5); return; } 

    P = H * D/abs(D.y);
    if ( P.y > 0. ) {                               // draw roof square grid
        O +=   T(P.xz+vec2(.5,0) ) 
             * vec4(0,1,1,0) * exp(-P.z/Z);         // color + darkening with z
        return; } 

    if ( P.y < 0. ) {                               // draw floor polar grid
        O +=  T(vec2(Z*atan(P.x,P.z),Z*log(length(P.xz)))+vec2(.5,0) )
             * vec4(1,.5,0,0) * exp(-P.z/Z);        // color + darkening with z
        return; }
}

