//画线函数,p为uv,即屏幕上任意一点,ab为线段两端点,size.x是线段宽,
//size.y是线段边缘模糊百分比,color是线段颜色
vec3 DrawLine(vec2 uv,vec2 a,vec2 b,vec2 size,vec3 color)
{
    vec2 ap = uv-a;
    vec2 ab = b-a;

    float t = dot(ap,ab)/dot(ab,ab);//点p在线段上投影占ab的百分比
    t = clamp(t,0.0,1.0);

    //ap-ab*t表示点p-点p到线段ab的垂点,即d是点p到线段的距离
    float d = length(ap-ab*t);
    float s = smoothstep(size.x,size.x-size.y*size.x,d);

    return s*color;
}
//画圆函数,p表示圆心,uv表示屏幕上任意一点,size.x是圆半径
//size.y表示圆边缘模糊百分比,color是圆的颜色
vec3 DrawCircle(vec2 p,vec2 uv,vec2 size,vec3 color)
{
    uv -= p;
    float d = length(uv);//uv点到中心点的距离
    d = smoothstep(size.x,size.x-size.x*size.y,d);

    return d*color;
}
//点p到直线ab的距离
float DistanceToLine(vec2 p,vec2 a,vec2 b)
{
    vec2 ap = p-a;
    vec2 ab = b-a;
    float t = dot(ap,ab)/dot(ab,ab);
    vec2 n = a+ab*t;
    return length(p-n);
}
//画三角形函数,uv同上,a,b,c是三角形三个顶点,color是三角形颜色
vec3 DrawTriangle(vec2 uv,vec2 a,vec2 b,vec2 c,vec3 color_a,
    vec3 color_b,vec3 color_c)
{
    vec2 ap = uv-a;
    vec2 bp = uv-b;
    vec2 cp = uv-c;
    vec2 ab = b-a;//逆时针方向的三角形三条边
    vec2 bc = c-b;
    vec2 ca = a-c;

    //ap与ab,bp与bc,cp与ca的三个叉乘符号若是同号,代表点p在三角形内
    //这是一种判断点是否在三角形内的方法,还可以根据三个小三角形面积是否等于
    //大三角形面积判断
    float c1 = ap.x*ab.y-ap.y*ab.x;
    float c2 = bp.x*bc.y-bp.y*bc.x;
    float c3 = cp.x*ca.y-cp.y*ca.x;

    float s = DistanceToLine(a,b,c);//大三角形的面积
    if(c1*c2>0.0 && c2*c3>0.0)
    {
        float d1 = DistanceToLine(uv,a,b);//uv到ab的距离
        float d2 = DistanceToLine(uv,b,c);//uv到bc的距离
        float d3 = DistanceToLine(uv,c,a);//uv到ca的距离

        //三个小三角形的面积,用于除以总面积表示重心坐标
        float s1 = d1*length(ab);
        float s2 = d2*length(bc);
        float s3 = d3*length(ca);

        //s2/s是对应a点颜色比例,s3/s是对应b点颜色比例
        //s1/s是对应c点颜色比例,画图之后更清晰易懂
        return s2/s*color_a+s3/s*color_b+s1/s*color_c;
    }

    return vec3(0.0);
}
//画矩形函数,uv同上,ab分别是矩形的左上点和右下点,blur的xy分别是
//xy方向的模糊百分比,color是矩形颜色
vec3 DrawRectangle(vec2 uv,vec2 a,vec2 b,vec2 blur,vec3 color)
{
    float x = b.x-a.x;
    float y = a.y-b.y;

    //矩形的四个边界,左右下上
    float x1 = smoothstep(0.0,x*blur.x,uv.x-a.x);
    float x2 = smoothstep(x,x-x*blur.x,uv.x-a.x);
    float y1 = smoothstep(0.0,y*blur.y,uv.y-b.y);
    float y2 = smoothstep(y,y-y*blur.y,uv.y-b.y);

    return x1*x2*y1*y2*color;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //将(0,0)点移动到屏幕中心,并使得xy方向单位长度相同
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    //线段的两个端点
    vec2 a = vec2(-0.5,0.1);
    vec2 b = vec2(-0.2,0.4);
    //圆心
    vec2 p = vec2(0.4,0.2);
    //三角形三个顶点坐标和颜色
    vec2 t1 = vec2(-0.4,-0.1);
    vec2 t2 = vec2(-0.6,-0.4);
    vec2 t3 = vec2(-0.2,-0.4);
    vec3 c1 = vec3(1,0,0);
    vec3 c2 = vec3(0,1,0);
    vec3 c3 = vec3(0,0,1);
    //矩形左上点后右下点
    vec2 r1 = vec2(0.2,-0.05);
    vec2 r2 = vec2(0.6,-0.45);

    col += DrawLine(uv,a,b,vec2(0.01,0.1),vec3(1.0,0.0,0.0));
    col += DrawCircle(p,uv,vec2(0.2,0.1),vec3(0.0,1.0,0.0));
    col += DrawTriangle(uv,t1,t2,t3,c1,c2,c3);
    col += DrawRectangle(uv,r1,r2,vec2(0.1,0.1),vec3(1.0,0.0,1.0));

    fragColor = vec4(col,1.0);
}

