vec3 accLight;
vec3 trace(vec3 ro, vec3 rd, int steps)
{
  accLight = vec3(0.);
  vec3 p  = ro;
  for (int i = 0; i < steps; ++i)
  {
    vec2 res = map(p);
    if (res.x < 0.01)
      return vec3(res.x, distance(p, ro), res.y);
    if (res.y < 0.)
      accLight += (vec3(172, 38, 235)/255.)*0.1+vec3(sin(distance(p, ro)*1.+iTime)*.5+.5, .5, .1)*(1.-sat(res.x/15.5))*.5*sat(sin(p.y*.05+5.*iTime));
    rd = normalize(rd+normalize(p)*.1);
    p+=rd*res.x*.25;
  }
  return vec3(-1.);
}

vec3 rdr(vec2 uv)
{
  vec3 background = (vec3(212, 140, 32)/255.).zxy;
  vec3 col = background;

  vec3 dof = vec3(rand()-.5, rand()-.5, 0.)*.1*FFTS(.05)*1.;
  vec3 ro = vec3(0.,-5.,-5.)-dof*.1;
  vec3 ta = vec3(sin(iTime)*5.,85.,0.);
  vec3 rd = normalize(ta-ro);

  rd += dof*.1;
  rd = getCam(rd, uv);

  float depth = 150.;
  vec3 res = trace(ro, rd, 128);
  if (res.y > 0.)
  {
    depth = res.y;
    vec3 p = ro+rd*res.y;
    vec3 n = getNorm(p, res.x);
    col = n*.5+.5;

    col = (vec3(23, 24, 51)/255.).zxy*sat(dot(normalize(vec3(n.x, -1., n.z)), n));
  }
  col += accLight;
  col = mix(col, background, 1.-sat(exp(-depth*depth*0.001)));
  return col;
}

