#define RAY_MAX_ITERATIONS 100
#define RAY_MAX_DISTANCE 100.0
#define RAY_SURF_DISTANCE 0.0001

#define PI 3.1415926535897932384626433832795
#define TAU (PI * 2.0)

float sine(float t) { return sin(t * PI); }
float cose(float t) { return cos(t * PI); }

mat2 rot(float r) {
    float s = sin(r), c = cos(r);
    return mat2(c, -s, s, c);
}

mat2 rote(float r) {
    return rot(r * PI);
}

mat3 rotX(float r) {
    float s = sin(r), c = cos(r);
    return mat3(
        1.0, 0.0, 0.0,
        0.0, c  , -s ,
        0.0, s  , c
    );
}

mat3 rotY(float r) {
    float s = sin(r), c = cos(r);
    return mat3(
        c  , 0.0, -s ,
        0.0, 1.0, 0.0,
        s  , 0.0, c
    );
}

mat3 rotZ(float r) {
    float s = sin(r), c = cos(r);
    return mat3(
        c  , -s , 0.0,
        s  , c  , 0.0,
        0.0, 0.0, 1.0
    );
}

mat3 roteX(float r) {
    return rotX(r * PI);
}

mat3 roteY(float r) {
    return rotY(r * PI);
}

mat3 roteZ(float r) {
    return rotZ(r * PI);
}

#define range(a, b, x) (((x) - (a)) / ((b) - (a)))
#define saturate(x) clamp(x, 0.0, 1.0)
#define linearstep(a, b, x) saturate(range(a, b, x))
#define cmix(a, b, x) mix(a, b, saturate(x))

#define steprange(a, b, t) (step(a, t) * step(t, b))

#define distanceSqr(v) dot(v, v)

float easeIn(float x) {
    return x * x;
}

float easeOut(float x) {
    return 1.0 - easeIn(1.0 - x);
}

float easeInOut(float x) {
    return mix(easeIn(x), easeOut(x), x);
}

float easeIn(float a, float b, float x) {
    return easeIn(linearstep(a, b, x));
}

float easeOut(float a, float b, float x) {
    return easeOut(linearstep(a, b, x));
}

float easeInOut(float a, float b, float x) {
    return easeInOut(linearstep(a, b, x));
}

float sdPlane(vec3 p, float y) {
    return p.y - y;
}

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdTorus(vec3 p, float t, float r) {
  return length(vec2(length(p.xz) - t, p.y)) - r;
}

float sdCappedCylinder(vec3 p, float h, float r) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

struct mapresult {
    float d;
    int m;
};

mapresult map(vec3 p) {
    float d = RAY_MAX_DISTANCE;
    int m = 0;

    float sd;

    // Time loop control
    float tx = iTime * 0.25;
    float ti = floor(tx);
    float tf = tx - ti;

    float slicedFactor = step(tf, 1.0 - 0.25); // 1.0 if slicing is complete, 0.0 otherwise.

    // Ball
    {
        vec3 o = vec3(0.0);

        o.x = -16.0;
        o.x += 16.0 * easeOut(0.0, 0.5, tf);
        o.x += 12.0 * easeOut(0.75, 1.0, tf);

        o.y = 0.0;
        o.y += -8.0 * easeIn(0.75, 1.0, tf);

        vec3 po = p - o;

        sd = sdSphere(po, 1.0);
        if(sd < d) {
            d = sd;
            m = 2;
        }

        // Keep the sliced detail
        po.xy *= rote(iTime);
        sd = -sdBox(po, vec3(0.0625, 4.0, 4.0));
        sd = mix(sd, 0.0, slicedFactor);
        if(sd >= d) {
            d = sd;
            m = 2;
        }
    }

    // Slicer
    {
        vec3 originp = p;
        originp -= vec3(0.0, 0.0, 2.0);
        originp.yz *= rote(tf - 0.125);

        // Cut out ball
        sd = -sdBox(originp - vec3(0.0, 4.0, 4.0), vec3(0.0625, 4.0, 4.0));
        sd = mix(0.0, sd, slicedFactor);
        if(sd >= d) {
            d = sd;
            m = 2;
        }

        sd = sdCappedCylinder(originp - vec3(0.0, 0.0, 0.0), 4.0, 0.0625);
        sd = min(sd, sdCappedCylinder(originp.yxz - vec3(0.0, -16.0 + 0.125, 0.0), 16.0, 0.09375));
        if(sd < d) {
            d = sd;
            m = 3;
        }
    }

    // Path
    {
        sd = sdBox(p - vec3(-8.0 - 0.0625, -10.125, 0.0), vec3(8.0, 10.0, 16.0) - 0.0625);
        sd -= 0.0625;
        sd = max(sd, -sdCappedCylinder(p.yxz - vec3(0.0, -8.0, 0.0), 9.0, 1.0));
        if(sd < d) {
            d = sd;
            m = 1;
        }
    }

    // Pusher
    {
        vec3 o = vec3(0.0);
        o.x = -20.0;
        o.x += -8.0;
        o.x -= 1.0;
        o.x += 16.0 * easeOut(0.0, 0.5, tf);
        o.x += 4.0 * easeInOut(0.625, 0.5, tf);
        o.x += 4.2 * easeIn(0.625, 0.75, tf);
        o.x += -24.0 * easeIn(0.75, 1.0, tf);

        sd = sdCappedCylinder(p.yxz - o.yxz, 8.0, 0.25);
        if(sd < d) {
            d = sd;
            m = 3;
        }

        sd = sdCappedCylinder(p.yxz - o.yxz - vec3(0.0, 8.0 - 0.125, 0.0), 0.125, 0.5);
        if(sd < d) {
            d = sd;
            m = 3;
        }
    }

    return mapresult(d, m);
}

mapresult march(vec3 ro, vec3 rd) {
    float d = 0.0;
    int m = 0;
    for(int i = 0; i < RAY_MAX_ITERATIONS; i++) {
        mapresult mr = map(ro + (rd * d));
        float sd = mr.d;
        d += sd;
        m = mr.m;
        if(d > RAY_MAX_DISTANCE) return mapresult(RAY_MAX_DISTANCE, 0);
        if(abs(sd) < RAY_SURF_DISTANCE) break;
    }
    return mapresult(d, m);
}

vec3 normal(vec3 p) {
    vec2 e = vec2(RAY_SURF_DISTANCE * 2.0, 0.0);
    return normalize(vec3(
        map(p + e.xyy).d - map(p - e.xyy).d,
        map(p + e.yxy).d - map(p - e.yxy).d,
        map(p + e.yyx).d - map(p - e.yyx).d
    ));
}

float shadow(vec3 origin, vec3 dir) {
    return step(RAY_MAX_DISTANCE, march(origin, dir).d);
}

float directionalLightShaded(vec3 origin, vec3 direction, vec3 normal) {
    return dot(normal, direction) * shadow(origin + ((normal * RAY_SURF_DISTANCE) * 2.0), direction);
}

vec3 sky(vec3 rd, vec3 sunDir) {
    float sun = dot(rd, sunDir);
    float sunk = (sun * 0.5) + 0.5;
    float suna = pow(sunk, 4.0);
    float sunb = pow(suna, 32.0);

    vec3 skyColorOut = mix(vec3(0.6, 0.6, 0.7), vec3(3.0, 1.5, 0.7), suna);
    skyColorOut = mix(skyColorOut, vec3(5.0, 4.0, 2.5), sunb);
    skyColorOut = mix(skyColorOut, vec3(8.0, 6.0, 4.0), smoothstep(0.9997, 0.9998, sunk));

    float m = 1.0 - pow(1.0 - max(rd.y, 0.0), 4.0);

    return skyColorOut;
}

vec3 aces(vec3 x) {
    return clamp((x * ((2.51 * x) + 0.03)) / (x * ((2.43 * x) + 0.59) + 0.14), 0.0, 1.0);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    float aspect = max(iResolution.x / iResolution.y, iResolution.y / iResolution.x);
    vec2 uv = (fragCoord / iResolution.xy) - 0.5;
    uv.x *= aspect;
    uv *= 2.0;

    vec3 ro = vec3(0.0, 0.0, -10.0);
    vec3 rd = normalize(vec3(0.0, 0.0, 1.0));
    ro.xy += uv * 5.0; // Ortho projection

    mat3 rotation =
        roteX(0.125) *
        roteY(0.25);

    ro *= rotation;
    rd *= rotation;

    mapresult mr = march(ro, rd);
    float d = mr.d;
    int m = mr.m;
    vec3 p = ro + (rd * d);
    vec3 n = normal(p);

    vec3 col = vec3(0.0, 0.0, 0.0);

    float skyFactor = d / RAY_MAX_DISTANCE;

    vec3 sunDir = normalize(vec3(0.5, 0.9, 0.5));

    vec3 skyColor = sky(rd, sunDir);

    vec3 surfaceColor = vec3(0.0);

    // Path
    if(m == 1) {
        surfaceColor = vec3(0.0);
    }
    // Ball
    else if(m == 2) {
        surfaceColor = vec3(0.3);
    }
    // Other stuff
    else if(m == 3) {
        surfaceColor = vec3(-0.25);
    }

    vec3 surfaceReflColor = vec3(0.0);
    for(int i = 0; i < 30; i++) {
        float seed = texture(iChannel3, vec2(iTime, float(i) * 0.62842)).x;
        vec3 rdRefl = reflect(rd, n);
        rdRefl = normalize(rdRefl + ((texture(iChannel3, vec2(p.x + p.z, p.y + p.z) + seed).xyz - 0.5) * 2.0) * 2.0);
        surfaceReflColor += sky(rdRefl, sunDir);
    }
    surfaceReflColor /= 30.0;
    surfaceColor += surfaceReflColor;

    surfaceColor *= mix(directionalLightShaded(p, sunDir, n), 1.0, 0.5);

    col = mix(
        surfaceColor,
        skyColor,
        skyFactor
    );

    col = aces(col);

    col *= steprange(-1.0, 1.0, uv.x);
    col *= steprange(-1.0, 1.0, uv.y);

    fragColor = vec4(col, 1.0);
}

