    return mat3(
        c  , -s , 0.0,
        s  , c  , 0.0,
        0.0, 0.0, 1.0
    );
}

mat3 roteX(float r) {
    return rotX(r * PI);
}

mat3 roteY(float r) {
    return rotY(r * PI);
}

mat3 roteZ(float r) {
    return rotZ(r * PI);
}

float sdPlane(vec3 p, float y) {
