{
  _seed = texture(iChannel2, fragCoord.xy/iResolution.xy).x+iTime;
	vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.xx;
  vec2 ouv = uv;
  //uv *= r2d(-iTime*.5);
  //uv = abs(uv);
  //uv -= vec2(.2+uv.y, 0.);
  uv *= r2d(.1*iTime);
  //uv = abs(uv);
  //uv *= r2d(sin(mix(lenny(uv), length(uv), -2.)*15.-iTime));
 float stp = .2*length(uv);
  vec2 uv2 = floor(uv/stp)*stp;
    uv2 *= r2d(.01*iTime);
  uv2 = abs(uv2);
  //uv *= r2d(FFTI(.1)*10.);
  //uv = abs(uv);
  //uv -= vec2(.25);

  vec3 col = rdr2(uv+rdr(uv).xy*.1)*1.;
  col += rdr(uv2)*.25;
  col *= 1.-sat(length(uv));
  //col = mix(vec3(0.), vec3(199, 242, 58)/255., col*.4);
  col = pow(col, vec3(2.45));
  //col *= vec3(199, 242, 58)/255.;
  float beat = 1./8.;
//  col += (mod(iTime, beat)/beat)*sat(FFTS(.1)*col)*45.;
  float repy = .05;
  float idy = floor((ouv.y+repy*.5)/repy);
  col = mix(col, col.zxy, mod(idy+float(int(iTime*8.)),5.)/5.);

col = mix(col, texture(iChannel0, fragCoord.xy/iResolution.xy).xyz, .5);
  col.xy *= r2d(iTime*.5);
  col = abs(col);

	fragColor = vec4(col.yxz, 1.);
}

