float circleSDF(vec2 uv)
{
   return length(uv) - 0.1; 
}


float Moon(vec2 uv)
{
    float scale = 3.f;
    float l1 = length(uv) - 0.1 * scale;
    float l2 = distance(vec2(0.03,0)* scale, uv) - 0.085 * scale;

    return max(l1, -l2);

}

float sdHexagon( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

float sdSquare(vec2 p)
{
  vec2 k = normalize(vec2(-0.5, 0.5));
  p = abs(p);
  float dk = dot(k.xy,p);
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;

  k = normalize(vec2(-0.923, 0.328));
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;

  k = normalize(vec2(0.5, -0.5));
  p -= 4.0*min(dot(k.xy,p),0.0)*k.xy;
  return sign(p.x - 0.1f);



  //return sign(p.y - 0.1f);
}

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

float sdStar5(in vec2 p, in float r, in float rf)
{
    vec2 k1 = vec2(0.809016994375, -0.587785252292);
    vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;
    p.x = abs(p.x);

    vec2 k = normalize(vec2(k1.x, k1.y));
    p -= 3.0*min(dot(k.xy,p),0.0)*k.xy;


    return sign(p.x - 0.08f);

    //p.y -= r;
    //vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
    //float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    //return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = (fragCoord-0.5 * iResolution.xy)/iResolution.y * 0.8;    
    uv.x = ((fragCoord-0.5 * iResolution.xy)/iResolution.y).x * 0.8 + 0.1;

    vec2 ux = fragCoord/iResolution.xy;
    float t = uv.x*7.-2.*iTime+uv.y*3.;
    uv.y += sin(t) * 0.02;    
    ux.y += sin(t) * 0.02;    

    //uv.y += sin(iTime + (1.0f + uv.x) * .6) * 0.1;    
    //uv.x += sin(iTime * uv.x)/ 32.f;


    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = vec3(1,0,0);
    col = mix(vec3(1,1,1), col, step(0., Moon(uv)));    
    //col = mix(vec3(1,1,1), col, step(0., sdStar5(uv-vec2(0.4, 0.0), 0.1f, 0.4f)));  
    uv = rotate(uv,9.6*3.1415/32.);
    vec2 translate = rotate(vec2(0.3, 0.0),9.6*3.1415/32.);
    col = mix(vec3(1,1,1), col, step(0., sdStar5(uv - translate, 0.1f, 0.4f)));
    //col = mix(vec3(0,0,0), col, step(0., ux.y-0.01));
    col = mix(vec3(0,0,0), col, smoothstep(0. , 0.04, ux.y));    
    col = mix(vec3(0,0,0), col, smoothstep(1. , 0.96, ux.y));    
    col = mix(vec3(0,0,0), col, smoothstep(1. , 0.98, ux.x));


    //col = mix(vec3(1,1,1), col, step(0., sdHexagon(uv-vec2(0.0, 0.0), 0.1f)));    
    //col = mix(vec3(1,1,0), col, step(0., circleSDF(uv-vec2(0.07, 0.0))));    
    //col = mix(vec3(0.5,1,0), col, step(0., sdSquare(uv-vec2(0.5, 0.0))));




    col *= 0.8 - cos(t) * 0.1;
    // Output to screen
    fragColor = vec4(col,1.0);
}

/*
See these sites for the details.

https://www.osar.fr/notes/logspherical/
https://github.com/pac-dev/notes/blob/master/content/logspherical/logpolar3d_rods.glsl
*/

#define M_PI 3.14151692
#define AA 1


// These lines are parsed by dspnote to generate sliders
float camera_y = 0.5; //dspnote param: 0.5 - 3
float rho_offset = 0.0; //dspnote param: 0 - 10
float density = 13.0; //dspnote param: 5 - 50, 13
float radius = 0.9; //dspnote param: 0.05 - 1, 0.9

float height = 0.01;
float lpscale;

float sdf(in vec3 p3d)
{
	// Choose 2 dimensions and apply the forward log-polar map
	vec2 p = p3d.xz;
	float r = length(p);
	p = vec2(log(r), atan(p.y, p.x));

	// Scale everything so it will fit nicely in the ]-pi,pi] interval
	p *= lpscale;
	float mul = r/lpscale;

	// Apply rho-translation, which yields zooming
	p.x -= rho_offset + iTime*lpscale*0.23;

	// Turn tiled coordinates into single-tile coordinates
	p = mod(p, 2.0) - 1.0;

	// Get rounded cylinder distance, using the original Y coordinate shrunk
	// proportionally to the other dimensions
	return (length(vec3(p, max(0.0, p3d.y/mul))) - radius) * mul;
}

vec3 color(in vec3 p)
{
	vec3 top = vec3(0.3, 0.4, 0.5);
	vec3 ring = vec3(0.6, 0.04, 0.0);
	vec3 bottom = vec3(0.3, 0.3, 0.3);
	bottom = mix(vec3(0.0), bottom, min(1.0, -1.0/(p.y*20.0-1.0)));
	vec3 side = mix(bottom, ring, smoothstep(-height-0.001, -height, p.y));
	return mix(side, top, smoothstep(-0.01, 0.0, p.y));
}

// Adapted from http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal(in vec3 pos)
{
	vec2 e = vec2(1.0,-1.0)*0.5773;
	const float eps = 0.0005;
	return normalize(
		e.xyy*sdf(pos + e.xyy*eps) + 
		e.yyx*sdf(pos + e.yyx*eps) + 
		e.yxy*sdf(pos + e.yxy*eps) + 
		e.xxx*sdf(pos + e.xxx*eps)
	);
}

// Based on http://iquilezles.org/www/articles/raymarchingdf/raymarchingdf.htm
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 iUV = (2.0 * gl_FragCoord.xy - iResolution.xy) / iResolution.y;
	lpscale = floor(density)/M_PI;

	 // camera movement	
	float an = 0.04*iTime;
	vec3 ro = vec3(1.0*cos(an), camera_y, 1.0*sin(an));
	vec3 ta = vec3( 0.0, 0.0, 0.0 );
	// camera matrix
	vec3 ww = normalize(ta - ro);
	vec3 uu = normalize(cross(ww,vec3(0.0,1.0,0.0)));
	vec3 vv = normalize(cross(uu,ww));

	vec3 tot = vec3(0.0);

	#if AA>1
	for(int m=0; m<AA; m++)
	for(int n=0; n<AA; n++)
	{
		// pixel coordinates
		vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
		vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
		#else    
		vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
		#endif

		// create view ray
		vec3 rd = normalize(p.x*uu + p.y*vv + 3.5*ww); // fov

		// raymarch
		const float tmax = 10.0;
		float t = 0.0;
		for( int i=0; i<256; i++ )
		{
			vec3 pos = ro + t*rd;
			float h = sdf(pos);
			if( h<0.0001 || t>tmax ) break;
			t += h;
		}

		// shading/lighting	
		vec3 col = vec3(0.0);
		if( t<tmax )
		{
			vec3 pos = ro + t*rd;
			vec3 nor = calcNormal(pos);
			float dif = clamp( dot(nor,vec3(0.57703)), 0.0, 1.0 );
			float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
			col = color(pos)*amb + color(pos)*dif;
		}

		// gamma        
		col = sqrt( col );
		tot += col;
	#if AA>1
	}
	tot /= float(AA*AA);
	#endif

	fragColor = vec4(tot, 1.0);
}

