       pos *= 0.5;
        xor *= 0.5;
    }
    return xor;
}

vec3 GetMaterial(vec3 p,  vec3 nor, vec3 ro,  vec3 rd, int id_color)
{  	vec3 colobj; 
    if (id_color==1) {return vec3(1.0);}
    if (id_color==36) {return  vec3(0.8549, 0.8549, 0.8549); }

