{    
    float sphereDist = length(point - SPHERE_CENTER.xyz) - SPHERE_CENTER.w;
    float planeDist = point.y;


    return min(planeDist, sphereDist);
}

float RayMarch(vec3 rayOrigin, vec3 rayDelta, out bool hitSphere)
{
    float dist = 0.0;

    for(int i = 0; i < MAX_STEPS; ++i)
    {
        vec3 p = rayOrigin + rayDelta * dist;
        float dS = calculateDistance(p, hitSphere);
        dist += dS;

        if(dist > MAX_DIST || dist < SURFACE_ERROR) break;
    }

    return dist;
}

float RayMarch(vec3 rayOrigin, vec3 rayDelta)
{
    float dist = 0.0;

    for(int i = 0; i < MAX_STEPS; ++i)
    {
        vec3 p = rayOrigin + rayDelta * dist;
        float dS = calculateDistance(p);
        dist += dS;

        if(dist > MAX_DIST || dist < SURFACE_ERROR) break;
    }

    return dist;
}

vec3 calculateNormal(vec3 p) {
	float d = calculateDistance(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        calculateDistance(p-e.xyy),
        calculateDistance(p-e.yxy),
        calculateDistance(p-e.yyx));

    return normalize(n);
}

float calculateDiffuseLighting(vec3 point)
{
    vec3 lightPos = LIGHT.xyz;
    lightPos.xz += vec2(sin(iTime), cos(iTime));
    vec3 l = normalize(lightPos - point);
    vec3 n = calculateNormal(point);
    float d = length(lightPos - point);


    float diff = clamp(dot(l, n), 0.0, 1.0) * LIGHT.w / d / d;

    float ray = RayMarch(point + n * 2.0 * SURFACE_ERROR, l);
    if(ray< length(point - lightPos)) diff *= 0.1;

    return diff;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y ;

    bool isSphere = false;
    float dist = RayMarch(RAY_ORIGIN, RAY_DIRECTION, isSphere);

    vec3 point = RAY_ORIGIN + RAY_DIRECTION * dist;

    float diff = calculateDiffuseLighting(point);

    calculateDistance(RAY_ORIGIN);

    vec3 col = vec3(diff);
    if(isSphere)
        col *= vec3(0.5, 0.3, 0.7);
    else
        col *= vec3(0.5, 0.3, 0.1);


    fragColor = vec4(col,1.0);
}

