#define PI 3.14159
#define SPEED 0.4
#define FORWARD -normalize((iMouse.xy / iResolution.xy) * 2.0 - 1.0)

// debug thingy
// #define RENDERSPIRAL

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalize uv [-1.0 - 1.0]
    vec2 uv = (fragCoord/iResolution.xy) * 2.0 - 1.0;
    vec2 normalized = normalize(uv);
    float uvDot = dot(normalized, FORWARD);
    float uvDot90 = dot(vec2(normalized.y, -normalized.x), FORWARD); // rotated 90deg
    // get the angle between this pixel and the slice
    float angle = acos(uvDot) / PI;
    // lefthand side
    float angle1 = (1.0 - angle) * 0.5;
    // righthand side
    float angle2 = angle * 0.5 + 0.5;
    // mix between the two based on which side of the spiral we're on
    float spiral = mix(angle1, angle2, floor(uvDot90) + 1.0);

    // do the wipe
    float t = fract(iTime * SPEED + 0.3); // adding a small amt just so the preview looks nicer
    vec4 tex1 = texture(iChannel0, fragCoord/iResolution.xy);
    vec4 tex2 = texture(iChannel1, fragCoord/iResolution.xy);

    #ifdef RENDERSPIRAL
    fragColor = vec4(vec3(spiral), 1.0);
    #else
    fragColor = mix(tex1, tex2, floor(t - spiral) + 1.0);
    #endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 q = uv - vec2(0.5,0.5);

    // Time varying pixel color
    vec3 col = vec3(1.0-(q.x*2.0),1.0-(q.y*2.0),1.0);

    float r = atan(q.y,q.x);

    float r_normal = (r+3.1415)/6.283;

    float len = length(q);

    col *= smoothstep(0.9,0.95,abs(cos(r*4.0+len*5.0+iTime))*abs(cos(len*iMouse.y)))+smoothstep(0.2,0.7,(cos((uv.y-uv.x)*6.0+iTime*2.0))+1.0)*0.2;

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float width = 800.0;float height = 450.0;
    float as = width/height;

    // Time varying pixel color
    vec3 col_1 = vec3(1.0,1.0,1.0);

    uv.x = uv.x*as-(1.0-(1.0/as));

    vec2 ori = vec2(uv.x+.1*(cos(iTime*5.0)+0.2*cos(iTime*25.0)),uv.y+.1*sin(iTime*5.0));
    //ori = vec2(uv.x,uv.y);
    //ori = vec2(uv.x-iMouse.x/iResolution.x+0.5,uv.y-iMouse.y/iResolution.y+0.5);

    float di = length(ori-vec2(.5,.5));
    float r = atan(ori.y-0.5,ori.x-0.5);

    float circle = smoothstep(.4,.5,.005/abs(di-cos(r*10.0)*.4));

    float limen = .99;

    float atom = 1.0;

    atom *= circle * smoothstep(limen,limen+.01,abs(cos(r*15.0/2.0+3.14592*iTime*1.0)));

    atom += smoothstep(0.96,0.965,1.0-di);

    //atom += smoothstep(.4,.5,);

    atom += .025/abs(di);

    atom += .001/abs(di-abs(cos(iTime))*.25) * step(-0.2,cos(iTime*160.0)+cos(iTime*139.0));
    atom += .001/abs(di-abs(cos(iTime*1.3+0.6))*.25)
    * step(1.0,cos(iTime*160.0)+cos(iTime*139.0))
    * step(0.8,abs(cos(r*5.0+iTime*5.0)));
    atom = clamp(atom,.0,1.0);
    float tri_d = .15;
    float tri_r = iTime;
    float triangle = .001/abs((ori.y-0.5-tri_d*sin(tri_r))-tan(tri_r-3.141592/6.0)*(ori.x-0.5-tri_d*cos(tri_r)));
    tri_r += (2.0/3.0)*3.141;
    triangle += .001/abs((ori.y-0.5-tri_d*sin(tri_r))-tan(tri_r-3.141592/6.0)*(ori.x-0.5-tri_d*cos(tri_r)));
    tri_r += (2.0/3.0)*3.141;
    triangle += .001/abs((ori.y-0.5-tri_d*sin(tri_r))-tan(tri_r-3.141592/6.0)*(ori.x-0.5-tri_d*cos(tri_r)));
    triangle = clamp(triangle,.0,1.0);
    col_1 *= atom;
    col_1 += triangle;

    col_1 *= vec3(1.0-di,.2+di,.2+di);
    // Output to screen
    fragColor = vec4(col_1,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = 800.0/450.0;

    // Time varying pixel color
    vec3 col = vec3(1.0,1.0,1.0);
    col.x *= clamp(0.0,1.0,fract(5.0*(uv.y-(uv.x+iTime*0.001)*pow(cos(iTime*0.1)*0.4,2.0))-iTime*0.3)-0.2);
    col.y *= clamp(0.0,1.0,fract((uv.y+0.1-uv.x*(pow(cos(iTime*0.1)*0.7,2.0)-0.2))/aspect*10.0)-0.2);

    col = col*0.3+vec3(0.3,0.7,0.7);

    col.y += smoothstep(0.99,0.998,fract((cos(uv.y*(30.0)-(uv.x+iTime*0.1)*300.0-2.0)+1.0)/2.0))*0.3;

    if(abs((uv.y-0.5)*15.0-cos((uv.x+iTime*0.2)*5.0))<0.03) col += 0.1;
    if(abs((uv.y-0.5)*15.0+cos((uv.x+iTime*0.2+4.0)*5.0))<0.03) col += 0.1;


    // Output to screen
    fragColor = vec4(col,1.0);
}

