float sphereSDF(vec3 p, vec3 c, float r) {
    return sqrt((p.x - c.x) * (p.x - c.x) + (p.y - c.y) * (p.y - c.y) + (p.z - c.z) * (p.z - c.z)) - r;
}

// https://iquilezles.org/articles/distfunctions/
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.yz)-t.x,p.x);
  return length(q)-t.y;
}

float sdOctahedron( vec3 p, float s)
{
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

float groundSDF(vec3 p) {
    return abs(p.y+5.);
    //return length(p.xy) + 0.5;
}

vec3 groundDiffuse(vec3 p) {
    //return vec3(p.z - floor(p.z));
    return vec3(sin(10.*p.z));
}

float sceneSDFStart(vec3 p) {
    //return sdTorus(p, vec2(1.5, 0.5));
    return sdOctahedron(p, 1.5);
}

float sceneSDFEnd(vec3 p) {
    return sphereSDF(p, vec3(0.0, 0.0, 0.0), 1.5);
}

float sceneSDF(vec3 p) {
    //return min(sphereSDF(p, vec3(0.0, 0.0, 0.0), 1.5), groundSDF(p));
    //return sdTorus(p, vec2(1.5, 0.5));
    float weight = sin(iTime) * 0.5 + 0.8;

    float blend = sceneSDFStart(p) * (weight - 0.2) + (0.8 - weight) * sceneSDFEnd(p);

    return min(blend, groundSDF(p));
}

void sceneMaterial(vec3 p, out vec3 diffuseColor) {
    if (groundSDF(p) <= RAYMARCH_SURF_DIST) {
        // ground color
        diffuseColor = groundDiffuse(p);
    }
}

float rayMarch(vec3 rayO, vec3 rayD, out float dist) {
    vec3 curIsect = rayO;
    rayD = normalize(rayD);
    float t = 0.0f;
    for (int i = 0; i < RAYMARCH_MAX_STEPS; i++) {
        dist = sceneSDF(curIsect);
        if (dist < RAYMARCH_SURF_DIST || dist > RAYMARCH_MAX_DIST) {
            break;
        }

        t += dist;
        curIsect = rayO + t * rayD;
    }
    return t;
}

// grad f(x,y,z) = (df/dx, df/dy, df/dz)
// normal at (x_0, y_0, z_0) is normalized grad SDF(x,y,z)
//     evaluated at (x_0, y_0, z_0)
vec3 sceneNormal(vec3 p) {
    float pSDF = sceneSDF(p);
    vec2 eps = vec2(1e-3, 0);
    vec3 grad = pSDF - vec3(
        sceneSDF(p - eps.xyy),    // f(x_0 - eps.x, y_0, z_0)
        sceneSDF(p - eps.yxy),    // f(x_0, y_0 - eps.x, z_0)
        sceneSDF(p - eps.yyx)     // f(x_0, y_0, z_0 - eps.x)
    );

    // normalize does the rest
    return normalize(grad);
}

vec3 shadeScene(vec3 isect) {
    // contrib from mainLight - point light
    vec3 lightPos = vec3(5.0, 5.0, 5.0);
    //vec3 lightPos = vec3(5.0f * cos(2.0 * iTime), 0.0, 5.0 * sin(2.0 *iTime));

    // test if the light can reach our ball
    vec3 light2Isect = normalize(isect - lightPos);
    float rayDist, lightT = rayMarch(lightPos, light2Isect, rayDist);
    float visibility = 0.0f;
    //float visibility = 1.0f;
    if (length(lightPos + lightT * light2Isect - isect) < 10. * RAYMARCH_SURF_DIST) {
        // shall contribute light
        visibility = 1.0f;
    }

    vec3 lightColor = vec3(0.5, 0.5, 0.5);
    vec3 lightL = lightColor * 5.5f;

    vec3 diffuseColor = vec3(1.0f);
    sceneMaterial(isect, diffuseColor);

    vec3 isectN = sceneNormal(isect);
    vec3 lightContrib = \
        visibility * diffuseColor * clamp(dot(isectN, normalize(lightPos - isect)), 0., 1.) * lightL / length(isect - lightPos);
    vec3 ambientContrib = diffuseColor * 0.1;
    return lightContrib + ambientContrib;
}

vec2 toSphericalCoord(vec3 v) {
  /* -PI ~ PI */
  float theta = atan(v.y, v.x);

  /* -PI/2 ~ PI/2 */
  float phi = asin(clamp(v.z, -1.f, 1.f));
  return vec2(theta, phi);
}

