void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouseUV = iMouse.xy / iResolution.xy;
    float aspectRatio = iResolution.x / iResolution.y;

    // Compute camera properties
    vec3 camPos = vec3(10.0, 0.0, 0.0);
    vec3 camLookat = vec3(-1.0, -3.0 + 6.0 * mouseUV.y, -3.0 + 6.0 *mouseUV.x);
    vec3 camUp = vec3(0.0, 0.0, 1.0);
    vec3 camZ = normalize(camLookat - camPos);            // forward
    vec3 camX = normalize(cross(camZ, normalize(camUp)));  // right
    vec3 camY = cross(camX, camZ);                        // up
    float camVertFOV = 120.0f * (PI / 180.0f) / 2.;       // 120 degree
    vec3 camScrC = camPos + camZ;
    vec3 camScrP = camScrC + camY * (uv.x -0.5) * tan(camVertFOV) + camX * (uv.y-0.5) * tan(camVertFOV) / aspectRatio;

    vec3 rayD = normalize(camScrP - camPos);
    vec3 rayO = camPos;
    float distClosest, tClosest = rayMarch(rayO, rayD, distClosest);


    //vec3 outColor = vec3(distClosest );
    //vec3 outColor = dist > 1. ? vec3(0.0) : vec3(1.0);

    vec3 outColor;
    if (distClosest > 10.0f * RAYMARCH_SURF_DIST) {
        outColor = shadeSkybox(rayD);
    } else {
        outColor = shadeScene(rayO + tClosest * rayD);
    }

    // Output to screen
    fragColor = vec4(outColor, 1.0);
}

