#define GRID_THICKNESS 1.0
#define GRID_SCALE 80.0

#define GRID_COLOR vec4(0.8, 0.8, 0.8, 1.0)
#define BACKGROUND_COLOR vec4(0.1, 0.1, 0.1, 1.0)

#define GRID_WOBBLE 1


// Calculating grid bsed on uv coordinates
float calculateGrid(in vec2 uv, float thickness)
{
    float gridX = float(fract(uv.x) <= thickness);
    float gridY = float(fract(uv.y) <= thickness);

    return max(gridX, gridY);
}

// Calculate simple wobble amount for demonstration
#ifdef GRID_WOBBLE
    vec2 calculateWobble(float multiplier, float freq, float scale)
    {
        return multiplier * vec2(sin(iTime * freq)) / scale;
    }
#endif

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 screenCoord = fragCoord / GRID_SCALE;

    // Apply wobble if needed
    #ifdef GRID_WOBBLE
        screenCoord += calculateWobble(2000.0, 0.1, GRID_SCALE);
    #endif

    // Scale the thickness appropriately to match the grid coordinate system scale
    float scaledThickness = GRID_THICKNESS / GRID_SCALE;
    float grid = calculateGrid(screenCoord, scaledThickness);

    // Applying different colors for grid and background
    vec4 finalColor = mix(BACKGROUND_COLOR, GRID_COLOR, grid);

    // Output to screen
    fragColor = finalColor;
}

