#define pi 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 ms = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;

    // Change me!
    float sc = 1.;   
    uv *= sc;

    // Line smoothstep
    float s;   

    // Mouse parameters
    float mx = 8. * (ms.x + 0.5);
    float my = ceil(5. * (ms.y + 0.5));

    float n = 50.;
    for (float i = 0.; i < n; i++) {
        float t = 0.2 * iTime + 2. * pi * i / n; 

        // Modified: y = sin(t) + uv.x * cos(t)
        float y = sin(mx * uv.x + t) -  uv.x * cos(t) 
                + 0.5 * fract( pi * 0.2 * iTime + my * (uv.x + i / n));

        // Include new line/wave
        float k = sc / iResolution.y;
        s = max(s, smoothstep(-k, k, -abs(uv.y - 0.25 * y) + k));
    }

    // Background + color
    s += exp(-4. * length(uv));
    vec3 e = vec3(0.5);
    vec3 col = pal(0.5 * s + 0.85, e, e, e, 0.5 * vec3(0,1,2)/3.);

    fragColor = vec4(col,1.0);
}

