#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define sabs(x) sqrt(x*x+1e-2)
//#define sabs(x, k) sqrt(x*x+k)-0.1

#define Rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float cc(float a, float b) {
    float f = thc(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

float cs(float a, float b) {
    float f = ths(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float mlength(vec3 uv) {
    return max(max(abs(uv.x), abs(uv.y)), abs(uv.z));
}

float smin(float a, float b) {
    float k = 0.12;
    float h = clamp(0.5 + 0.5 * (b-a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

image_code:
// CC0: Pretty sweet colors
//  I watched a YT (https://www.youtube.com/watch?v=pG0t19bEYJw), didn't remember anything except I thought 
//  the colors and shapes were pretty sweet around 0:09 in the video. So improvised a shader around it.
//  Unfortunately in chromium (Chrome, Edge etc) the colors for me looks dull and boring. Hopefully it's ok for you.
//  In FF it looks right though.

#define RESOLUTION  iResolution
#define TIME        iTime
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
float sRGB(float t) { return mix(1.055*pow(t, 1./2.4) - 0.055, 12.92*t, step(t, 0.0031308)); }
// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(in vec3 c) { return vec3 (sRGB(c.x), sRGB(c.y), sRGB(c.z)); }


float plane(vec2 p, vec3 pl) {
  return dot(p, pl.xy) + pl.z;
}

vec2 toSmith(vec2 p)  {
  // z = (p + 1)/(-p + 1)
  // (x,y) = ((1+x)*(1-x)-y*y,2y)/((1-x)*(1-x) + y*y)
  float d = (1.0 - p.x)*(1.0 - p.x) + p.y*p.y;
  float x = (1.0 + p.x)*(1.0 - p.x) - p.y*p.y;
  float y = 2.0*p.y;
  return vec2(x,y)/d;
}

vec2 fromSmith(vec2 p)  {
  // z = (p - 1)/(p + 1)
  // (x,y) = ((x+1)*(x-1)+y*y,2y)/((x+1)*(x+1) + y*y)
  float d = (p.x + 1.0)*(p.x + 1.0) + p.y*p.y;
  float x = (p.x + 1.0)*(p.x - 1.0) + p.y*p.y;
  float y = 2.0*p.y;
  return vec2(x,y)/d;
}

const vec3[] bcols = vec3[](
    HSV2RGB(vec3(243.0/360.0,0.95,0.07))
  , HSV2RGB(vec3(246.0/360.0,0.89,0.28))
  , HSV2RGB(vec3(244.0/360.0,0.80,0.23))
  , HSV2RGB(vec3(263.0/360.0,0.84,0.27))
  , HSV2RGB(vec3(277.0/360.0,0.86,0.26))
  , HSV2RGB(vec3(337.0/360.0,0.98,0.61))
  , HSV2RGB(vec3(348.0/360.0,0.99,0.83))
  , HSV2RGB(vec3(357.0/360.0,0.93,0.85))
  );
const int noOfBCols = bcols.length();

vec2 transform(vec2 p, float i) {
  float tm = TIME*0.2;
  float ii = i/float(noOfBCols);
  float f =sin(3.0*p.y+2.1*i+tm);
  vec2 sp = toSmith(p);
//  sp.y -= 0.1*i+0.9*sin(+0.1*i);
  sp *= ROT(0.1*i+tm);
  sp *= ROT(mix(0.0, 0.2, ii)*f);
//  p.x += 0.08*f;
  p = fromSmith(sp);;
  return p;
}

float df(vec2 p, float i) {
  return plane(p, vec3(normalize(-vec2(1.0, 1.0)), 0.2*i-0.3));
}

vec3 effect(vec2 p, vec2 np) {
  float aaa = 2.0/RESOLUTION.y;
  vec3 col = bcols[0];

  for (int i = 1; i < noOfBCols; ++i) {
    float ii = float(i);
    vec2 pp   = transform(p, ii);
    vec2 npp  = transform(np, ii);
    float aa  = distance(pp, npp)*sqrt(0.5);
    float d = df(pp, ii);
    col = mix(col, col*0.33, exp(-max(10.0*d*aaa/aa, 0.0)));
    col = mix(col, bcols[i], smoothstep(aa, -aa, d));
  }


  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q  = fragCoord/RESOLUTION.xy;
  vec2 p  = -1.0 + 2.0*q;
  p.x     *= RESOLUTION.x/RESOLUTION.y;
  vec2 np = p + 2.0/RESOLUTION.y;

  vec3 col = effect(p, np);
  col = sRGB(col);
  fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv =  (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    for(float i = 1.0; i < 8.0; i++){
    uv.y += i * 0.1 / i * 
      sin(uv.x * i * i + iTime * 0.5) * sin(uv.y * i * i + iTime * 0.5);
  }

   vec3 col;
   col.r  = uv.y - 0.1;
   col.g = uv.y + 0.3;
   col.b = uv.y + 0.95;

    fragColor = vec4(col,1.0);
}

float sdfArc(float sa, float ea, float r, vec2 uv){
    float tau = 6.28318530718;
    //find coterminal angles
    sa = mod(mod(radians(sa),tau) + tau, tau);
    ea = mod(mod(radians(ea),tau) + tau, tau);
    float a = mod(mod(atan(uv.y,uv.x),tau) + tau, tau);
    //https://stackoverflow.com/questions/12234574/calculating-if-an-angle-is-between-two-angles
    if ((sa <= a && a <= ea && sa < ea) || ((sa <= a || a <= ea) && sa >= ea)){
        return abs(r - length(uv));
    }
    //get closest endpoint
    vec2 p1 = vec2(cos(sa), sin(sa))*r;
    vec2 p2 = vec2(cos(ea), sin(ea))*r;
    return min(distance(p1, uv), distance(p2, uv));
}

vec3 colorSdf(float d){//from https://www.shadertoy.com/view/tlSGzG
    vec3 col = vec3(1.0) - sign(d*2.0)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d*2.0));
	col *= 0.8 + 0.2*cos(140.0*d*2.0);
	return mix(col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d*2.0)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    vec3 col = colorSdf(sdfArc(iTime*20.0, iTime*40.0, 0.3, uv - vec2(0.8, 0.5)));

    fragColor = vec4(col,1.0);
}

//For the behavior of blob shape, see 'blobRadius()' and 'randBlob()'.
//For the behavior of blob color, see 'colorBlob()'.
//For the behavior of the lights, see 'genLighting()', 'getWorldLighting()', and 'getBlobLighting()'.

///////////////////////
//    Blob shape     //
///////////////////////

//The size of blobs relative to their cell.
#define BLOB_AVERAGE_SIZE_PER_CELL_SIZE 0.4


//A distorted circle.
struct Blob
{
    float initialRadius;

    Ripple fastRhythm;
    float fastRhythmWeight;

    Ripple slowRhythm1, slowRhythm2;
    float slowRhythmWeight;

    vec2 center;
    float scale; //A bit redundant, but makes the shape much easier to manipulate
};

//Gets the oscillating effect from a blob's slow rhythms.
float blobSlowRhythm(Blob blob, float thetaT)
{
    return blob.slowRhythmWeight * (
        ripple(blob.slowRhythm1, thetaT, iTime) +
        ripple(blob.slowRhythm2, thetaT + 0.4128901, iTime) //Arbitrary offset to keep the rhythms unaligned
    );
}
//Gets the min/max of a blob's slow rhythm output.
vec2 blobSlowRhythmRange(Blob blob)
{
    return blob.slowRhythmWeight *
              (rippleRange(blob.slowRhythm1) +
               rippleRange(blob.slowRhythm2));
}

//Gets the oscillating effect from a blob's fast rhythms.
float blobFastRhythm(Blob blob, float thetaT)
{
    return blob.fastRhythmWeight *
           ripple(blob.fastRhythm, thetaT, iTime);
}
//Gets the min/max of a blob's fast rhythm output.
vec2 blobFastRhythmRange(Blob blob)
{
    return blob.fastRhythmWeight * rippleRange(blob.fastRhythm);
}

//Computes the blob's radius at a specific angle, determined by a direction vector.
float blobRadius(Blob blob, vec2 offset)
{
    float thetaT = angleT(offset);
    return blob.scale * (blob.initialRadius +
                         blobSlowRhythm(blob, thetaT) +
                         blobFastRhythm(blob, thetaT));
}

//Returns 1) the signed-distance to this blob,
//        2) a 0-or-1 value indicating whether you're inside the blob.
vec2 blobCollision(Blob blob, vec2 pos)
{
    vec2 relativePos = pos - blob.center;
    float dist = length(relativePos),
          radius = blobRadius(blob, relativePos);

    float signedDist = dist - radius;
    float mask = 1.0 - max(0.0, sign(signedDist));
    return vec2(signedDist, mask);
}

//Generates a randomized blob, given some uniform-random 0-1 values,
//    and the range of possible values.
Blob randBlob(vec3 rngSeeds,
              vec2 rangeInitialRadius,
              vec2 rangeFastPolyrhythm, vec2 rangeFastAmplitude, vec2 rangeFastSpikes,
              vec2 rangeSlow1Speed, vec2 rangeSlow1Spikes,
              vec2 rangeSlow2Speed, vec2 rangeSlow2Spikes,
              vec2 rangeSlowAmplitude,
              vec2 cellCenter, float cellSize)
{
    float halfCellSize = cellSize * 0.5;

    //Generate some random signals.
    vec4 rng1 = hashTo4(rngSeeds * 7777.7777),
         rng2 = hashTo4((rngSeeds.zxy + rng1.wxz) * 5555.5555),
         rng3 = hashTo4((rngSeeds.yzx + rng1.yzw) * 8989.9898),
         rng4 = hashTo4(rngSeeds.xzy * 5555.5555);

    float polyrhythm = floor(mix(rangeFastPolyrhythm.x, rangeFastPolyrhythm.y, rng1.r));
    float tempo = BLOB_TEMPO * polyrhythm;

    //The blob will wander around its center position.
    //The wandering is based on sampling from a noise texture
    //    (so we can take non-uniform sources like blue noise).
    //Depends on linear sampling to get a smooth wandering.
    float wanderSpeed = mix(0.001, 0.02, rng4.x),
          wanderLookupAngle = PI2 * iTime * wanderSpeed;
    vec2 wanderRadiusTexels = 10.0 / iChannelResolution[1].xy,
         wanderLookupDir = vec2(cos(wanderLookupAngle), sin(wanderLookupAngle)),
         wanderLookup = rng4.yz + (wanderRadiusTexels * wanderLookupDir);
    vec4 wanderNoise = texture(iChannel1, wanderLookup);
    vec2 wanderPosOffset = halfCellSize *
                           mix(vec2(-0.5), vec2(0.5),
                               smoothstep(0.0, 1.0, wanderNoise.xy));

    vec2 blobCenter = cellCenter + wanderPosOffset +
                      (halfCellSize * mix(vec2(-0.5), vec2(0.5), rng3.gb));

    return Blob(
        mix(rangeInitialRadius.x, rangeInitialRadius.y, rng1.g),
        //Fast rhythm:
        Ripple(mix(rangeFastAmplitude.x, rangeFastAmplitude.y, rng1.b),
               tempo,
               int(mix(rangeFastSpikes.x, rangeFastSpikes.y, rng1.a)),
               true),
        1.0,
        //Slow rhythms:
        Ripple(1.0,
               mix(rangeSlow1Speed.x, rangeSlow1Speed.y, rng2.r),
               int(mix(rangeSlow1Spikes.x, rangeSlow1Spikes.y, rng2.g)),
               false),
        Ripple(1.0,
               mix(rangeSlow2Speed.x, rangeSlow2Speed.y, rng2.b),
               int(mix(rangeSlow2Spikes.x, rangeSlow2Spikes.y, rng2.a)),
               false),
        mix(rangeSlowAmplitude.x, rangeSlowAmplitude.y, rng3.r),
        //Transform:
        blobCenter,
        BLOB_AVERAGE_SIZE_PER_CELL_SIZE * cellSize
    );
}



////////////////////////
//    Dance Floor     //
////////////////////////

//A "dance floor" is split into a grid,
//    and N blobs are generated within each grid.
//The blobs wander back and forth by using a position offset,
//    taken by linear-sampling a noise texture.

//The range/probability of blobs per-cell are kept constant to make the code simpler (and faster).
//The number of blobs for a specific cell is computed as a random value within this range, floored to integer.
#define BLOBS_COUNT_MIN 0.2   /* Chance of 0 blobs in a cell */
#define BLOBS_COUNT_MAX 1.5
#define N_MAX_BLOBS (int(BLOBS_COUNT_MAX))

struct DanceFloor
{
    float cellSize;
    float seed;

    //Each dance floor gets a pseudo-random offset to help hide the grid alignment.
    vec2 offset;
};

//Generates a dance floor with a pseudo-random offset.
DanceFloor makeDanceFloor(float cellSize, float seed)
{
    return DanceFloor(cellSize, seed,
                      cellSize * fract(seed * vec2(9999.2374, 4444.98725)));
}

vec2 danceFloorCellIndex(DanceFloor df, vec2 pos) { return floor((pos - df.offset) / df.cellSize); }
vec2 danceFloorCellPos(DanceFloor df, vec2 cellIdx) { return ((cellIdx + 0.5) * df.cellSize) + df.offset; }

//Generates the blobs for a specific dance floor cell.
//The cell index should be integers, but is passed as floats for simplicity.
void blobsInCell(DanceFloor danceFloor, vec2 cellIndex,
                 out int nBlobs, out Blob blobs[N_MAX_BLOBS])
{
    vec3 noiseInput = vec3((cellIndex * 2.952832938), danceFloor.seed);

    nBlobs = int(mix(BLOBS_COUNT_MIN, BLOBS_COUNT_MAX, hashTo1(noiseInput * 888.888)));

    vec2 cellCenter = danceFloorCellPos(danceFloor, cellIndex);
    for (int i = 0; i < min(N_MAX_BLOBS, nBlobs); ++i) //Explicitly limit the iterations to a hard-coded max,
    {                                                  //    in case that helps the compiler optimize/unroll
        blobs[i] = randBlob(
            hashTo3(vec3(hashTo1(noiseInput.xy * 9191.1919),
                         noiseInput.z * 1919.9191,
                         float(i * 3000))),

            //Radius:
            vec2(0.87, 1.2),

            //Fast:
            vec2(1.0, 3.99999), vec2(0.02, 0.2), vec2(2.0, 6.75),

            //Slow1:
            vec2(0.175, 0.25), vec2(1.0, 4.5),
            //Slow2:
            vec2(0.3, 0.4), vec2(1.0, 4.15),
            //Slow general:
            vec2(0.2, 0.4),

            //Transform data:
            cellCenter, danceFloor.cellSize
        );
    }
}

//Describes a specific point inside a blob.
struct BlobHit
{
    vec2 cellIndex;
    float blobIndex;

    float blobRadius;

    //The point, relative to the blob's center.
    vec2 posOffset;
    //The point's distance from the blob center.
    float dist;
};

//Finds which blob in the dance floor a given position is touching.
//Returns whether any such blob was found, and outputs data about the blob into an 'out' variable.
bool danceFloorBlobAt(DanceFloor danceFloor, vec2 pos,
                      out BlobHit hitData, out Blob blobData, out int blobIdx)
{
    vec2 cellIndex = danceFloorCellIndex(danceFloor, pos);
    cellIndex = floor(cellIndex);

    //Check the neighboring cells as well as this one, to handle blobs near the boundary.
    for (int x = -1; x <= 1; ++x)
    {
        for (int y = -1; y <= 1; ++y)
        {
            vec2 neighborCellIndex = cellIndex + vec2(x, y);

            //Get the blobs in this cell.
            int nBlobs;
            Blob blobs[N_MAX_BLOBS];
            blobsInCell(danceFloor, neighborCellIndex, nBlobs, blobs);

            //Check for collision.
            for (int i = 0; i < nBlobs; ++i) {
                vec2 toPos = pos - blobs[i].center;
                float radius = blobRadius(blobs[i], toPos);
                float dist = length(toPos);
                if (dist <= radius)
                {
                    blobIdx = i;
                    blobData = blobs[i];
                    hitData = BlobHit(neighborCellIndex, float(i),
                                      radius, toPos, dist);
                    return true;
                }
            }
        }
    }

    return false;
}


//////////////////////////
//    World lighting    //
//////////////////////////

//Computes the global "ambient" light brightness, from 0 to 1.
float getGlobalLight(float time)
{
    //Define a bunch of cycling amplitudes.
    //Mix them together in different ways (max, min, mul, add),
    //    and then smoothly interpolate between the ways.

    float modeT = OSCILLATE(0.0, 1.0, time / 6.0);

    Ripple cycles[] = Ripple[](
        Ripple(1.0, 0.7, 1, false),
        Ripple(0.7, 0.1, 1, false),
        Ripple(1.0, 0.6, 1, false),
        Ripple(1.0, 0.3, 1, false)
    );

    float rMin = 99999.999,
          rMax = -99999.999,
          rSum = 0.0,
          rProd = 1.0;
    for (int i = 0; i < cycles.length(); ++i)
    {
        float r = ripple(cycles[i], iTime, iTime);
        rMin = min(rMin, r);
        rMax = max(rMax, r);
        rSum += r;
        rProd *= r;
    }

    if (modeT < 0.33333)
        return SATURATE(mix(rProd, rMin, INV_LERP(0.0, 0.33333, modeT)));
    else if (modeT < 0.66667)
        return SATURATE(mix(rMin, rSum, INV_LERP(0.33333, 0.66667, modeT)));
    else
        return SATURATE(mix(rSum, rMax, INV_LERP(0.66667, 1.0, modeT)));
}


//////////////////////////
//    Floor lighting    //
//////////////////////////


const vec3[] LIGHT_COLORS = vec3[](
    vec3(1.0, 0.0, 0.0),
    vec3(0.0, 1.0, 0.0),
    vec3(1.0, 1.0, 0.0),
    vec3(1.0, 0.0, 0.5),
    vec3(0.0, 0.3, 1.0)
);

#define STREAK_THICKNESS 0.012

//Party lighting for one dance floor.
struct FloorLighting
{
    vec3 dir;
    vec2 offset;
    int nStreaks;
    float streaksAngleSpacing;
    vec4 streakColor;
};


//Computes randomized lighting for a dance floor.
FloorLighting genLighting(DanceFloor df, float time)
{
    //Change the lighting at regular intervals, based on tempo.
    float lightingIdx = floor((time * BLOB_TEMPO * 0.5) +
                              (0.2 * hashTo1(df.seed * 12.439782)));

    vec2 seeds = vec2(df.seed, lightingIdx);
    vec4 rand1 = hashTo4(seeds * 4565.6545);

    float lightingAngle = PI2 * sign(rand1.x - 0.5) *
                          mix(0.0, PI2/4.0, rand1.x);

    return FloorLighting(
        vec3(cos(lightingAngle), sin(lightingAngle), -1.0),
        mix(vec2(-0.5), vec2(0.5), hashTo2(rand1.xyz +
                                           (rand1.w * df.seed) +
                                           3453.34534)),
        int(mix(0.0, 5.99999, rand1.y)),
        PI2 * mix(0.01, 0.031, rand1.z),
        vec4(RAND_IN_ARRAY(LIGHT_COLORS, rand1.w),
             mix(0.5, 1.0, hashTo1((time * (df.seed + 0.1)) * 8979.7899)))
    );
}

//Computes the influence of floor lighting on a given pixel,
//    not including the light's effect on blobs.
vec3 getWorldLighting(FloorLighting lights, vec2 pos)
{
    vec3 color = vec3(0.0);
    //See if any streaks are passing through this position.
    for (int i = 0; i < lights.nStreaks; ++i)
    {
        float angleOffset = lights.streaksAngleSpacing *
                            float(i - (lights.nStreaks / 2)),
              aSin = sin(angleOffset),
              aCos = cos(angleOffset);
        vec2 streakOrigin = (lights.dir.xy * -2.5) + lights.offset,
             //Note that direction doesn't need to be normalized.
             streakDir = normalize(mat2(aCos, aSin, -aSin, aCos) * lights.dir.xy);

        float lineDist = distToLine(streakOrigin, streakDir, pos);
        if (lineDist < STREAK_THICKNESS)
        {
            //The line gets weaker at the edges, and far from the source.
            float lineStrength = (1.0 - (lineDist / STREAK_THICKNESS)) *
                                 //Note: this second term can break if
                                 //    the line is exactly vertical.
                                 (1.0 - SATURATE((pos - streakOrigin).x / streakDir.x / 6.0));
            color += lights.streakColor.rgb * lights.streakColor.a * lineStrength;
        }
    }

    return SATURATE(color);
}

//Computes the influence of floor lighting on the surface of a blob.
vec3 getBlobLighting(FloorLighting lights, Blob blob, BlobHit hit,
                     vec3 blobNormal, float globalAmbient)
{
    //Mute the party light's saturation when lighting a blob,
    //    since blobs already have a specific color palette.
    vec3 lightHSV = RGBtoHSV(lights.streakColor.rgb);
    lightHSV.y /= mix(2.0, 8.0, 1.0 - globalAmbient);
    vec3 lightColor = HSVtoRGB(lightHSV);

    //Augment the computed normal with a smooth dropoff
    //    based on the side of the blob we are on.
    float offsetNormalStrength = mix(9.0, 20.0, globalAmbient);
    vec3 normal = normalize(blobNormal +
                            vec3(hit.posOffset * offsetNormalStrength, 0.05));

    float ambient = 0.0,
          diffuse = dot(normal, -lights.dir);

    return max(ambient, diffuse) * lightColor;
}


