//Computes the color for a point in a blob.
vec3 colorBlob(DanceFloor floor, FloorLighting lighting, float worldAmbient,
               Blob blob, BlobHit hit,
               int floorIdx, int blobIdx)
{
    //Generate random numbers to color with.
    vec3 rand1 = hashTo3(5120.0 *
                           vec3(vec2(hit.cellIndex) / PI,
                                hashTo1(vec2(floorIdx, blobIdx))));

    //Pick a randomized palette.
    //Bias each dance floor towards a specific palette.
    vec3 paletteBias = hashTo3(float(floorIdx + 1) * 5434.3454);
    #define PALETTE_T(noiseChannel) applyBias(rand1.noiseChannel, \
                                              paletteBias.noiseChannel, \
                                              0.45)
    vec3 cBody = RAND_IN_ARRAY(BODY_PALETTE, PALETTE_T(x)),
         cBorder = RAND_IN_ARRAY(BORDER_PALETTE, PALETTE_T(y)),
         cDetails = RAND_IN_ARRAY(DETAILS_PALETTE, PALETTE_T(z));

    //Apply a floor-wide adjustment to the palette.
    //HSV space is more convenient for this kind of work.
    vec3 hsvBody = RGBtoHSV(cBody),
         hsvBorder = RGBtoHSV(cBorder),
         hsvDetails = RGBtoHSV(cDetails);
    vec3 colorRand3 = hashTo3(7348.3498 * float(floorIdx)),
         shiftRange = vec3(0.05, 0.1, 0.075),
         shiftAmount = mix(-shiftRange, shiftRange, colorRand3);
    hsvBody = SATURATE(hsvBody + shiftAmount);
    hsvBorder = SATURATE(hsvBorder + shiftAmount);
    hsvDetails = SATURATE(hsvDetails + shiftAmount);
    cBody = HSVtoRGB(hsvBody);
    cBorder = HSVtoRGB(hsvBorder);
    cDetails = HSVtoRGB(hsvDetails);

    //Simulate a bump-map via a scalar that tracks
    //    whether the surface is facing inward or outward.
    #define BUMP_STRENGTH 0.6
    float bumpOutward = 0.0;

    //Calculate some measurements.
    float blobAverageRadius = blob.initialRadius * blob.scale;
    float thetaT = angleT(hit.posOffset),
          edgeT = hit.dist / hit.blobRadius,
          circleT = hit.dist / blobAverageRadius;
    float slowRhythm = blobSlowRhythm(blob, thetaT),
          fastRhythm = blobFastRhythm(blob, thetaT);
    vec2 slowRhythmRange = blobSlowRhythmRange(blob),
         fastRhythmRange = blobFastRhythmRange(blob);
    float slowRhythmT = INV_LERP(slowRhythmRange.x, slowRhythmRange.y, slowRhythm),
          fastRhythmT = INV_LERP(fastRhythmRange.x, fastRhythmRange.y, fastRhythm);


    //Fade to the border color at the edge of the blob.
    float borderMask = smoothstep(0.0, 1.0, pow(edgeT, 2.0 / floor.cellSize));
    bumpOutward = SATURATE(pow(edgeT, 10.0)*5.0);

    //Add a "rim" circle.
    //The circle has an inside dropoff, and an outside dropoff.
    //It's positioned based on the blob's "initialRadius",
    //    so it's circular and not affected by the rhythms,
    //    but very small influences *are* added from rhythms to create more motion.
    float perfectAlignment = pow(fastRhythmT * slowRhythmT, 2.5);
    float innerEnd = OSCILLATE(mix(0.70, 0.60, perfectAlignment),
                               0.72,
                               fastRhythmT),
          outerStart = OSCILLATE(0.75,
                                 mix(0.77, 0.82, perfectAlignment),
                                 slowRhythmT);
    #define RIM_BUMP_STRENGTH 1.0
    float blobCircleEndRadius = outerStart * blobAverageRadius;
    //Define it as a piecewise equation with 3 parts:
    //   1. inside the circle
    //   2. on the border
    //   3. outside the circle
    float inCircleT = INV_LERP(0.0, innerEnd, circleT),
          outCircleT = INV_LERP(blobCircleEndRadius, hit.blobRadius,
                                hit.dist);
    float pastInner = step(innerEnd, circleT),
          pastOuter = step(outerStart, circleT);
    vec3 circlePhases = vec3(1.0-pastInner,
                             pastInner*(1.0-pastOuter),
                             pastOuter);
    #define INNER_SLOPE_EXP 5.0
    #define OUTER_SLOPE_EXP 0.4
    // Vary the rim strength based on the rhythms.
    vec3 rimStrengthWeights = vec3(0.1,
                                   0.85,
                                   0.05),
         rimStrengths = vec3(1.0,
                             pow(slowRhythmT, 5.0),
                             pow(fastRhythmT, 2.0));
    float rimStrength = dot(rimStrengthWeights, rimStrengths) /
                        (rimStrengthWeights.x + rimStrengthWeights.y + rimStrengthWeights.z);
    vec3 perPhaseStrengths = rimStrength * vec3(
        pow(inCircleT, INNER_SLOPE_EXP),
        1.0,
        pow(1.0 - outCircleT, OUTER_SLOPE_EXP)
    );
    vec3 perPhaseDerivative = rimStrength * RIM_BUMP_STRENGTH * vec3(
        -(1.0 / innerEnd) * INNER_SLOPE_EXP *
          pow(inCircleT, INNER_SLOPE_EXP-1.0),
        0.0,
        OUTER_SLOPE_EXP *
          pow(-outCircleT + 1.0, OUTER_SLOPE_EXP - 1.0)
    );
    float rimMask = dot(circlePhases, perPhaseStrengths),
          rimBump = clamp(dot(circlePhases, perPhaseDerivative), -1.0, 1.0);
    //If the border bump and rim bump are pushing in the same direction,
    //    average them together.
    //Otherwise, take the larger of the two by magnitude.
    bumpOutward = (sign(rimBump) != sign(bumpOutward)) ?
                      (rimBump + bumpOutward)/2.0 :
                      ((abs(rimBump) > abs(bumpOutward)) ?
                           rimBump : bumpOutward);

    //TODO: Add an angle-based effect inside the rim.

    vec3 detailedBody = mix(cBody, cDetails, rimMask);
    vec3 albedo = mix(detailedBody, cBorder, borderMask);

    //Add some lighting.
    vec2 towardsOutside = normalize(hit.posOffset);
    //TODO: Should the normal take into account the blob radius's changes? It fortunately has an analytical derivative.
    vec3 normal = normalize(vec3(towardsOutside * bumpOutward,
                                 1.0 / BUMP_STRENGTH));
    vec3 lightColor = getBlobLighting(lighting, blob, hit, normal, worldAmbient);

    return albedo * lightColor;
}


////////////////////////////
//    Post-processing     //
////////////////////////////

vec3 postProcess(vec3 color)
{
    //Disable PP until I'm finished with lighting.
    return color;

    //Increase brightness and contrast.
    vec3 colorHSV = RGBtoHSV(color);
    colorHSV.z = smoothstep(0.0, 1.0, pow(colorHSV.z, 0.68));
    color = HSVtoRGB(colorHSV);

    return color;
}


/////////////////
//    main     //
/////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float worldAmbient = getGlobalLight(iTime);

    //Set the background color to something neutral:
    fragColor.rgb = vec3(0.23, 0.21, 0.25);
    fragColor.a = 1.0;

    vec2 uv = -1.0 + (2.0 * fragCoord/iResolution.xy);
    uv.x *= iResolution.x/iResolution.y;

    //Generate the dance floors, and collect the world lights.
    #define N_FLOORS 5
    DanceFloor floors[N_FLOORS];
    FloorLighting floorLights[N_FLOORS];
    vec3 worldLightColor = vec3(0.0);
    for (int floorI = 0; floorI < N_FLOORS; ++floorI)
    {
        float floorT = float(floorI + 1) / float(N_FLOORS),
              floorSize = pow(floorT, 1.6235) * 2.0,
              floorSeed = float(floorI) * PHI * 1000.0;

        floors[floorI] = makeDanceFloor(floorSize, floorSeed);
        floorLights[floorI] = genLighting(floors[floorI], iTime);

        worldLightColor += getWorldLighting(floorLights[floorI], uv);
    }

    //Render the blob at this pixel.
    vec3 blobColor = vec3(0.0);
    for (int floorI = 0; floorI < N_FLOORS; ++floorI)
    {
        DanceFloor df = floors[floorI];

        BlobHit blobHit;
        Blob blob;
        int blobI;
        if (danceFloorBlobAt(df, uv, blobHit, blob, blobI))
        {
            FloorLighting lighting = floorLights[floorI];

            float floorT = float(floorI + 1) / float(N_FLOORS),
                  fadeT = 1.0 - (floorT - (1.0 / float(N_FLOORS)));
            blobColor = colorBlob(df, lighting, worldAmbient,
                                  blob, blobHit,
                                  floorI, blobI) *
                           pow(fadeT, 0.75);//TODO: Check out this param
            break;
        }
    }

    //Compute the final color.
    fragColor.rgb = postProcess(SATURATE(blobColor + worldLightColor));
}

