//////////////////////
//    Utilities     //
//////////////////////

#define PI 3.1415926535898
#define PI2 (2.0 * PI)

#define PHI 1.618033988


#define OSCILLATE(a, b, input) (mix(a, b, 0.5 + (0.5 * cos(PI2 * (input)))))

#define INV_LERP(a, b, x) ((x-a) / (b-a))
#define SATURATE(x) clamp(x, 0.0, 1.0)

#define RAND_IN_ARRAY(array, t) array[int(mix(0.0, float(array.length()) - 0.00001, t))]


//Gets the angle of the given vector, in the range 0-1.
float angleT(vec2 dir) { return 0.5 + (0.5 * atan(dir.y, dir.x)/PI); }

//Given a uniformly-distributed value, and another target value,
//    biases the uniform value towards the target.
//The "biasStrength" should be between 0 and 1.
float applyBias(float x, float target, float biasStrength)
{
    //Degenerative case if x=0.
    if (x == 0.0)
        return mix(x, target, biasStrength);

    //Get the "scale" of the target relative to x.
    //Multiplying x by this number would give exactly the target.
    float scale = target / x;

    //Weaken the "scale" by pushing it towards 1.0, then apply it to 'x'.
    //Make sure to respect the sign, in case 'x' or 'target' is negative.
    return x * sign(scale) * pow(abs(scale), biasStrength);
}

float distToLine(vec2 lineP, vec2 lineDir, vec2 pos)
{
    //Reference: https://en.wikipedia.org/wiki/Distance_from_a_point_to_a_line

    vec2 numerator2 = lineDir * (lineP - pos).yx;
    return abs(numerator2.x - numerator2.y) / length(lineDir);
}

//RGB<=>HSV converter, from here: https://stackoverflow.com/a/17897228
vec3 RGBtoHSV(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}
vec3 HSVtoRGB(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


////////////////
//   Ripple   //
////////////////

//An oscillating value, as a function of the angle around a circle.
struct Ripple
{
    float scale, speed;
    int nSpikes;
    bool canBeNegative;
};
float ripple(Ripple r, float angleT, float time)
{
    float inpt = (time * r.speed) +
                  (angleT * float(r.nSpikes));
    float minVal = (r.canBeNegative ? -r.scale : 0.0);
    return OSCILLATE(minVal, r.scale, inpt);
}

vec2 rippleRange(Ripple r)
{
    return vec2(r.canBeNegative ? -r.scale : 0.0,
                r.scale);
}


