void mainImage( out vec4 fragColor, in vec2 fragCoord )
{                                  
    vec3 ballCol = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;  // fetch pixel from buffer C
    float LumMask = 1. - ((ballCol.r + ballCol.g + ballCol.b) * .333333); // using luminocity to make a mask 


    vec2 uv = (fragCoord.xy -.5 * iResolution.xy) / iResolution.y; // create background
    uv *= vec2(8., 16.) * .5;
    vec2 ID = floor(uv);
    uv.x += 0.5 * mod(ID.y, 2.); // offsets every other background tile
    vec2 gv = fract(uv) - .5;    
    float bg = 0.0 + (0.05 * fract(iTime * 0.1+ log(abs(gv.x + uv.x)))) * LumMask;  


    fragColor = vec4(ballCol + bg, 1.);

}

