#define TAU 6.28318530718
#define PI 3.14159265359
#define hue(v) ( .6 + .6 * cos( 2.*PI*(v) + vec3(0,-2.*PI/3.,2.*PI/3.) ) )


// changes require "Reset time" to work (back play arrow)
#define BALLCOUNT 50
#define BALL_MIN_SIZE 20. 
#define BALL_MAX_SIZE 30. 

#define TIME_SCALE 50.
#define MAX_VELOCITY 6.
#define TRACER_INTENSITY 3.5


vec2 hash21(float p) // random stuff
{
    vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);

}
float hash11(float p)// random stuff
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
float hash12(vec2 p)// random stuff
{
    vec3 p3 = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

