void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iCoord = ivec2(fragCoord);   // fragCoord as integers      
    if (iCoord.x > BALLCOUNT || iCoord.y > 1)  // skip pixels with no saved data
        return;             
    if (iCoord.y == 0)  // if its top pixel, deal with postion and direction
    {
        vec2 pos1,dir1;        
        if(iFrame  == 0)  // initialize balls by setting start position and start velocity (direction)
        {
            vec2 n1 = hash21(iDate.w + fragCoord.x * 123.1234 );
            vec2 n2 = hash21(iDate.w + fragCoord.x * 321.123);
            float n = (n2.x + n2.y) * .5;
            pos1 = iResolution.xy * n1;   

            dir1 = normalize(n2) * (0.3 + (MAX_VELOCITY * n));
            if (n1.x + n2.x > 1.)
                dir1.x *= -1.;
            if (n1.y + n2.y > 1.)
                dir1.y *= -1.;
        }
        else  // if already initialized fetch data from buffer  
        {
            vec4 data = texelFetch(iChannel1, ivec2(iCoord.x, 0),0).rgba;
            pos1 = data.rg;
            dir1 = data.ba;           
        }        

        vec2 oldPos = pos1;

        float speed = iTimeDelta *  TIME_SCALE;  // move ball                       
        pos1 += (dir1 *speed);       

        if (pos1.x >= iResolution.x || pos1.x <= 0.)  // change direction if outside of screen.
        {
            dir1.x *=-1.;
            pos1.x = oldPos.x;
        }
        else if (pos1.y >= iResolution.y|| pos1.y <= 0.)
        {
            dir1.y *=-1.;    
            pos1.y = oldPos.y;
        }   
        pos1.x = clamp(pos1.x, 0. , iResolution.x);
        pos1.y = clamp(pos1.y, 0. , iResolution.y);
        fragColor = vec4(pos1.x, pos1.y, dir1.x, dir1.y); // save data in buffer
    }          
    else // if its second from top pixel, deal with size and hue
    {
        if (iFrame == 0) // if its first frame, initiate 
        {
            float ID = hash11(iDate.w + fragCoord.x);            
            float size = BALL_MIN_SIZE + ((BALL_MAX_SIZE - BALL_MIN_SIZE) * ID);
            fragColor = vec4(ID, size, 0., 0.);        
        }
        else // otherwise just pass same data.
            fragColor = texelFetch(iChannel0, ivec2(iCoord.x, 1),0).rgba;                        

    }        

}

