void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iCoord = ivec2(fragCoord);   // fragCoord as integers  
    if (iCoord.x > BALLCOUNT || iCoord.y > 1) // make sure pixel contains data otherwise return
        return;               
    if (iCoord.y == 1) // if its second top pixel, just pass the existing data on and return
    {
        fragColor = texelFetch(iChannel0,iCoord,0).rgba;
        return;
    }    
    vec4 data = texelFetch(iChannel0, iCoord,0).rgba;  // fetch data from pixel
    vec2 pos1 = data.rg;
    vec2 dir1 = data.ba;
    float r1 = texelFetch(iChannel0, ivec2(iCoord.x, 1),0).g;

    for(int i=0; i < BALLCOUNT; i++)  // check all the balls for collisions and apply new velocity.
    {
        if (i != iCoord.x)
        {
            vec4 otherOBJ = texelFetch(iChannel0, ivec2(i, 0),0).rgba;
            vec2 pos2 = otherOBJ.rg;        

            float r2 = texelFetch(iChannel0, ivec2(i, 1),0).g;

            float edgeDist = length(pos1-pos2) -r1  -r2;
            if (edgeDist < 0.)                        
            {
                vec2 dir2 = otherOBJ.ba;                
                float angle = atan(pos2.y - pos1.y, pos2.x - pos1.x);                                
                pos1.x = clamp(pos1.x + cos(angle) * edgeDist, 0., iResolution.x);
                pos1.y = clamp(pos1.y + sin(angle) * edgeDist, 0., iResolution.y);
                vec2 tanVec = normalize(vec2(-(pos2.x - pos1.x), pos2.y - pos1.y));                
                vec2 relVel = dir2 - dir1;                
                float valLength = min(dot(relVel, tanVec) , MAX_VELOCITY);
                vec2 velCompOn = tanVec * valLength ;
                vec2 velCompPerp = relVel - velCompOn ;
                dir1 += velCompPerp;  
                break;
            }            
        }
    }
    fragColor = vec4(pos1.x, pos1.y, dir1.x, dir1.y); // set the data to buffer                         
}

