void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iCoord = ivec2(fragCoord);  // fragCoord as integers
    vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.);               // Creating tracers by taking avarage of nearby pixels  
    for (int x = -1; x <= 1; x++)      // then avaraging them. You can also just darken each frame. 
        for (int y = -1; y <= 1; y++)  // but this makes it smoother.
            col += texelFetch(iChannel1,iCoord+ ivec2(x,y), 0).rgb ;   
    col /= 9.;
    col = mix(col, texelFetch(iChannel1,iCoord, 0).rgb, .5);    
    float d = TRACER_INTENSITY * (1.0 / iFrameRate);
    col = mix(col, vec3(0.) , clamp(d, 0., 1.) );


    vec3 bCol = vec3(0.);   // Drawing balls using data from buffer B
    float p =0.;    
    for(int i=0; i <= BALLCOUNT; i++)
    {
        vec2 pos =texelFetch(iChannel0, ivec2(i,0),0).rg;
        if (length(fragCoord-pos) < BALL_MAX_SIZE)
        {
            vec2 data = texelFetch(iChannel0, ivec2(i, 1),0).rg;
            float size = data.g * 1.5;
            float l = length(fragCoord-pos);

            float p = smoothstep(size,size*0.7, l) * 0.1 ;
            p += smoothstep(size*0.7,0., l) ;

            vec3 bCol = hue(data.r - (p * 0.15)) * p ;
            p = 1. - p;
            col = vec3(
            bCol.r + col.r * p, 
            bCol.g + col.g * p, 
            bCol.b + col.b * p);

        }                        
    }    
    fragColor = clamp(vec4(col, 1.), 0., 1.); // clamping to avoid issues with tracers.
}

