// stores N² frames: if click, or every second if mouse drag

void mainImage( out vec4 O, vec2 U )
{
    if (iMouse.w <= 0. && ( iMouse.z<=0. || iFrame%60>0 ))  { O = T(U); return; }
    U *= N/R;
    vec2 I = floor(U), F = fract(U);
