#define BOXES 10
float BOX_RADIUS = .04;

vec2 BOX_SIZE(float time) { return  vec2(.47, .2) * 1.; }
float BOX_R = 0.004;
vec2 VELOCITY = vec2(0.253, 0.15);

vec3 hue_to_rgb(float hue){
        float R = abs(hue * 6. - 3.) - 1.;
        float G = 2. - abs(hue * 6. - 2.);
        float B = 2. - abs(hue * 6. - 4.);
        return clamp(vec3(R,G,B), 0., 1.);
}

vec2 circle(float x) {
    return vec2(sin(x), cos(x));
}

vec2 tri(float x, vec2 p) {
    return 2. * abs(x/p - floor(x/p + .5));
}

vec2 velocity(int box, float time) {
    return BOX_SIZE(time) * vec2(VELOCITY * float(box + 1)/float(BOXES));
}

vec2 boxPos(int box, float time) {
    vec2 boxSize = BOX_SIZE(time) - BOX_RADIUS;
    vec2 start = -boxSize;
    return start + tri(time, boxSize / velocity(box, time)) * 2. * boxSize;
}

//https://iquilezles.org/articles/distfunctions2d/
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float opOnion( float d, float r )
{
  return abs(d) - r;
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

