vec2 trihi(float x, vec2 p) {
    p /= 2.;
    return (x/p - floor(x/p)) / 2.;
}

vec2 mainSound( int samp, float time )
{
    float amp;
    for(int i=0; i<BOXES; i++) {
        vec2 boxSize = BOX_SIZE(time) - BOX_RADIUS;
        vec2 period = trihi(time, boxSize / velocity(i, time));

        float hz = pow(2., (5. * float(BOXES-i))/12.) * 261.625565 * 2.;

        //if(min(period.x,period.y) < .1) amp = 1.
        amp += sin(6.2831*hz*time)*exp(-300.0*period.x) * .2;
        amp += sin(6.2831*hz*time)*exp(-300.0*period.y) * .2;        
    }

    if(time < .25) amp = 0.;


    return vec2( amp );
}

