// Standard 2D rotation formula.
mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }

// IQ's signed box formula.
float sBoxS(in vec2 p, in vec2 b, in float sf){

  vec2 d = abs(p) - b + sf;
  return min(max(d.x, d.y), 0.) + length(max(d, 0.)) - sf;
} 


// IQ's vec2 to float hash.
float hash21(vec2 p){  
    p = mod(p, gSc.xy);
    return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453); 
}

// IQ's vec2 to vec2 hash.
vec2 hash22(vec2 p){
    p = mod(p, gSc.xy);
    return fract(sin(vec2(dot(p, vec2(12.783, 78.137)), dot(p, vec2(41.581, 57.263))))
                          *vec2(43758.5453, 23421.6361));
}

