// Dave Hoskins's vec2 to float hash -- Modified for this particular example.
// You can find the details here:
//
// Hash without Sine - Dave_Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash21B(vec2 p){ 


    p = mod(p, gSc.xy);
    //return texture(iChannel1, p/gSc.xy).x;
    //return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453); 
    //return (fract(sin(dot(p, vec2(27.619, 57.583)))*43758.5453) - .5)*.85; 
    //return hash21(p);

    //  2 in, 1 out. 
	vec3 p3  = fract(vec3(p.xyx)*.1031);
    p3 += dot(p3, p3.yzx + 43.523);
    return (fract((p3.x + p3.y) * p3.z) - .5)*.85;

}

// The asymmetric block pattern.
//
// By the way, you could take a simple line-drawing and partitioning approach to greatly
// minimize the instruction count, and if 2D bump mapping effects, etc, are all you're
// after, it might be worth doing. However, if you wish to raymarch this, or do other
// interesting things, the four rectangles, and corresponding IDs, are a necessary evil.
vec4 pattern(vec2 p, vec2 sc){

    //p = conv(p);

    vec2 p0 = p;

    vec2 ip = floor(p*sc) + .5; // Grid ID.
    p -= ip/sc; // Local coordinates.

    vec3 e = vec3(-1, 0, 1); // Helper vector.


    float h11 = hash21B(ip); // Original cell.

    float h10 = hash21B(ip + e.xy); // Left.
    float h01 = hash21B(ip + e.yz); // Top.
    float h12 = hash21B(ip + e.zy); // Right.
    float h21 = hash21B(ip + e.yx); // Bottom.

    float h00 = hash21B(ip + e.xz); // Top left.
    float h02 = hash21B(ip + e.zz); // Top right.
    float h22 = hash21B(ip + e.zx); // Bottom right.
    float h20 = hash21B(ip + e.xx); // Bottom left.


    vec2[4] ctr;
    vec2[4] l;


    // The code looks fiddly, but it's based on a simple idea.
    // A while ago, I noticed that if you ran vertical and 
    // horizontal lines on alternate checkered tiles, you could
    // render perpendicular lines on either side at random
    // positions and everything would line up to form rectangles.
    // The following is just an implementation of that.

    // If you uncomment the SHOW_GRID define you'll see that 
    // each cell consists of either a vertical line flanked on
    // either side by horizontal lines at random Y-positions, or 
    // a horizontal line flanked on either side by vertical
    // lines at random X-positions.

    // Implementing the aforementioned is simple enough. However, 
    // lines are great, but cell boundaries -- in order to
    // render things like blocks would be the thing we'd be more 
    // interested in rendering, so that requires a little more
    // work.   	




    if(mod((ip.x + ip.y), 2.)<.5){ // Horizontal cell.

        // Partition the cell with a randomly positioned horizontal 
        // line and two joining randomly positioned vertical lines
        // then determine the cell dimensions and cell center of
        // all four resultant rectangular blocks.

        // Four block dimensions (X: Width, Y: Height).
        l[0] = vec2(h01 - h10, h00 - h11) + 1.;
        l[1] = vec2(-h01 + h12, h02 - h11) + 1.;
        l[2] = vec2(-h21 + h12, -h22 + h11) + 1.;
        l[3] = vec2(h21 - h10, -h20 + h11) + 1.;

        // Four block centers.
        ctr[0] = vec2(h01, h11) + l[0]*vec2(-.5, .5);
        ctr[1] = vec2(h01, h11) + l[1]*vec2(.5, .5);
        ctr[2] = vec2(h21, h11) + l[2]*vec2(.5, -.5);
        ctr[3] = vec2(h21, h11) + l[3]*vec2(-.5, -.5); 

    }
    else { // Vertical cell.

        // Partition the cell with a randomly positioned vertical 
        // line and two joining randomly positioned horizontal lines
        // then determine the cell dimensions and cell center.

        // Four block dimensions (X: Width, Y: Height).
        l[0] = vec2(-h00 + h11, h01 - h10) + 1.;
        l[1] = vec2(h02 - h11, h01 - h12) + 1.;
        l[2] = vec2(h22 - h11, -h21 + h12) + 1.;
        l[3] = vec2(-h20 + h11, -h21 + h10) + 1.;

        // Four block centers.
        ctr[0] = vec2(h11, h10) + l[0]*vec2(-.5, .5);
        ctr[1] = vec2(h11, h12) + l[1]*vec2(.5, .5);
        ctr[2] = vec2(h11, h12) + l[2]*vec2(.5, -.5);
        ctr[3] = vec2(h11, h10) + l[3]*vec2(-.5, -.5); 


    }


    // Debugging: Show the squares with a set single dimension.
    //l[0] = l[1] = l[2] = l[3] = vec2(.7); // Overlapping: vec2(1.5); 

    // Scaling down the block dimensions.
    //l[0] /= sc; l[1] /= sc; l[2] /= sc; l[3] /= sc;



    // Determine the minimum block using the standard method.
    float d = 1e5;
    vec2 tileID = vec2(0);
    //vec2 ctri = vec2(0);
    vec2 li = vec2(0);
    vec2 svP;


    vec3 col;
    for(int i = 0; i<4; i++){

         vec2 ap = abs(p - ctr[i]/sc) - l[i]/sc/2.;
         float bx = max(ap.x, ap.y);
    	//float bx = sBoxS(p - ctr[i]/sc, l[i]/sc/2., 0.);

        if(bx<d) {
            d = bx;
            tileID = (ip + ctr[i]);
            //ctri = ctr[i];
            li = l[i];
            svP = p - ctr[i]/sc;

            float rnd = hash21(tileID);

            col = .5 + .5*cos(6.2831*rnd + vec3(0, 1, 2));
        }

    }


    // Return the distance value of the closed rectangular block
    // and it's cell center, which doubles as a unique ID. In this
    // case, though, we're returning the the tile center and 
    // and tile dimensions.
    //return vec4(svP, li);//li/s
    return vec4(tileID/sc, li/sc);//vec4(tileID/sc, li/sc);
//return vec4(d, col);//li/s
}



vec4 funcFace0(vec3 q3){


    // Coordinates.
    vec2 p = (q3.xy); 
    vec4 p4 = pattern(p, gSc.xy);

    return p4;


}



// Cube mapping - Adapted from one of Fizzer's routines. 
int CubeFaceCoords(vec3 p){

    // Elegant cubic space stepping trick, as seen in many voxel related examples.
    vec3 f = abs(p); f = step(f.zxy, f)*step(f.yzx, f); 

    ivec3 idF = ivec3(p.x<.0? 0 : 1, p.y<.0? 2 : 3, p.z<0.? 4 : 5);

    return f.x>.5? idF.x : f.y>.5? idF.y : idF.z; 
}



void mainCubemap(out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir){


    // UV coordinates.
    //
    // For whatever reason (which I'd love expained), the Y coordinates flip each
    // frame if I don't negate the coordinates here -- I'm assuming this is internal, 
    // a VFlip thing, or there's something I'm missing. If there are experts out there, 
    // any feedback would be welcome. :)
    vec2 uv = fract(fragCoord/iResolution.y*vec2(1, -1));

    // Adapting one of Fizzer's old cube mapping routines to obtain the cube face ID 
    // from the ray direction vector.
    int faceID = CubeFaceCoords(rayDir);

    // We're only using one cube map face, so don't calculate any others...
    // or give the annoying compiler a chance to calculate others.
    if(faceID > 0) return;


    // Pixel storage.
    vec4 col = vec4(0);



    // Precalculation flag: GPUs are annoying. Sometimes, they'll will calculate
    // both the "if" and "else" statements every time. The "if" part here is extremely
    // expensive, so we don't want that. The solution is to not have an "if-else"
    // statement at all.
    int preCalc = 0;


    // Initial conditions -- Performed upon initiation.
    //if(abs(tx(iChannel0, uv, 5).w - iResolution.y)>.001){
    //if(iFrame<1){
    //
    // Great hack, by IQ, to ensure that this loads either on the first frame, or in the
    // event that the texture hasn't loaded (this happens a lot), wait, then do it...
    // Well kind of. Either way, it works. It's quite clever, which means that it's something 
    // I never would have considered. :)
    if(textureSize(iChannel0, 0).x<2 || iFrame<maxFrames){



        //if(iFrame>=maxFrames) return;


        // Fill the first cube face with a custom 3D function.
        if(faceID==0){

            //vec3 p = convert2DTo3D(uv);      
            vec3 p = vec3(uv, 0);      

            col = funcFace0(p);
            //col = mix(col, 1.-funcFace0(p*2.), 1./16.);

            preCalc = 1;

        }

        /*
        // Last channel on the last face: Used to store the current 
        // resolution to ensure loading... Yeah, it's wasteful and it
        // slows things down, but until there's a reliable initiation
        // variable, I guess it'll have to do. :)
        if(faceID==5){

            col.w = iResolution.y;
        }
        */


    }


    if(preCalc == 0 && faceID == 0){

       col = tx0(iChannel0, uv);
       //col = texture(iChannel0, vec3(-.5, uv.yx - .5));
       //col = tx(iChannel0, uv, faceID);
    }


    // Update the cubemap faces.
    fragColor = col;

}

buffer_a_code:
/*

    Asymmetric Texture Raycasting
    -----------------------------

    This is a cell by cell traversal of a packed asymmetric rectangle grid
    texture. I've been wanting to put up a ray traversal example that 
    people may not have seen before. I'm pretty sure there are no packed
    rectangle grid traversals on Shadertoy, and I don't recall seeing a 
    texture traversal either, but there might be a couple lurking around.

    Traversing a packed rectangle algorithm created in realtime is not the 
    easist thing to do, but it's definitely doable, and it's accurate. 
    Unfortunately, the process is not quite fast enough... Not yet anyway. 
    Reading from a precalculated buffer texture is subject to resolution 
    constraints, but is way faster, so is the lesser of the two evils. 
    Getting pixels to play nice wasn't fun (it never is), but it's done now, 
    so hopefully the next time won't be as frustrating. :)

    In theory, traversing a texture grid is pretty easy: Read into the
    texture and read out the precalculated central grid object position
    ID and object dimensions (width and height for rectangles), then use
    them to traverse to the next cell position -- Raytracing to rectangle
    edges isn't rocket science. However, calculations have to be perfect,
    and pixel resolution contraints don't always allow for that, so various
    fudges are necessary.

    Anyway, the purpose of this was just to show that it could be done. I 
    kind of cobbled this together from my other examples, so I wouldn't pay 
    too much attention to it. I'd like to put together a static path
    traced version later.


    Related examples:

    // The packed texture pattern itself was based on an
    // idea I came up with a while back.
    Asymmetric Blocks - Shane
    https://www.shadertoy.com/view/Ws3GRs

    // For anyone completely new to cell by cell traversal, here's an
    // unlisted simplified 2D rectangle traversal example that should 
    // be much easier to understand.
    Minimal Line Traversal - Shane
    https://www.shadertoy.com/view/sdfBDM


*/

// Far plane. Ironically, this one is quite close. :)
#define FAR 25.


 // Ray origin, ray direction, point on the line, normal. 
float rayLine(vec2 ro, vec2 rd, vec2 p, vec2 n){

   // This is trimmed down, and can be trimmed down more. Note that 
   // "1./dot(rd, n)" can be precalculated outside the loop. However,
   // this isn't a GPU intensive example, so it doesn't matter here.
   return dot(p - ro, n)/dot(rd, n);

}

vec2 getUV(vec2 p){

    //p /= 2.;

    // Cube map texture coordinate conversion.
    p *= cubemapRes;
    vec2 ip = floor(p); //p -= ip;
    return fract((ip + .5)/cubemapRes) - .5;

}

// The path is a 2D sinusoid that varies over time, which depends upon the 
// frequencies and amplitudes.
vec2 path(in float z){ 
    //return vec2(0);
    return vec2((cos(z*.36/8.) - sin(z*.2/8.)*1.2)*6., sin(z*.12));
} 


float h(vec2 p){

    // Only one texture read.
    vec3 tx = texture(iChannel0, p/iChannelResolution[0].xy).xyz;  tx *= tx;///iChannelResolution[0].xy
    // Greyscale height. Using "tx.x" would work, too.
	float f = dot(tx, vec3(.299, .587, .114));
    float f2 = f;//min(f, .5);

    //f = sin(f*6.2831 + iTime)*.5 + .5;
    // Using the camera path to carve out a channel for the camera to glide through.
    vec2 pth = path(p.y);
    float pX = abs(p.x - pth.x); // The channel.
    f *= min(pX*pX/24., 1.);

    // Overall height.
    return f*8. + f2*.5 + pth.y;

}

// Rectangle scale: This was hacked in at the last minute and is a little
// fickle. Sizes one to about 8 are OK. Lower numbers mean smaller rectangles,
// which require more traversal steps in the "raycast" function.
const vec2 txSc = vec2(6);

// Grid cell function.
vec4 gridID(vec2 p){

    // Same size squares, for comparison. 
    //return vec4(floor(p/txSc) + .5, txSc);

    // Texture multiple ID.
    vec2 p0 = (floor(p/txSc)*txSc.xy);

    // Texture grid information -- Cube map faces are annoying to read into.
    vec2 uv = getUV(p/txSc);

    // Read the texture face information.
    vec4 hm2 = texture(iChannel3, vec3(-.5, uv.yx)); 
    // Converting to exact pixel positions for wrapping purposes.
    hm2 = convert2(hm2)*txSc.xyxy;

    // Return the central position and dimension of the nearest rectangle.
    return vec4((p0 + hm2.xy), hm2.zw);

}




// Sign function without the zero, which can cause problems for some routines.
vec3 sign2(in vec3 p){ return vec3(p.x<0.? -1 : 1, p.y<0.? -1 : 1,  p.z<0.? -1 : 1); }
//vec2 sign2(in vec2 p){ return vec2(p.x<0.? -1 : 1, p.y<0.? -1 : 1); }

vec4 raycast(vec3 ro, vec3 rd){

    // Result.
    vec4 res = vec4(FAR, 0, 0, 0);

    // Unit direction sign.
    vec3 srd = sign2(rd);

    // Rectangle normals: Any two will do. By the way, there's nothing
    // stopping you from declaring all four normals for all surrounding
    // walls, but since you know only two will be in front of the
    // direction ray at any given time, it makes sense to only choose
    // two.
    //
    // Declare two normals. Any side by side ones will do.
    vec2 n1 = vec2(-1, 0), n2 = vec2(0, -1); // Right and top edges.

    // If the cell wall is behind the ray (or the ray is facing the opposing cell
    // wall, if you prefer), use the normal index from the back cell wall. This 
    // trick is possible because of the rectangle symmetry. As an aside, for 
    // anyone who doesn't know, dotting the direction ray with the face normal 
    // is something you do in software engines for back face culling.
    n1 = dot(rd.xz, n1)<0.? -n1 : n1;
    n2 = dot(rd.xz, n2)<0.? -n2 : n2;

    // Initiate the ray position at the ray origin.
    vec3 pos = ro;

    // Obtain the coordinates of the cell that the current ray position 
    // is contained in -- I've arranged for the cell coordinates to 
    // represent the cell center to make things easier.
    //vec2 ip = gridID(pos.xz);

    float t1 = 1e8, t2 = 1e8, tT = 1e8;

    int hit = 0;

    vec4 gID = gridID(pos.xz);
    vec2 ip;// = gID.xy;


    // Iterate through 24 cells -- Obviously, if the cells were smaller,
    // you'd need more to cover the distance.
    for(int i = 0; i<120; i++){ 


        ip = gID.xy;
        float ma = h(ip);

        // At this point, we haven't advanced the ray to the back of the cell boundary,
        // so we're at one of the front cell face positions. Therefore, check to see if 
        // we're under the pylon height. If so, we've hit a face, so mark the face as hit, 
        // then break.
        if(pos.y<ma){
            // Hit a side.
            hit = 1;
            break; 

        } 

        // Ray intersection from the currect cell position to each of the 
        // visible cell walls. Normals face inward.
        // You pass in the current position, the unit direction ray, a known 
        // point on the cell wall (any will do) and the cell wall's normal.
        t1 = rayLine(pos.xz, rd.xz, (ip + n1*gID.zw*.5), -n1);
        t2 = rayLine(pos.xz, rd.xz, (ip + n2*gID.zw*.5), -n2);

        // Determine the closest edge then record the closest distance and
        // asign its normal index.
        vec3 tn = t1<t2? vec3(t1, n1) : vec3(t2, n2);

        // Top face distance.
        tT = (ma - pos.y)/rd.y;
        tT = tT<0. ? 1e8 : tT;


        // We've now advanced to one of the back faces of the cell. Check to see whether
        // we're still under the pylon height, and if so, we've hit the top face --  
        // I always have to think about this, but the logic is that we haven't hit a front
        // cell face and we're still under the height, so we've hit the top. Anyway, mark 
        // the top face as hit, advance the distance in the Y direction to the top face, 
        // then break.
        if(tT<tn.x){

            //dist += tT;
            pos += rd*tT; 
            hit = 2;
            break;

        }      


        // If this cell's ID matches the ID of the backgound cell, 
        // flag it as hit in order to color it, or whatever.
        //if(length(cell - ip)<.001){ hit = 1; break; }

        // Advance the cell index position by the indices of the 
        // cell wall normal that you hit. 
        //ip += tn.yz;
        // Advance the ray position by the distance to the next cell wall.
        pos += rd*tn.x;

        // Textures have fixed size, so increasing the scale effects stepping from
        // one grid cell to the next. Hence, the "txSc" variable.
        gID = gridID(pos.xz + (srd.xz/1024.)*txSc);// + (srd.xz/1024.)*txSc


    }

    float fID = tT<t1 && tT<t2? 0. : t1<t2? 1. : 2.;
    if(fID == 1.){ fID = dot(rd.xz, vec2(-1, 0))<0.? -fID : fID; }
    else if(fID == 2.){ fID = dot(rd.xz, vec2(0, -1))<0.? -fID : fID; }

    res.x = length(pos - ro);
    if(hit == 0) res.x = FAR;

    return vec4(res.x, fID, ip);

}

// mat3 rotation... I did this in a hurry, but I think it's right. :)
// I have a much better one than this somewhere. 
mat3 rot(vec3 ang){

    vec3 c = cos(ang), s = sin(ang);

    return mat3(c.x*c.z - s.x*s.y*s.z, -s.x*c.y, -c.x*s.z - s.x*s.y*c.z,
                c.x*s.y*s.z + s.x*c.z, c.x*c.y, c.x*s.y*c.z - s.x*s.z,
                c.y*s.z, -s.y, c.y*c.z);    
}

// Standard normal function.
vec3 nr(float fID, vec3 rd) {

    vec3 n = fID == 0.? vec3(0, 1, 0) : abs(fID) == 1.? vec3(1, 0, 0) : vec3(0, 0, 1);
    n *= fID<-.001? -1. : 1.; 
	return n;
}

// Very lame sky with sun-like object. Basically, this was a quick hack to emulate
// the "Forest Blurred" cube map... It needs work. :)
vec3 getSky(vec3 rd, vec3 ld){

    float lgt = max(dot(rd, ld), 0.);
    vec3 sky = mix(vec3(.1, .05, .04), vec3(.95, .97, 1)*3., clamp((rd.y + .5),0., 1.))/1.5;
    sky = mix(sky, vec3(8), pow(lgt, 8.));
    return min(sky*vec3(1, 1.1, 1.3), 1.);
}

// Block face pattern field.
vec3 df(vec2 p){

    // Asymetric grid pattern.
    const float sc = 1.5;
    p /= sc;

    // Texture multiple.
    vec2 p0 = floor(p);
    // Read into the texture (from the cube map face).
    vec2 uv = getUV(p);
    vec4 hm2 = texture(iChannel3, vec3(-.5, uv.yx));
    hm2 = convert2(hm2);
    // Rectangle distance. 
    vec2 q = abs((uv + .5) - hm2.xy) - hm2.zw/2.;
    float d = max(q.x, q.y); 
    // Rectangle distance and central ID.
    return vec3(d, p0 + hm2.xy);

}

void mainImage(out vec4 c, vec2 u){


    // Screen coordinates: The coordinates are already in one to one ratio form, so 
    // a simple translation and scaling is all that is necessary in this setting.
    u = (u - iResolution.xy*.5)/iResolution.y;

    // Ray origin.
    vec3 o = vec3(0, 1.5, iTime*1.5);


    // "Look At" position.
    vec3 lk = o + vec3(0, -.35, 1.);//vec3(0, -.25, iTime);  


	// Sending the camera and "look at" vectors along the path. The "path" function is 
	// synchronized with the distance function.
    o.xy += path(o.z);
	lk.xy += path(lk.z);


    // Using the above to produce the unit ray-direction vector.
    float FOV = .75; // FOV - Field of view.

    vec3 fwd = normalize(lk - o);    
    vec3 rgt = normalize(vec3(fwd.z, 0., -fwd.x)); 
    // "right" and "forward" are perpendicular, due to the dot product being zero. Therefore, I'm 
    // assuming no normalization is necessary? The only reason I ask is that lots of people do 
    // normalize, so perhaps I'm overlooking something?
    vec3 up = cross(fwd, rgt); 

    // Camera.
    mat3 mCam = mat3(rgt, up, fwd);

    // Camera movement. Rotation, swivle, etc.
    mCam *= rot(vec3(-path(o.z).x/64., 0, 0)); // Camera roll.
    //mCam *= rot(vec3(0, 0, iTime/4.)); // Camera pitch.

    // Unit direction ray.
    vec3 r = mCam*normalize(vec3(u, 1./FOV));

    // Directional light.
    vec3 l = normalize(vec3(.2, .7, .8)); 

    // Raycasting
    vec4 res = raycast(o, r);

    float t = res.x, d;
    float fID = res.y;
    vec2 id = res.zw;

    t = min(t, FAR); // Clipping to the far distance, which helps avoid artifacts.

    // Scene color, initialized to zero.
    c = vec4(0);

    // If we've hit an object, light it up.
    if(t<FAR){

        vec3 p = o + r*t, n = nr(fID, r);

        l -= p; // Light to surface vector. Ie: Light direction vector.
        d = max(length(l), 0.001); // Light to surface distance.
        l /= d; // Normalizing the light direction vector.

        l = normalize(vec3(.7, .8, 1));

        // Diffuse.
        float dif = max(dot(l, n), 0.);

        float sh = 1.;
        if(dif>0.){
           vec4 resSh = raycast(p + n*.002, l);

           if(resSh.x<FAR - 1e-3) sh = 0.; //
           //if(resSh.x<d - 1e-3) sh = 0.; // Point light.

        }


        // Scene object color.
        //
        // Coloring half the grid objects.
        float rnd = fract(sin(dot(id, vec2(157, 113)))*43758.5453);
        //float rnd2 = fract(sin(dot(id + .5, vec2(157, 113)))*43758.5453);


        // Rectangle ID to texure color.
        vec3 txC = texture(iChannel1, id/gSc.xy*txSc).xyz; txC *= txC;


        // UV coordinates for each face.
        vec2 uv = fID == 0.? p.xz : abs(fID) == 1.? p.zy : p.xy;

        // Texture pattern layers.
        vec3 tx = texture(iChannel1, rot2(3.14159/3.*1.)*uv/2.).xyz; tx *= tx;
        vec3 tx2 = texture(iChannel1, rot2(-3.14159/6.*1.)*uv/1. + .5).xyz; tx2 *= tx2;
        vec3 tx3 = texture(iChannel1, rot2(-3.14159/12.*1.)*uv*2. + 1.).xyz; tx3 *= tx3;
        // Mixing the layers.
        tx = mix(tx, tx2, .35);
        tx = mix(tx, tx3, .35);
        float gr = dot(tx, vec3(.299, .587, .114));

        // Applying the texture color and pattern.
        c.xyz = txC;
        c.xyz *= (.75 + gr*1.5);


        // IQ's rim lighting snippet: For anyone not familiar, he's using 
        // the Fresnel factor for some sillouette lighting.
        float rim = pow(clamp(1. + dot(r, n), 0., 1.), 5.);


        // Specular reflection. 
        float spe = pow(clamp(dot(l, reflect(r, n)), 0., 1.), 8.);

        // Grid rectangle central position and dimension.
        // Note the desperate step hack to ensure we hit the correct cell -- Seems to work.
        vec4 gID = gridID(p.xz + sign2(r).xz/1024.*txSc);

        // Lamest AO ever, and not accurate at all. I'll sort it out later.
        vec2 p2 = (p.xz - gID.xy);
        float h0 = h(gID.xy);
        vec4 h4 = vec4(h((gID.xy + vec2(-1, 0))*gID.zw), h((gID.xy + vec2(1, 0))*gID.zw), 
                       h((gID.xy + vec2(0, -1))*gID.zw), h((gID.xy + vec2(0, 1))*gID.zw));

        vec4 h4mh0 = h4 - h0;
        float ao = 1.;
        float minEdge = min(gID.z, gID.w)/4.;
        float edge = gID.w/4.;//min(s.x, s.y)/4.;
        float edge2 = gID.z/4.;//max(s.x, s.y)/4.;


        float aoSh = .25;
        if(n.y>.5){
            if(p2.y>minEdge && h4mh0.w>0.) ao = min(ao, 1. - smoothstep(0., 1., (p2.y - edge)/edge)*aoSh);
            if(p2.y<-minEdge && h4mh0.z>0.) ao = min(ao, 1. - smoothstep(0., 1., (-p2.y - edge)/edge)*aoSh);
            if(p2.x<-minEdge && h4mh0.x>0.) ao = min(ao, 1. - smoothstep(0., 1., (-p2.x - edge2)/edge2)*aoSh);
            if(p2.x>minEdge && h4mh0.y>0.) ao = min(ao, 1. - smoothstep(0., 1., (p2.x - edge2)/edge2)*aoSh);
        }
        else {

            vec4 mEdge4 = vec4(minEdge) - (h4 - h0)/2.;
            vec4 hp = p.y - h4 - mEdge4;


            if(n.z<-.5 && hp.z<0.) ao = min(ao, 1. - smoothstep(0., 1., -(hp.z)/(mEdge4.z))*aoSh);
            if(n.z>.5  && hp.w<0.) ao = min(ao, 1. - smoothstep(0., 1., -(hp.w)/(mEdge4.w))*aoSh);
            if(n.x<-.5 && hp.x<0.) ao = min(ao, 1. - smoothstep(0., 1., -(hp.x)/(mEdge4.x))*aoSh);
            if(n.x>.5 && hp.y<0.) ao = min(ao, 1. - smoothstep(0., 1., -(hp.y)/(mEdge4.y))*aoSh);

        }
        ao = max(ao, 0.);


        // Side wall shading. Also a hack, but it's effective.
        vec2 pth = path(p.z);
        ao *= pow(clamp((p.y - pth.y + .001)/(h0 - pth.y + .001), 0., 1.), 1.);

        // Side wall debugging.
        //if(abs(n.y)<.5) c.xyz *= vec3(2, 1, 1);


        // Local coordinates.
        vec2 lc = p.xz - gID.xy;
        // Domain.
        vec2 ap = abs(lc) - gID.zw/2.;

        // Face edges.
        float fEdge = max(ap.x, ap.y);
        fEdge = abs(fEdge);
        fEdge = max(fEdge, -(p.y - h0)) - .01;
        // Side edges.
        float sEdge = min(ap.x, ap.y);
        sEdge = max(-sEdge, (p.y - h0)) - .01;
        // Combining.
        fEdge = min(fEdge, sEdge);


        // Applying the finer pattern to the box faces... This  definitely needs
        // a tidy up.
        vec3 d3 = df(uv);
        vec3 sC = c.xyz; // Old color.
        vec3 txC2 = texture(iChannel1, hash22(d3.yz + .1)).xyz; txC2 *= txC2;
        //if(fID == 0.){ // Top of the box only.
        vec3 smC = txC2;
        smC *= (.75 + gr*1.5);
        smC = mix(sC, smC, .5);
        c.xyz = mix(c.xyz/8., smC, 1. - smoothstep(0., txSc.x*.001*(1. + res.x*res.x*.001), d3.x + .007));
        //}



        // Applying diffuse lighting, ambient lighting, etc.
        vec3 cCol = (dif*sh + vec3(1, .8, .5)*spe*sh + vec3(.3, .5, 1)*rim*4. + .25)*ao;
        c.xyz = c.xyz*cCol;
        sC = sC*cCol;


        // Applying the edges. I've hacked a lot of this in after the fact, so will need
        // to reorder, etc.
        vec3 svC = c.xyz;
        float grr = dot(c.xyz, vec3(.299, .587, .114));
        c.xyz = mix(c.xyz/(.5 + grr*grr), vec3(1), .03/(.5 + grr*grr)); // Lightening.
        float grrC = dot(sC, vec3(.299, .587, .114));
        sC = mix(sC/(.5 + grrC*grrC), vec3(1), .03/(.5 + grrC*grrC)); // // Lightening.

        // Edge rendering.
        const float ew = .05;
        c.xyz = mix(c.xyz + .0, mix(sC, vec3(1), .1), 
                    (1. - smoothstep(0., .001*(1. + res.x*res.x*.05), fEdge - ew)));
        c.xyz = mix(c.xyz, vec3(0), (1. - smoothstep(0., .001*(1. + res.x*res.x*.05), fEdge))*.6);
        c.xyz = mix(c.xyz, mix(sC, vec3(0), .6),
                    (1. - smoothstep(0., .001*(1. + res.x*res.x*.05), abs(fEdge - ew) - .01/2.)));



        // Reflection: Not a proper reflective color pass, but it works well
        // enough here. If we hit something, render nothing, otherwise, render
        vec3 ref = reflect(r, n);
        vec4 resRef = raycast(p + n*.002, ref);
        vec3 refTx = getSky(ref, l);
        if(resRef.x<FAR - 1e-3) refTx *= 0.; // Point light.

        // Fresnel reflection of sorts. This has been poached from one of IQ's examples.
        float fr = mix(.03, .25, pow(max(0., 1. + dot(r, n)), 3.));
        c.xyz = mix(c.xyz, min(refTx, 1.)*3., fr);



        // Debug.
        //c.xyz = vec3((dif + .5))*ao;



    }

    // Applying some distance fog.
    vec4 fCol = vec4(getSky(r, l), 1);
    c = mix(clamp(c, 0., 1.), fCol, smoothstep(.2, .8, t/FAR));


    /*
    // Just the height texture on its own, for anyone interested.
    vec2 u2 = u + iTime/8.;
    vec3 d3 = df(u2);
    float rnd = hash21(d3.yz/32. + .05);
    //float rnd = hash21(hm2.zw)*.5 + .5;
    vec3 rCol = .5 + .5*cos(6.2831*rnd*8. + vec3(0, 1, 2)*1.5);
    c.xyz = mix(vec3(0), rCol, 1. - smoothstep(0., 1./iResolution.y, d3.x));
    // Equal to the focal point to avoid blurriness from the DOF pass.
    // Alternatively, you could turn DOF off.
    t = 8.; 
    */

    // Temporal blurring. Not used here.
    //vec4 bTx = texture(iChannel2, u0/iResolution.xy);
    //c = mix(bTx, vec4(max(c.xyz, 0.), t), 1./2.);

    // Backbuffer value. The camera to hit point distance is there for DOF calculations.
    if(iFrame < maxFrames) c = vec4(1);
    c = vec4(max(c.xyz, 0.), t);


}

