vec2 getDir(vec2 p){
    vec2 v = (vec2(cos(iTime+p.x), sin(iTime+p.y)));
    if(abs(v.x)<.25)
        v.x = 0.;
    if(abs(v.y)<.25)
        v.y = 0.;
 	return ceil(abs(v))*sign(v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 1.-uv;
    vec3 c = texture(iChannel0, uv).rgb;   

    vec2 dir = getDir((uv.xy-.5)*vec2(cos(iTime), sin(iTime*.5))*20.);

    uv = 1.-uv;
   uv = fract((fragCoord.xy+dir) / iResolution.xy);

    vec3 f = texture(iChannel0, uv).rgb;
    vec3 o = c;
    if(f.r>0. && o.g >0.){
        c.g-=s;
        if(c.g<0.)
        	c = f+vec3(s,0.,0.);
    }
    if (f.g>0. && o.b >0.){
        c.b-=s;
        if(c.b<0.)
        	c = f+vec3(.0,s,0.);
    }
    if(f.b>0. && o.r >0.){
        c.r-=s;
        if(c.r<0.)
        	c = f+vec3(.0,0.,s);
    }

    if(iFrame<=1 || iMouse.z> 0.){
    	c = texture(iChannel1, uv).rgb;  
        if(c.r>c.g && c.r>c.b)
            c = vec3(1., 0., 0.);
        else if(c.g>c.r && c.g>c.b)
            c = vec3(0., 1., 0.);
        else
