#define PI 3.1415926538

mat2 rotate(float a) {
    float s = sin(a);
    float c = cos(a);
    // 2D rotation matrix: https://en.wikipedia.org/wiki/Rotation_matrix
    return mat2(c, -s, s, c);
}

float sdfBox(in vec2 p, in vec2 center, in vec2 half_wh, mat2 rotor) {
    // vec2 p: the point for which to calculate sdf
    // vec2 center: the center of the box
    // vec2 half_wh: the half-width and half-height of the box
    // based on IQ's Box SDF tutorial: https://www.youtube.com/watch?v=62-pRVZuS5c
    // which is a "mandatory" watch

    // put p in the "right hand quadrant" relative to box center
    vec2 symm_p = abs((p - center) * rotor);
    vec2 delta = symm_p - half_wh;
    // when the point is outside the box, outside will be positive, inside = 0
    vec2 outside = max(delta, vec2(0.0));
    // when the point is inside the box, inside will be negative, outside = 0
    float inside = min(max(delta.x, delta.y), 0.0);

    return length(outside) + inside;
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set the default background to white
    vec3 col = vec3(1.0, 1.0, 1.0);

    // the position of the pixel that will be painted is stored in fragCoord
    vec2 p = fragCoord;

    // the center of the box
    vec2 center = vec2(0.25 * iResolution.x, 0.75 * iResolution.y);
    vec2 half_wh = 0.5 * vec2(0.1 * iResolution.x, 0.3 * iResolution.y);

    // play around with this to get a feel for how it controls anti-aliasing
    float smooth_cutoff = 0.01;
    mat2 rotor = rotate(-0.25 * PI);
    col *= smoothstep(0.0, 1.0 + smooth_cutoff, sdfBox(p, center, half_wh, rotor));

    // set the colour of pixels in the fragment to be col, with alpha 1.0
    fragColor = vec4(col, 1.0);
}

