precision highp float;

#define PI 3.1415926535897932384626433832795028841

vec3 HSLtoRGB(vec3 hsl) {
    //0 <= h <= 360
    //0 <= s <= 1
    //0 <= l <= 1
    float chroma = hsl.y * (1.0 - abs(2.0 * hsl.z - 1.0));
    float h_prime = hsl.x / 60.0;
    float x = chroma * (1.0 - abs(mod(h_prime, 2.0) - 1.0));
    vec3 rgb;
    if (0.0 <= h_prime && h_prime < 1.0) {
        rgb = vec3(chroma, x, 0.0);
    } else if (1.0 <= h_prime && h_prime < 2.0) {
        rgb = vec3(x, chroma, 0.0);
    } else if (2.0 <= h_prime && h_prime < 3.0) {
        rgb = vec3(0.0, chroma, x);
    } else if (3.0 <= h_prime && h_prime < 4.0) {
        rgb = vec3(0.0, x, chroma);
    } else if (4.0 <= h_prime && h_prime < 5.0) {
        rgb = vec3(x, 0.0, chroma);
    } else {
        rgb = vec3(chroma, 0.0, x);
    }
    float m = hsl.z - 0.5 * chroma;
    return rgb + m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float zoom = 3.0;
    vec2 center = vec2(0.0);
    vec2 z = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.x * zoom + center;
    float exponent = 2.0 + iTime * 0.2;
    float iter = 0.0;
    const float max_iter = 256.0;
    while (iter < max_iter) {
        vec2 oldz = z;
        vec2 f = power(z, exponent) - vec2(1.0, 0.0); //Function f(z)
        vec2 df = exponent * power(z, exponent - 1.0); //f'(z)
        z = z - div(f, df);

        float r = mag2(z - oldz);
        if (r < 1.0e-6) {
            break;
        }
        iter += 1.0;
    }

    if (iter >= max_iter) {
        fragColor = vec4(vec3(0.0), 1.0);
    } else {
        float h = arg(z) / PI;
        if (h < 0.0) {
            h = h + 2.0;
        }
        vec3 col = HSLtoRGB(vec3(h * 180.0, 1.0, 0.5));
        fragColor = vec4(col, 1.0);
    }
}

