// Voronoi Tracking Experiment 1

// based on the shader https://www.shadertoy.com/view/tlKGDh of michael0884

// use mouse for add particles
// use spacebar for clear the screen

#define COUNT_STEPS 50
#define SCALE_STEP 0.005
#define OFFSET_STEP 2.0

vec4 getPheromone(vec2 p)
{
	return texelFetch(iChannel1, ivec2(loop(p, iResolution.xy)), 0);
}

vec4 getPheromoneInv(vec2 p)
{
	return getPheromone(p);
}

vec4 layer(vec2 p, float scale, vec3 light)
{
	p -= iResolution.xy * 0.5;
	p *= scale;
	p += iResolution.xy * 0.5;

	vec4 color;
	color.a = step(0.95, clamp(getPheromone(p).x*100.0,0.0,1.0));
	color.rgb = light * color.a;
	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);

    // lighting
    float e = 100.0 / min(iResolution.x, iResolution.y);
	float f = getPheromoneInv(fragCoord).x;
	float fx = (f-getPheromoneInv(fragCoord + vec2(1,0)).x)/e;
	float fy = (f-getPheromoneInv(fragCoord + vec2(0,1)).x)/e;
	vec3 n = normalize(vec3(0,0,1) - vec3(fx,fy,0.0));
	float diff = max(dot(vec3(0,0,1), n), 0.0);
	float spec = pow(max(dot(normalize(lightDirection), reflect(vec3(0,0,1),n)), 0.0), specularPower);	
    vec3 color = vec3(clamp((lightDiffuse * diff + lightSpecular * spec) * 1.5, 0.0, 1.0));

    // cycle
    float a = iTime * 0.5;
    vec2 off = vec2(cos(a), sin(a));

    // layering of same texture
	float fcount = float(max(COUNT_STEPS, 1));
	float scale = 1.0 + fcount * SCALE_STEP;
	float fog = 0.0;
	float fogStep = 1.0 / fcount;
	float offScale = 1.0 + fcount * OFFSET_STEP;
	for (int i = 0 ; i < COUNT_STEPS ; ++i)
	{
		scale -= SCALE_STEP;
		fog += fogStep;
        offScale -= OFFSET_STEP;
		vec4 c = layer(fragCoord + off * offScale, scale, color);
		if (c.a > 0.5) // smart merge for avoid overwrite
			fragColor.rgb = c.rgb * fog;
	}
}

