    cTile[7].tlColor = Y; cTile[7].trColor = R; cTile[7].brColor = Y; cTile[7].blColor = Y;
    cTile[8].tlColor = R; cTile[8].trColor = Y; cTile[8].brColor = R; cTile[8].blColor = R;
    cTile[9].tlColor = Y; cTile[9].trColor = Y; cTile[9].brColor = R; cTile[9].blColor = R;
    cTile[10].tlColor = R; cTile[10].trColor = Y; cTile[10].brColor = R; cTile[10].blColor = Y;
    cTile[11].tlColor = Y; cTile[11].trColor = Y; cTile[11].brColor = R; cTile[11].blColor = Y;
    cTile[12].tlColor = R; cTile[12].trColor = Y; cTile[12].brColor = Y; cTile[12].blColor = R;
    cTile[13].tlColor = Y; cTile[13].trColor = Y; cTile[13].brColor = Y; cTile[13].blColor = R;
    cTile[14].tlColor = R; cTile[14].trColor = Y; cTile[14].brColor = Y; cTile[14].blColor = Y;
    cTile[15].tlColor = Y; cTile[15].trColor = Y; cTile[15].brColor = Y; cTile[15].blColor = Y;
}

int getHorizontal(wangTile tileArray[16], wangTile tile)
{
    int i = 1000;
    while(i != 0)
    {
        int index = rand() % 16;
        if(tileArray[index].leftColor == tile.rightColor)
            return index;
        else
            i--;
    }
    return 0;
}

int getCornerHorizontal(cornerTile tileArray[16], cornerTile tile)
{
    int i = 1000;
    while(i != 0)
    {
        int index = rand() % 16;
        if(tileArray[index].tlColor == tile.trColor && tileArray[index].blColor == tile.brColor)
            return index;
        else
            i--;
    }
    return 0;
}

int getVertical(wangTile tileArray[16], wangTile tile)
{
    int i = 1000;
    while(i != 0)
    {
        int index = rand() % 16;
        if(tileArray[index].bottomColor == tile.topColor)
            return index;
        else
            i--;
    }
    return 0;
}

int getCornerVertical(cornerTile tileArray[16], cornerTile tile)
{
    int i = 1000;
    while(i != 0)
    {
        int index = rand() % 16;
        if(tileArray[index].blColor == tile.tlColor && tileArray[index].brColor == tile.trColor)
            return index;
        else
            i--;
    }
    return 0;
}

int getCross(wangTile tileArray[16], wangTile tileBottom, wangTile tileLeft)
{
    int i = 1000;
    while(i != 0)
    {
        int index = rand() % 16;
        if(tileArray[index].bottomColor == tileBottom.topColor && tileArray[index].leftColor == tileLeft.rightColor)
            return index;
        else
            i--;
    }
    return 0;
}

int getCornerCross(cornerTile tileArray[16], cornerTile tileBottom, cornerTile tileLeft)
{
    int i = 1000;
    while(i != 0)
    {
        int index = rand() % 16;
        if(tileArray[index].blColor == tileBottom.tlColor && tileArray[index].brColor == tileBottom.trColor && tileArray[index].tlColor == tileLeft.trColor && tileArray[index].blColor == tileLeft.brColor)
            return index;
        else
            i--;
    }
    return 0;
}

vec3 drawWangTile(vec2 uv, vec2 offset, wangTile tile)
{
	vec3 col = vec3(1.);
    vec2 p = uv + offset;

    float b = sdTrapezoid(p, vec2(0.05, 0.), vec2(0.1, 0.), float(0.05), float(0.1));
    float d = sdTrapezoid(p, vec2(-0.05, 0.), vec2(-0.1, 0.), float(0.05), float(0.1));
    float c = sdTrapezoid(vec2(p.x, p.y + 0.075), float(0.1), float(0.05), float(0.025));
    float a = sdTrapezoid(vec2(p.x, p.y - 0.075), float(0.05), float(0.1), float(0.025));
    float z = sdCircle(p, 0.1);

    if(z < 0.)
    	col = vec3(0.);
  	if(a < 0.)
    	col = tile.topColor;
  	if(b < 0.)
    	col = tile.rightColor;
  	if(c < 0.)
    	col = tile.bottomColor;
  	if(d < 0.)
    	col = tile.leftColor;

	return col;
}

vec3 drawCornerTile(vec2 uv, vec2 offset, cornerTile tile)
{
    vec3 col = vec3(1.);
    vec2 p = uv + offset;

    float a = sdCircle(p + vec2(0.1, -0.1), 0.1);
    float b = sdCircle(p + vec2(-0.1, -0.1), 0.1);
    float c = sdCircle(p + vec2(-0.1, 0.1), 0.1);
    float d = sdCircle(p + vec2(0.1, 0.1), 0.1);
    float z = sdCircle(p, 0.1);

    if(z < 0.)
    	col = vec3(0.);
    if(a < 0. && p.x < 0. && p.y > 0. && p.x > -0.1 && p.y < 0.1)
    	col = tile.tlColor;
    if(b < 0. && p.x > 0. && p.y > 0. && p.x < 0.1 && p.y < 0.1)
        col = tile.trColor;
  	if(c < 0. && p.x > 0. && p.y < 0. && p.x < 0.1 && p.y > -0.1)
        col = tile.brColor;
    if(d < 0. && p.x < 0. && p.y < 0. && p.x > -0.1 && p.y > -0.1)
        col = tile.blColor;

    return col;
}

vec3 drawLine(vec2 uv, inout vec3 col)
{
    float t;
    uv.x += 0.1;
    uv.y += 0.1;

    for(int i = 0; i < 10; i ++)
    {
        t = sdLine(vec2(uv.x - float(i) * 0.2), vec2(0., 0.0), vec2(0., -1.), float(0.005));
        if(t < 0.)
            col *= vec3(0.);
    }
    for(int i = 0; i < 6; i++)
    {
        t = sdLine(vec2(uv.x, uv.y - float(i) * 0.2), vec2(1.8, 0.0), vec2(0., 0.), float(0.005));
        if(t < 0.)
            col *= vec3(0.);
    }

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  	vec2 uv = fragCoord/iResolution.xy; // <0,1>
    uv.x -= 0.05;
    uv.y -= 0.1;
  	uv.x *= iResolution.x/iResolution.y; // fix aspect ratio

    seed = int(iTime * 0.5) % 32767;

    iniWangTile();
    iniCornerTile();

  	vec3 col = vec3(1.);
    vec2 offset;

    int wangTileIndex[45];
    wangTileIndex[0] = rand() % 16;
    int cornerTileIndex[45];
    cornerTileIndex[0] = rand() % 16;

    if(seed % 2 == 0)
    {
        for(int i = 0; i < 5; i++)
        {
            for(int j = 0; j < 9; j++)
            {
                if(i == 0 && j == 0)
                    col = drawWangTile(uv, vec2(offset.x - float(j) * float(0.2), offset.y - float(i) * float(0.2)), wTile[wangTileIndex[0]]);
                else if(i == 0 && j != 0)
                {
                    wangTileIndex[j] = getHorizontal(wTile, wTile[wangTileIndex[j - 1]]);
                    col *= drawWangTile(uv, vec2(offset.x - float(j) * float(0.2), offset.y - float(i) * float(0.2)), wTile[wangTileIndex[j]]);
                }
                else if(i !=0 && j == 0)
                {
                    wangTileIndex[i * 9] = getVertical(wTile, wTile[wangTileIndex[(i-1) * 9]]);
                    col *= drawWangTile(uv, vec2(offset.x - float(j) * float(0.2), offset.y - float(i) * float(0.2)), wTile[wangTileIndex[i * 9]]);
                }
                else if(i !=0 && j != 0)
                {
                    wangTileIndex[i * 9 + j] = getCross(wTile, wTile[wangTileIndex[(i - 1) * 9 + j]], wTile[wangTileIndex[i * 9 + j - 1]]);
                    col *= drawWangTile(uv, vec2(offset.x - float(j) * float(0.2), offset.y - float(i) * float(0.2)), wTile[wangTileIndex[i * 9 + j]]);
                }
            }
        }
    }

    else
    {
        for(int i = 0; i < 5; i++)
        {
            for(int j = 0; j < 9; j++)
            {
                if(i == 0 && j == 0)
                    col = drawCornerTile(uv, vec2(offset.x - float(j) * float(0.2), offset.y - float(i) * float(0.2)), cTile[cornerTileIndex[0]]);
                else if(i == 0 && j != 0)
                {
                    cornerTileIndex[j] = getCornerHorizontal(cTile, cTile[cornerTileIndex[j - 1]]);
                    col *= drawCornerTile(uv, vec2(offset.x - float(j) * float(0.2), offset.y - float(i) * float(0.2)), cTile[cornerTileIndex[j]]);
                }
                else if(i !=0 && j == 0)
                {
                    cornerTileIndex[i * 9] = getCornerVertical(cTile, cTile[cornerTileIndex[(i-1) * 9]]);
                    col *= drawCornerTile(uv, vec2(offset.x - float(j) * float(0.2), offset.y - float(i) * float(0.2)), cTile[cornerTileIndex[i * 9]]);
                }
                else if(i !=0 && j != 0)
                {
                    cornerTileIndex[i * 9 + j] = getCornerCross(cTile, cTile[cornerTileIndex[(i - 1) * 9 + j]], cTile[cornerTileIndex[i * 9 + j - 1]]);
                    col *= drawCornerTile(uv, vec2(offset.x - float(j) * float(0.2), offset.y - float(i) * float(0.2)), cTile[cornerTileIndex[i * 9 + j]]);
                }
            }
        }
    }



    col *= drawLine(uv, col);

	fragColor = vec4(col,1.0);
}

#define PI 3.14
#define wavelength 4.8
#define steepness 0.7

#define surfaceEdge .001

#define normalPower 1.0
vec3 ambient = vec3(0.001);

//OPERATORS_3D______OPERATORS_3D______OPERATORS_3D______OPERATORS_3D______OPERATORS_3D______
float random (vec2 seed) 
{
    return fract(sin(dot(seed.xy,
                         vec2(12.9898,78.233)))
                             * 43758.5453123);
}
mat2 Rotate(float angle)
{
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c,-s,s,c);
}

vec3 Wave(vec4 wave, vec3 pos, vec2 SW)
{
    float s = SW.x;
    float w = SW.y;
    float k = 2. * PI / w ;
    float c = sqrt(9.8 / k);
    vec2 d = normalize(wave.xy);

    float f = k * (dot(d, pos.xz) - c * iTime);
    float a = s / k;

    return vec3(d.x * (a * cos(f)),
				a * sin(f),
				d.y * (a * cos(f)));
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 uv) 
{
    vec2 i = floor(uv);
    vec2 f = fract(uv);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

// Low quality, turbulent FBM (for raymarching)
float fbm(vec2 uv) 
{
    float h = 0.0, a = 1.0;    
    for (int i = 0; i < 4; ++i) 
    {
        h += 1.0-abs(a * noise(iTime - uv) ); // ridged perlin noise
        a *= 0.45; uv *= 2.02;
    }   

    return h;
}

//PRIMITIVE____PRIMITIVE____PRIMITIVE____PRIMITIVE____PRIMITIVE____PRIMITIVE____
vec2 sphere(vec3 position, vec4 objPosition, float matId)
{
    return vec2(length(position-objPosition.xyz) - objPosition.w,matId);
}

vec2 box(vec3 position, vec3 bounds, float matId)
{
  vec3 q = abs(position) - bounds;
  return vec2(length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0),matId);
}

vec2 sdHexPrism( vec3 p, vec2 h, float matId)
{
  const vec3 k = vec3(-0.8660254, 0.5, 0.57735);
  p = abs(p);
  p.xy -= 2.0*min(dot(k.xy, p.xy), 0.0)*k.xy;
  vec2 d = vec2(
       length(p.xy-vec2(clamp(p.x,-k.z*h.x,k.z*h.x), h.x))*sign(p.y-h.x),
       p.z-h.y );
  return vec2(min(max(d.x,d.y),0.0) + length(max(d,0.0)),matId);
}

vec2 sdTorus( vec3 p, vec2 t ,float matId)
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return vec2(length(q)-t.y, matId);
}

//vec2 sdCone( vec3 p, vec2 c, float h ,float matId)
vec2 sdCone( in vec3 p, in vec2 c, float h, float matId)
{
  // c is the sin/cos of the angle, h is height
  // Alternatively pass q instead of (c,h),
  // which is the point at the base in 2D
  vec2 q = h*vec2(c.x/c.y,-1.0);

  vec2 w = vec2( length(p.xz), p.y );
  vec2 a = w - q*clamp( dot(w,q)/dot(q,q), 0.0, 1.0 );
  vec2 b = w - q*vec2( clamp( w.x/q.x, 0.0, 1.0 ), 1.0 );
  float k = sign( q.y );
  float d = min(dot( a, a ),dot(b, b));
  float s = max( k*(w.x*q.y-w.y*q.x),k*(w.y-q.y)  );
  return vec2(sqrt(d)*sign(s), matId);
}

vec2 sdBoxFrame( vec3 p, vec3 b, float e ,float matId)
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return vec2(min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0)),matId);
}
//OBJECTS_STRUCT_______OBJECTS_STRUCT_______OBJECTS_STRUCT_______OBJECTS_STRUCT_______
vec2 waterplane(vec3 position, float matId)
{
    vec4 waveInfo = vec4(1,-3,0,0);
    vec4 waveInfo2 = vec4(-1,5,0,0);
    vec4 waveInfo3 = vec4(-3,2,0,0);
    float oPos = position.y;

    vec3 wave = vec3(fbm(position.xz + 0.7 + iTime));
    wave += Wave(waveInfo, position,vec2(steepness,wavelength));
    wave += Wave(waveInfo3,position,vec2(steepness * 2., wavelength / 2.));

    float o = (wave.x + wave.y + wave.z) / 3. ;
    // .21 | .11
    oPos += o * 0.11;
    return vec2(oPos,matId);
}

vec2 pier(vec3 position, float matId)
{
    vec3 boxPosCtrl = (position - vec3(-8.8,0,14));
    float boxs = box( boxPosCtrl, vec3(9,1,1), matId).x;

    boxPosCtrl = (position - vec3(-8.8, 2.1, 14.21));
    boxs = min( boxs, box( boxPosCtrl, vec3(9,1.5,.1), matId).x );

    boxPosCtrl = position - vec3(-2.3, 0, 12.21);
    boxs = min( boxs, box( boxPosCtrl , vec3(4,1,2), matId).x );

    boxPosCtrl = position - vec3(-1.3, 0.21, 12.21);
    boxs = min( boxs, box( boxPosCtrl , vec3(3,2,1), matId).x );

    boxs -= texture(iChannel0,position.xy * .21).b * 0.07;

    return vec2(boxs,matId);
}

vec2 lighthouse(vec3 position, float matId)
{
    vec3 tmpPose = position - vec3(0,4.0,13);
    tmpPose.yz *= Rotate(1.6);
    float lhouse = sdHexPrism(tmpPose, vec2(1,2.55),matId).x;

    tmpPose = position - vec3(0,8.8,13);    
    lhouse = min(lhouse, sdCone(tmpPose, vec2(1,1.2), 1.4, matId).x);

    tmpPose = position - vec3(0,5,12);  
    lhouse = max( -box( tmpPose, vec3(.1,.2,.5), matId).x, lhouse );

    tmpPose = position - vec3(0,3.5,12);  
    lhouse = max( -box( tmpPose, vec3(.1,.2,.5), matId).x, lhouse );

    tmpPose = position - vec3(0,9,13);  
    lhouse = min(  lhouse, box( tmpPose, vec3(.05,.7,.05), matId).x);

    tmpPose = position - vec3(0,7,13); 
    tmpPose.xz *= Rotate(0.7);
    lhouse = min( lhouse, sdBoxFrame( tmpPose, vec3(.8,.5,.8), .05, matId).x);

    tmpPose = position - vec3(0,5.7,13);
    tmpPose.yz *= Rotate(1.6);
    lhouse = min( lhouse, sdHexPrism(tmpPose, vec2(1.5, .021),matId).x);

    tmpPose = position - vec3(0,2.4,13);
    tmpPose.yz *= Rotate(1.6);
    lhouse = min( lhouse, sdHexPrism(tmpPose, vec2(1.3, .5),matId).x);

    tmpPose = position - vec3(0,7.4,13);
    tmpPose.yz *= Rotate(1.6);
    lhouse = min( lhouse, sdHexPrism(tmpPose, vec2(1.3, .021),matId).x);

    lhouse -= texture(iChannel2,position.xy * .21).b * 0.02;

    return vec2(lhouse,matId);
}
//RAYFUNCTIONS______RAYFUNCTIONS______RAYFUNCTIONS______RAYFUNCTIONS______RAYFUNCTIONS______
vec2 Map(vec3 position)
{
    float mat = 2.0;
    float final = position.y;

    float waterplane = waterplane(position,1.).x;
    final = waterplane;
    float prevd = final;

    float pier = pier(position,2.).x;
    final = min(waterplane, pier);

    if (final < prevd)
        mat = 3.0;
    prevd = final;

    float lighthouse = lighthouse(position + vec3(0.5,0,0),3.).x;
    final = min(final,lighthouse);
    if (final < prevd)
        mat = 4.0;
    prevd = final;
    ;
    float sphDist = sphere(position + vec3(0.5,0,0), vec4(0, 6.9, 13, 0.21),0.).x;
    final = min(final,sphDist);

    if (final < prevd)
        mat = 5.0;
    prevd = final;

    return vec2(final,mat);
}

vec2 Raycast(vec3 rayOrigin, vec3 rayDirection)
{
    //distance of a point on ray
    float d = 0.0;
    float matId = 0.0;

    //cast all ray points
    for(int i = 0; i < 200; i++)
    {
        vec3 position = rayOrigin + rayDirection * d;

        //map.x = distS;
        vec2 map = Map(position);

        matId = map.y;

        if (map.x < 0.001) break;

        d += map.x;

        if(d > 120.0) break;

    }
    if(d > 100.0) d = -1.0;
    return vec2(d,matId);
}

vec3 CalcNormal(vec3 position)
{
    float d = Map(position).x;

    vec2 edge = vec2(0.001, 0);
    vec3 normal = d - vec3(
        Map(position-edge.xyy).x,
            Map(position-edge.yxy).x,
                Map(position-edge.yyx).x
                                        );
    return normalize(normal);
}

//LIGHTNING____LIGHTNING____LIGHTNING____LIGHTNING____LIGHTNING____LIGHTNING____LIGHTNING____
vec3 CalcLight(vec3 position,vec3 lightPosition,vec3 rayOrigin, vec3 rayDirection, vec4 lightColor, bool point)
{
    vec3 l;
    vec3 n = CalcNormal(position);
    float shadow;

    if(point)
    {
        l = normalize(lightPosition-position);
        shadow = Raycast(position + n * surfaceEdge, l).x;
    }
    else
    {
        l = normalize(lightPosition);
        shadow = step( Raycast(position + n * surfaceEdge, l).x, 0.0);
    }

    float nl = clamp(dot(n,l), 0., 1.);
    return lightColor.rgb * (nl * shadow * lightColor.w) ;
}

vec3 SkyMotions(float status, vec3 rd, vec3 ro, vec3 lp)
{
    //color = vec3(0.6,0.7,0.9) - 0.5 * rd.y;
        //color = mix(color,vec3(0.9,0.95,1.2),exp(-10.0*rd.y));
    vec3 day_colorsky = (vec3(0.6,0.7,0.9) - 0.5 * rd.y);
    vec3 day_colorhorz = vec3(0.9,0.95,1.2);
    float stat = dot(lp.x,lp.y);
    vec3 colorsky = mix(day_colorsky, day_colorhorz, exp(-10.0 *rd.y ));
    float sun = pow(1.0 - length( ( rd + vec3(0.5 + status,0.1,-0.4)) - 0.4), 20.5) * 25.;

    return colorsky + clamp(sun,0.01,1.);
}

float ao(vec3 p, vec3 n) 
{
	float o = 0.0, s = 0.011, w = 1.0;

	for(int i = 0; i < 15; i++) {
		float d = Map(p + n*s).x;
		o += (s - d)*w;
		w *= 0.98;
		s += s/float(i + 1);
	}

	return 1.0 - clamp(o, 0.0, 1.0);
}

vec3 RenderImage(out vec3 ro, out vec3 rd, vec3 color, vec3 lp, float status)
{
    for(int i = 0; i < 2; i++)
    {
        //SKY
        //color = vec3(0.6,0.7,0.9) - 0.5 * rd.y;
        //color = mix(color,vec3(0.9,0.95,1.2),exp(-10.0*rd.y));

        //2.0 = standard
        color = SkyMotions(status,rd,ro,lp);

        //AMBIENT < ? >
        vec3 ambient = clamp(color,vec3(0),vec3(1));

        vec2 ray = Raycast(ro,rd);
        if(ray.x > 0.0)
        {
            vec3 diffuse = vec3(0);
            vec3 position = ro + rd * ray.x;
            vec3 normal = CalcNormal(position);
            vec3 l = normalize(lp-position);          

            float bounce = clamp(0.5 + 0.5 * dot(normal,vec3(0.0,-1.0,0.0)),0.0,1.0);
            float SkyAmbient = clamp(0.5 + 0.5 * dot(normal,vec3(0.0,1.0,0.0)),0.0,1.0)+ 0.05;

            float AO = ao(position, normal);

            diffuse += CalcLight( position, lp, ro, rd, vec4(1, 1, 1, 1.0), false);
            diffuse +=  ambient * SkyAmbient * ctrl; //vec3(0.9,0.95,1.2) * SkyAmbient;      
            vec3 mat = vec3(0.16);

            if(ray.y > 4.5)
            {
                //SPHERE
                mat = vec3(1,0,0) * (sin(iTime * 3.0)  * sin(iTime * 0.21) );
                return mat * AO * 2.0;
            }
            else if(ray.y > 3.5)
            {
                //LIGHTHOUSE
                mat = vec3(1,1,1) * diffuse * 5.0 * ctrl;
                return mat * AO * 2.0;
            }  
            else if(ray.y > 2.5)
            {
                //PIER
                vec3 channel0 = texture(iChannel0,position.xy * 0.21 + position.zy * 0.21).rgb;
                mat = channel0;
                mat =  mat * diffuse * ctrl;
                return mat * AO * 2.0;
            }   
            else if(ray.y > 1.5)
            {
                //WATER  
                mat = vec3(0.21,0.21,0.7) * diffuse * ctrl;

                vec3 ref = reflect(rd, normal);
