#define pi 3.14159
#define ths(a,b) tanh(a*sin(b))/tanh(a)

float seg(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Change me for different patterns!
    float f = 2./3.; 
    //f = 0.5;
    //f = 0.5 + 0.5 * cos(0.01 * iTime);

    // Angle + point
    float a = 0.;
    vec2 p = vec2(0);

    float d = 100.;
    for (float i = 0.; i < 50.; i++) {
        // Random number gives us a unique shape
        float h = h21(vec2(i - floor(iTime), 103. + i));

        // Angle moves: LEFT | left | forwards | right | RIGHT
        // f changes by how much
        a += pi * (f * floor(5. * h) - 1.);

        // New point
        vec2 q = p + 0.05 * ths(5., pi * iTime) * vec2(cos(a), sin(a));

        // Segment from old to new point (check normal and flipped x coords)
        // Uncomment second line to see non-mirrored version
        d = min(d, seg(uv, p, q));
        d = min(d, seg(vec2(-uv.x, uv.y), p, q));

        p = q;
    }

    float k = 0.5 / iResolution.y;
    float s = smoothstep(-k, k, -d + 0.009);

    vec3 col = vec3(s);

    fragColor = vec4(col,1.0);
}

//Improved with sdf functions

float rhombusSDF(vec2 p, float scale)
{
    p = abs(p);

    float d = p.x + p.y; 

    float f1 = d - scale;
    float f2 = d - scale + 0.01;

    return max(f1 ,-f2);
}

float squareSDF(vec2 p, float scale)
{
    p = abs(p);

    float f1 = max(p.x, p.y) - scale;    
    float f2 = max(p.x, p.y) - scale + 0.005;
    return max(f1,-f2);
}

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    uv = rotate(uv, 3.1415/16. * iTime);
    // Time varying pixel color
    float px=2.5/iResolution.y ;
    vec3 col = vec3(0.);
    col = mix(vec3(1,1,1), col, smoothstep(0., px, rhombusSDF(uv, 0.025f)));    
    col = mix(vec3(1,1,1), col, smoothstep(0., px, squareSDF(uv, 0.025f)));

    col = mix(vec3(1,1,1), col, smoothstep(0., px, rhombusSDF(uv, 0.05f)));    
    col = mix(vec3(1,1,1), col, smoothstep(0., px, squareSDF(uv, 0.05f)));

    col = mix(vec3(1,1,1), col, smoothstep(0., px, rhombusSDF(uv, 0.1f)));    
    col = mix(vec3(1,1,1), col, smoothstep(0., px, squareSDF(uv, 0.1f)));

    col = mix(vec3(1,1,1), col, smoothstep(0., px, rhombusSDF(uv, 0.2f)));    
    col = mix(vec3(1,1,1), col, smoothstep(0., px, squareSDF(uv, 0.2f)));

    col = mix(vec3(1,1,1), col, smoothstep(0., px, rhombusSDF(uv, 0.4f)));    
    col = mix(vec3(1,1,1), col, smoothstep(0., px, squareSDF(uv, 0.4f)));

    col = mix(vec3(1,1,1), col, smoothstep(0., px, rhombusSDF(uv, 0.8f)));    
    col = mix(vec3(1,1,1), col, smoothstep(0., px, squareSDF(uv, 0.8f)));

    col *= cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

