#define MAX_BOUNCE_COUNT 4
#define SAMPLE_COUNT 32
#define SENSOR_OFFSET 0.1
#define SENSOR_SCALE (SENSOR_OFFSET * 0.3)
#define MAX_DISTANCE 1e12

#define UP   vec3(0.0, 1.0, 0.0)
#define ZERO vec3(0.0, 0.0, 0.0)
#define ONE  vec3(1.0, 1.0, 1.0)

float randomUnit(inout uint state) {
    state = xorshift32(state);
    return mod(float(state), 16777216.0) / 16777216.0;
}

vec3 randomvec3(inout uint state) {
    return normalize(vec3(
        randomUnit(state) * 2.0 - 1.0,
        randomUnit(state) * 2.0 - 1.0,
        randomUnit(state) * 2.0 - 1.0
    ));
}

struct Material {
    vec3 reflectColor;
    vec3 emitColor;
    float roughness;
    float fresnel;
};

struct Sphere {
    Material m;
    vec3 p; // Position
    float r; // Radius
};

Sphere spheres[] = Sphere[](
    Sphere(
        Material(
            vec3(0.2, 0.2, 0.2),
            ZERO,
            0.6,
            0.5
        ),
        vec3(-0.2, -0.2, 0.0),
        0.5
    ),
    Sphere(
        Material(
            vec3(0.05, 0.05, 0.05),
            vec3(0.5, 0.1, 0.1) * 10.0,
            0.2,
            0.8
        ),
        vec3(1.5, 0.0, 1.0),
        0.2
    ),
    Sphere(
        Material(
            vec3(0.4, 0.1, 0.1),
            ZERO,
            0.3,
            0.9
        ),
        vec3(-1.1, -0.45, 1.5),
        0.3
    ),

    Sphere(
        Material(
            vec3(0.5, 0.5, 0.5),
            ZERO,
            0.8,
            0.1
        ),
        vec3(1.0, -0.6, 0.5),
        0.3
    ),

    // Wall
    Sphere(
        Material(
            vec3(0.01, 0.01, 0.01),
            ZERO,
            0.9,
            0.0
        ),
        vec3(0.0, 0.0, -1000),
        900.0
    ),

    // FLOOR
    Sphere(
        Material(
            vec3(0.7, 0.7, 0.7),
            ZERO, //vec3(0.01, 0.01, 0.01),
            0.9,
            0.1
        ),
        vec3(0.0, -10001.0, 0.0),
        10000.0
    ),

    // SKY
    Sphere(
        Material(
            ZERO, // vec3(0.5, 0.5, 0.5),
            vec3(0.7, 0.4, 0.8) * 100000.0,
            0.0,
            0.0
        ),
        vec3(0.0, 10000000.0, 0.0),
        9999000.0
    )
);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    uint rngState = vec4touint(texture(iChannel0, fragCoord.xy / iResolution.xy));

    vec3 cameraP = vec3(0.0, -0.5, 5.0);
    vec3 cameraZ = normalize(-cameraP);
    vec3 cameraX = normalize(cross(cameraZ, UP));
    vec3 cameraY = normalize(cross(cameraX, cameraZ));

    vec3 finalColor = ZERO;

    for(int sampleI = 0; sampleI < SAMPLE_COUNT; sampleI++) {
        vec2 rngPix = vec2(randomUnit(rngState) - 0.5, randomUnit(rngState) - 0.5);
        vec2 uv = (fragCoord + rngPix - iResolution.xy / 2.0) / (iResolution.y / 2.0);
        vec3 sensorP = cameraP + cameraZ * SENSOR_OFFSET + (cameraX * uv.x + cameraY * uv.y) * SENSOR_SCALE;
        vec3 sensorN = normalize(sensorP - cameraP);

        vec3 rayP = sensorP;
        vec3 rayN = sensorN;
        vec3 color = ZERO;
        vec3 colorMute = ONE;
        float travelDistance = 0.0;
        for(int bounceI = 0; bounceI < MAX_BOUNCE_COUNT; bounceI++) {
            float minDistance = MAX_DISTANCE;
            Sphere minSphere;

            for(int i = 0; i < spheres.length(); i++) {
                Sphere s = spheres[i];
                float a = dot(rayN, rayN);
                float b = 2.0 * dot(rayN, rayP - s.p);
                float c = dot(s.p, s.p) + dot(rayP, rayP) + -2.0 * dot(s.p, rayP) - s.r * s.r;

                float d = b * b - 4.0 * a * c;
                if (d <= 0.0) continue;

                float t = (-b - sqrt(d)) / (2.0 * a);
                if (t <= 0.0) continue;

                if (t < minDistance) {
                    minDistance = t;
                    minSphere = s;
                }
            }
            if (minDistance == MAX_DISTANCE) break;

            Material mat = minSphere.m;
            vec3 p = rayP + rayN * minDistance;
            vec3 n = (p - minSphere.p) / minSphere.r;

            travelDistance += minDistance;
            float attenuation = 1.0 / (1.0 + 0.05 * travelDistance + 0.01 * travelDistance * travelDistance);

            float cosineLaw = dot(n, -rayN);
            if (cosineLaw < 0.0) cosineLaw = 0.0;

            color += mat.emitColor * colorMute * attenuation * cosineLaw;

            colorMute *= mat.reflectColor * cosineLaw;
            if (colorMute.r < 1e-6 && colorMute.g < 1e-6 && colorMute.b < 1e-6) break;

            rayP = p;
            vec3 rvec = randomvec3(rngState);
            if (dot(n, rvec) < 0.0) rvec = -rvec;
            rayN = normalize(mix(reflect(rayN, n), rvec, mat.roughness * mix(1.0, cosineLaw, mat.fresnel)));
        }
        finalColor += color;
    }

    fragColor = vec4(pow(finalColor / float(SAMPLE_COUNT), vec3(1.0 / 2.2)), 1.0);
}

