    state ^= xorshift32(state);
    fragColor = vec4(
        float(state & 0xffu) / 255.0,
        float((state >> 8) & 0xffu) / 255.0,
        float((state >> 16) & 0xffu) / 255.0,
        float((state >> 24) & 0xffu) / 255.0
    );
}

